			<span className="text-slate-500">&rarr;</span>
			<span className="text-slate-200 font-medium">{after.toFixed(1)}</span>
			<span className={`font-medium ${deltaColor}`}>
				({deltaSign}
				{delta.toFixed(1)})
			</span>
			{afterGrade && <span className="text-slate-500">{afterGrade}</span>}
		</div>
	);
}

