 1/**
 2 * Frontend types for the remediation execution system
 3 */
 4
 5export type RemediationStatus = "queued" | "running" | "completed" | "failed";
 6
 7export interface FileChange {
 8	path: string;
 9	status: "modified" | "added" | "deleted";
10	diff: string;
11	additions: number;
12	deletions: number;
13}
14
15export interface RemediationAction {
16	issueIndex: number;
17	status: "fixed" | "added" | "skipped";
18	file?: string;
19	summary: string;
20	issueTitle?: string;
21}
22
23export interface RemediationSummary {
24	errorFixActions: RemediationAction[];
25	suggestionEnrichActions: RemediationAction[];
26	addressedCount: number;
27	skippedCount: number;
28	parsed: boolean;
29}
30
31export interface PromptStats {
32	durationMs: number;
33	costUsd?: number;
34	inputTokens?: number;
35	outputTokens?: number;
36}
37
38export interface RemediationResult {
39	fullPatch: string;
40	fileChanges: FileChange[];
41	totalAdditions: number;
42	totalDeletions: number;
43	filesChanged: number;
44	totalDurationMs: number;
45	totalCostUsd: number;
46	totalInputTokens: number;
47	totalOutputTokens: number;
48	summary?: RemediationSummary;
49	errorFixStats?: PromptStats;
50	suggestionEnrichStats?: PromptStats;
51}
52
53export interface IRemediationProgressState {
54	status: RemediationStatus;
55	currentStep?: string;
56	batchInfo?: { batchNumber: number; totalBatches: number };
57	logs: Array<{ timestamp: string; message: string }>;
58	result?: RemediationResult;
59	error?: { message: string };
60	// Enriched progress tracking
61	errorCount?: number;
62	suggestionCount?: number;
63	totalBatches?: number;
64	completedBatches?: number;
65	currentPhase?: "errors" | "suggestions";
66	runningTotalCostUsd?: number;
67	runningTotalDurationMs?: number;
68	runningTotalInputTokens?: number;
69	runningTotalOutputTokens?: number;
70	currentBatchIssues?: string[];
71	lastBatchStats?: {
72		durationMs: number;
73		costUsd: number;
74		inputTokens: number;
75		outputTokens: number;
76	};
77}
78
79export interface DiffLine {
80	type: "addition" | "deletion" | "context" | "header";
81	content: string;
82	oldLineNumber?: number;
83	newLineNumber?: number;
84}
85
86export interface DiffHunk {
87	header: string;
88	lines: DiffLine[];
89}
