  3export interface TabItem {
  4	id: string;
  5	label: string;
  6	icon?: React.ReactNode;
  7	count?: number;
  8}
  9
 10interface TabsProps {
 11	tabs: TabItem[];
 12	activeTab: string;
 13	onTabChange: (tabId: string) => void;
 14}
 15
 16interface TabPanelProps {
 17	id: string;
 18	activeTab: string;
 19	children: React.ReactNode;
 20}
 21
 22export const Tabs: React.FC<TabsProps> = ({ tabs, activeTab, onTabChange }) => {
 23	const tabsRef = useRef<HTMLDivElement>(null);
 24	const [indicatorStyle, setIndicatorStyle] = useState<{
 25		left: number;
 26		width: number;
 27	}>({ left: 0, width: 0 });
 28
 29	// Update indicator position when active tab changes
 30	useEffect(() => {
 31		if (!tabsRef.current) return;
 32
 33		const activeTabElement = tabsRef.current.querySelector(
 34			`[data-tab-id="${activeTab}"]`,
 35		) as HTMLButtonElement | null;
 36
 37		if (activeTabElement) {
 38			const containerRect = tabsRef.current.getBoundingClientRect();
 39			const tabRect = activeTabElement.getBoundingClientRect();
 40
 41			setIndicatorStyle({
 42				left: tabRect.left - containerRect.left,
 43				width: tabRect.width,
 44			});
 45		}
 46	}, [activeTab]);
 47
 48	return (
 49		<div ref={tabsRef} className="tab-navigation relative" role="tablist">
 50			{/* Animated indicator */}
 51			<div
 52				className="tab-indicator"
 53				style={{
 54					left: indicatorStyle.left,
 55					width: indicatorStyle.width,
 56					height: "calc(100% - 12px)",
 57					top: "6px",
 58				}}
 59			/>
 60
 61			{tabs.map((tab) => {
 62				const isActive = tab.id === activeTab;
 63				return (
 64					<button
 65						key={tab.id}
 66						role="tab"
 67						data-tab-id={tab.id}
 68						aria-selected={isActive}
 69						aria-controls={`tabpanel-${tab.id}`}
 70						className={`tab-button ${isActive ? "tab-button-active" : ""}`}
 71						onClick={() => onTabChange(tab.id)}
 72					>
 73						{tab.icon && <span className="tab-icon">{tab.icon}</span>}
 74						<span>{tab.label}</span>
 75						{tab.count !== undefined && (
 76							<span
 77								className={`tab-count ${isActive ? "tab-count-active" : ""}`}
 78							>
 79								{tab.count}
 80							</span>
 81						)}
 82					</button>
 83				);
 84			})}
 85		</div>
 86	);
 87};
