 33export function RemediationProgress({
 34	currentStep,
 35	batchInfo,
 36	logs,
 37	errorCount,
 38	suggestionCount,
 39	totalBatches,
 40	completedBatches,
 41	currentPhase,
 42	runningTotalCostUsd,
 43	runningTotalDurationMs,
 44	runningTotalInputTokens,
 45	runningTotalOutputTokens,
 46	currentBatchIssues,
 47}: RemediationProgressProps) {
 48	const [elapsed, setElapsed] = useState(0);
 49	const logEndRef = useRef<HTMLDivElement>(null);
 50
 51	useEffect(() => {
 52		const start = Date.now();
 53		const interval = setInterval(() => {
 54			setElapsed(Math.floor((Date.now() - start) / 1000));
 55		}, 1000);
 56		return () => clearInterval(interval);
 57	}, []);
 58
 59	// Auto-scroll log to bottom when new entries are added
 60	const logCount = logs.length;
 61	// biome-ignore lint/correctness/useExhaustiveDependencies: logCount triggers scroll on new log entries
 62	useEffect(() => {
 63		logEndRef.current?.scrollIntoView({ behavior: "smooth" });
 64	}, [logCount]);
 65
 66	const formatTime = (seconds: number) => {
 67		const m = Math.floor(seconds / 60);
 68		const s = seconds % 60;
 69		return m > 0 ? `${m}m ${s}s` : `${s}s`;
 70	};
 71
 72	const baseLabel = currentStep
 73		? STEP_LABELS[currentStep] || currentStep
 74		: "Initializing...";
 75	const stepLabel =
 76		batchInfo &&
 77		batchInfo.totalBatches > 1 &&
 78		(currentStep === "executing_error_fix" ||
 79			currentStep === "executing_suggestion_enrich")
 80			? `${baseLabel} (batch ${batchInfo.batchNumber}/${batchInfo.totalBatches})`
 81			: baseLabel;
 82
 83	const hasPlan = totalBatches !== undefined && totalBatches > 0;
 84	const hasProgress = completedBatches !== undefined && completedBatches > 0;
 85	const progressPercent =
 86		hasPlan && completedBatches !== undefined
 87			? Math.round((completedBatches / totalBatches) * 100)
 88			: 0;
 89
 90	return (
 91		<div className="card space-y-4">
 92			{/* Summary header */}
 93			{hasPlan && (
 94				<div className="text-sm text-slate-300">
 95					{errorCount !== undefined && errorCount > 0 && (
 96						<span>
 97							Fixing{" "}
 98							<span className="text-slate-100 font-medium">
 99								{errorCount} error{errorCount !== 1 ? "s" : ""}
100							</span>
101						</span>
102					)}
103					{errorCount !== undefined &&
104						errorCount > 0 &&
105						suggestionCount !== undefined &&
106						suggestionCount > 0 && <span>, then enriching </span>}
107					{suggestionCount !== undefined && suggestionCount > 0 && (
108						<span>
109							{errorCount === undefined || errorCount === 0 ? "Enriching " : ""}
110							<span className="text-slate-100 font-medium">
111								{suggestionCount} suggestion
112								{suggestionCount !== 1 ? "s" : ""}
113							</span>
114						</span>
115					)}
116					<span className="text-slate-500">
117						{" "}
118						({totalBatches} batch
119						{totalBatches !== 1 ? "es" : ""})
120					</span>
121				</div>
122			)}
123
124			{/* Current step with spinner */}
125			<div className="flex items-center gap-3">
126				<svg
127					className="animate-spin h-5 w-5 text-blue-400 flex-shrink-0"
128					fill="none"
129					viewBox="0 0 24 24"
130				>
131					<circle
132						className="opacity-25"
133						cx="12"
134						cy="12"
135						r="10"
136						stroke="currentColor"
137						strokeWidth="4"
138					/>
139					<path
140						className="opacity-75"
141						fill="currentColor"
142						d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
143					/>
144				</svg>
145				<span className="text-sm text-slate-200 font-medium">{stepLabel}</span>
146				<span className="text-xs text-slate-500 ml-auto">
147					{formatTime(elapsed)}
148				</span>
149			</div>
150
151			{/* Progress bar */}
152			{hasPlan && (
153				<div className="space-y-1">
154					<div className="flex items-center justify-between">
155						<span className="text-xs text-slate-400">
156							Batch {completedBatches ?? 0}/{totalBatches}
157							{currentPhase && (
158								<span className="text-slate-500 ml-1">
159									({currentPhase === "errors" ? "errors" : "suggestions"})
160								</span>
161							)}
162						</span>
163						<span className="text-xs text-slate-500">{progressPercent}%</span>
164					</div>
165					<div className="h-1.5 bg-slate-700 rounded-full overflow-hidden">
166						<div
167							className="h-full bg-blue-500 rounded-full transition-all duration-500"
168							style={{ width: `${progressPercent}%` }}
169						/>
170					</div>
171				</div>
172			)}
173
174			{/* Running totals strip */}
175			{hasProgress && (
176				<div className="flex flex-wrap gap-x-4 gap-y-1 text-xs text-slate-400">
177					{runningTotalCostUsd !== undefined && runningTotalCostUsd > 0 && (
178						<span>
179							Cost:{" "}
180							<span className="text-slate-300">
181								${runningTotalCostUsd.toFixed(4)}
182							</span>
183						</span>
184					)}
185					{runningTotalInputTokens !== undefined &&
186						runningTotalOutputTokens !== undefined &&
187						(runningTotalInputTokens > 0 || runningTotalOutputTokens > 0) && (
188							<span>
189								Tokens:{" "}
190								<span className="text-slate-300">
191									{runningTotalInputTokens.toLocaleString()} in /{" "}
192									{runningTotalOutputTokens.toLocaleString()} out
193								</span>
194							</span>
195						)}
196					{runningTotalDurationMs !== undefined &&
197						runningTotalDurationMs > 0 && (
198							<span>
199								Time:{" "}
200								<span className="text-slate-300">
201									{formatTime(Math.round(runningTotalDurationMs / 1000))}
202								</span>
203							</span>
204						)}
205				</div>
206			)}
207
208			{/* Current batch detail */}
209			{currentBatchIssues && currentBatchIssues.length > 0 && (
210				<div className="bg-slate-800/50 rounded-lg border border-slate-700/50 p-2.5">
211					<div className="text-xs text-slate-500 mb-1.5">
212						Current batch ({currentBatchIssues.length} issue
213						{currentBatchIssues.length !== 1 ? "s" : ""})
214					</div>
215					<div className="space-y-0.5">
216						{currentBatchIssues.map((summary) => (
217							<div key={summary} className="text-xs text-slate-400 truncate">
218								{summary}
219							</div>
220						))}
221					</div>
222				</div>
223			)}
224
225			{/* Activity log */}
226			{logs.length > 0 && (
227				<div className="bg-slate-900/50 rounded-lg p-3 border border-slate-700/50 max-h-40 overflow-y-auto custom-scrollbar">
228					{logs.map((log, i) => (
229						<div key={i} className="text-xs text-slate-400 py-0.5">
230							<span className="text-slate-600 mr-2">
231								{new Date(log.timestamp).toLocaleTimeString()}
232							</span>
233							{log.message}
234						</div>
235					))}
236					<div ref={logEndRef} />
237				</div>
238			)}
239		</div>
240	);
241}
