 1/**
 2 * Card for a single changed file with expandable diff viewer.
 3 */
 4
 5import { useState } from "react";
 6import type { FileChange, RemediationAction } from "../types/remediation";
 7import { DiffViewer } from "./DiffViewer";
 8
 9interface FileChangeCardProps {
10	file: FileChange;
11	defaultExpanded?: boolean;
12	summaries?: RemediationAction[];
13}
14
15export function FileChangeCard({
16	file,
17	defaultExpanded = false,
18	summaries,
19}: FileChangeCardProps) {
20	const [expanded, setExpanded] = useState(defaultExpanded);
21
22	const badgeClass =
23		file.status === "added"
24			? "change-badge-added"
25			: file.status === "deleted"
26				? "change-badge-deleted"
27				: "change-badge-modified";
28
29	return (
30		<div className="card">
31			<button
32				onClick={() => setExpanded(!expanded)}
33				className="w-full flex items-center gap-3 text-left"
34			>
35				{/* Expand/collapse indicator */}
36				<svg
37					className={`w-4 h-4 text-slate-400 transition-transform flex-shrink-0 ${expanded ? "rotate-90" : ""}`}
38					fill="none"
39					viewBox="0 0 24 24"
40					stroke="currentColor"
41				>
42					<path
43						strokeLinecap="round"
44						strokeLinejoin="round"
45						strokeWidth={2}
46						d="M9 5l7 7-7 7"
47					/>
48				</svg>
49
50				{/* File path */}
51				<span className="font-mono text-sm text-slate-200 flex-1 min-w-0 truncate">
52					{file.path}
53				</span>
54
55				{/* Status badge */}
56				<span
57					className={`text-xs px-2 py-0.5 rounded font-medium ${badgeClass}`}
58				>
59					{file.status}
60				</span>
61
62				{/* Stats */}
63				<span className="flex-shrink-0 flex items-center gap-2 text-xs">
64					{file.additions > 0 && (
65						<span className="text-green-400">+{file.additions}</span>
66					)}
67					{file.deletions > 0 && (
68						<span className="text-red-400">-{file.deletions}</span>
69					)}
70				</span>
71			</button>
72
73			{expanded && (
74				<div className="mt-3 border-t border-slate-700/50 pt-3">
75					{summaries && summaries.length > 0 && (
76						<p className="text-xs text-slate-400 mb-2 px-1">
77							{summaries
78								.map(
79									(s) =>
80										`${s.status === "fixed" ? "Fixed" : "Added"}: ${s.summary}`,
81								)
82								.join(" | ")}
83						</p>
84					)}
85					<DiffViewer diff={file.diff} />
86				</div>
87			)}
88		</div>
89	);
90}
