 1/**
 2 * Patch download button component.
 3 */
 4
 5import { useCallback, useState } from "react";
 6import { useEvaluationApi } from "../hooks/useEvaluationApi";
 7
 8interface PatchDownloadProps {
 9	remediationId: string;
10}
11
12export function PatchDownload({ remediationId }: PatchDownloadProps) {
13	const api = useEvaluationApi();
14	const [downloading, setDownloading] = useState(false);
15
16	const handleDownload = useCallback(async () => {
17		setDownloading(true);
18		try {
19			await api.downloadPatch(remediationId);
20		} catch {
21			// Error is handled by the hook
22		} finally {
23			setDownloading(false);
24		}
25	}, [api, remediationId]);
26
27	return (
28		<button
29			onClick={handleDownload}
30			disabled={downloading}
31			className="btn-secondary flex items-center gap-2"
32		>
33			<svg
34				className="w-4 h-4"
35				fill="none"
36				viewBox="0 0 24 24"
37				stroke="currentColor"
38			>
39				<path
40					strokeLinecap="round"
41					strokeLinejoin="round"
42					strokeWidth={2}
43					d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-4l-4 4m0 0l-4-4m4 4V4"
44				/>
45			</svg>
46			{downloading ? "Downloading..." : "Download Patch"}
47		</button>
48	);
49}
