 71interface IUseEvaluationApiReturn {
 72	submitJob: (
 73		repositoryUrl: string,
 74		evaluators?: number,
 75		provider?: ProviderName,
 76		evaluatorFilter?: EvaluatorFilter,
 77		timeout?: number,
 78		concurrency?: number,
 79		selectedEvaluators?: string[],
 80	) => Promise<IEvaluateResponse>;
 81	submitBatch: (
 82		urls: string[],
 83		evaluators?: number,
 84		provider?: ProviderName,
 85		evaluatorFilter?: EvaluatorFilter,
 86		timeout?: number,
 87		concurrency?: number,
 88		selectedEvaluators?: string[],
 89	) => Promise<IBatchEvaluateResponse>;
 90	getBatchStatus: (batchId: string) => Promise<IBatchStatusResponse>;
 91	getJobStatus: (jobId: string) => Promise<IJobStatusResponse>;
 92	cancelJob: (jobId: string) => Promise<void>;
 93	generateRemediationPrompts: (
 94		evaluationId: string,
 95		issues: Issue[],
 96		targetFileType: "AGENTS.md" | "CLAUDE.md",
 97	) => Promise<RemediationPromptsResponse>;
 98	executeRemediation: (
 99		evaluationId: string,
100		issues: Issue[],

100		issues: Issue[],
101		targetFileType: "AGENTS.md" | "CLAUDE.md",
102		provider: ProviderName,
103	) => Promise<ExecuteRemediationResponse>;
104	getRemediationResult: (
105		remediationId: string,
106	) => Promise<RemediationStatusResponse>;
107	getRemediationForEvaluation: (
108		evaluationId: string,
109	) => Promise<RemediationForEvaluationResponse | null>;
110	deleteRemediation: (remediationId: string) => Promise<void>;
111	downloadPatch: (remediationId: string) => Promise<void>;
112	isLoading: boolean;
113	error: string | null;
114	clearError: () => void;
115}
116
117export function useEvaluationApi(): IUseEvaluationApiReturn {
118	const [isLoading, setIsLoading] = useState(false);
119	const [error, setError] = useState<string | null>(null);
120
121	const submitJob = useCallback(
122		async (
123			repositoryUrl: string,
124			evaluators?: number,
125			provider?: ProviderName,
126			evaluatorFilter?: EvaluatorFilter,
127			timeout?: number,
128			concurrency?: number,
129			selectedEvaluators?: string[],
130		): Promise<IEvaluateResponse> => {
131			setIsLoading(true);
132			setError(null);
133
134			try {
135				const response = await fetch("/api/evaluate", {
136					method: "POST",
137					headers: { "Content-Type": "application/json" },
138					body: JSON.stringify({
139						repositoryUrl,
140						options: {
141							...(evaluators ? { evaluators } : {}),
142							...(provider ? { provider } : {}),
143							...(evaluatorFilter ? { evaluatorFilter } : {}),
144							...(timeout ? { timeout } : {}),
145							...(concurrency ? { concurrency } : {}),
146							...(selectedEvaluators ? { selectedEvaluators } : {}),
147						},
148					}),
149				});
150
151				if (!response.ok) {
152					const errorData = await response.json().catch(() => ({}));
153
154					// Handle queue full error (429) with user-friendly message
155					if (response.status === 429 || errorData.code === "QUEUE_FULL") {
156						throw new Error(
157							"The evaluation queue is currently at capacity. Please wait a few moments for running evaluations to complete, then try again.",
158						);
159					}
160
161					throw new Error(errorData.error || `HTTP error ${response.status}`);
162				}
163
164				const data = await response.json();
165
166				// Ensure sseUrl is an absolute URL
167				if (data.sseUrl && data.sseUrl.startsWith("/")) {
168					data.sseUrl = `${window.location.origin}${data.sseUrl}`;
169				}
170
171				return data as IEvaluateResponse;
172			} catch (err) {
173				const message =
174					err instanceof Error
175						? err.message
176						: "Failed to submit evaluation job";
177				setError(message);
178				throw err;
179			} finally {
180				setIsLoading(false);
181			}
182		},
183		[],
184	);
185
186	const getJobStatus = useCallback(
187		async (jobId: string): Promise<IJobStatusResponse> => {
188			try {
189				const response = await fetch(`/api/evaluate/${jobId}`, {
190					method: "GET",
191					headers: { "Content-Type": "application/json" },
192				});
193
194				if (!response.ok) {
195					const errorData = await response.json().catch(() => ({}));
196					throw new Error(errorData.error || `HTTP error ${response.status}`);
197				}
198
199				return (await response.json()) as IJobStatusResponse;
200			} catch (err) {
201				const message =
202					err instanceof Error ? err.message : "Failed to get job status";
203				setError(message);
204				throw err;
205			}
206		},
207		[],
208	);
209
210	const cancelJob = useCallback(async (jobId: string): Promise<void> => {
211		try {
212			const response = await fetch(`/api/evaluate/${jobId}`, {
213				method: "DELETE",
214				headers: { "Content-Type": "application/json" },
215			});
216
217			if (!response.ok && response.status !== 404) {
218				const errorData = await response.json().catch(() => ({}));
219				throw new Error(errorData.error || `HTTP error ${response.status}`);
220			}
221		} catch (err) {
222			// Silently handle cancel errors - job might already be completed
223			console.warn("[API] Cancel job error:", err);
224		}
225	}, []);
226
227	const submitBatch = useCallback(
228		async (
229			urls: string[],
230			evaluators?: number,
231			provider?: ProviderName,
232			evaluatorFilter?: EvaluatorFilter,
233			timeout?: number,
234			concurrency?: number,
235			selectedEvaluators?: string[],
236		): Promise<IBatchEvaluateResponse> => {
237			setIsLoading(true);
238			setError(null);
239
240			try {
241				const response = await fetch("/api/evaluate/batch", {
242					method: "POST",
243					headers: { "Content-Type": "application/json" },
244					body: JSON.stringify({
245						urls,
246						options: {
247							...(evaluators ? { evaluators } : {}),
248							...(provider ? { provider } : {}),
249							...(evaluatorFilter ? { evaluatorFilter } : {}),
250							...(timeout ? { timeout } : {}),
251							...(concurrency ? { concurrency } : {}),
252							...(selectedEvaluators ? { selectedEvaluators } : {}),
253						},
254					}),
255				});
256
257				if (!response.ok) {
258					const errorData = await response.json().catch(() => ({}));
259					throw new Error(errorData.error || `HTTP error ${response.status}`);
260				}
261
262				return (await response.json()) as IBatchEvaluateResponse;
263			} catch (err) {
264				const message =
265					err instanceof Error
266						? err.message
267						: "Failed to submit batch evaluation";
268				setError(message);
269				throw err;
270			} finally {
271				setIsLoading(false);
272			}
273		},
274		[],
275	);
276
277	const getBatchStatus = useCallback(
278		async (batchId: string): Promise<IBatchStatusResponse> => {
279			try {
280				const response = await fetch(`/api/evaluate/batch/${batchId}`, {
281					method: "GET",
282					headers: { "Content-Type": "application/json" },
283				});
284
285				if (!response.ok) {
286					const errorData = await response.json().catch(() => ({}));
287					throw new Error(errorData.error || `HTTP error ${response.status}`);
288				}
289
290				return (await response.json()) as IBatchStatusResponse;
291			} catch (err) {
292				const message =
293					err instanceof Error ? err.message : "Failed to get batch status";
294				setError(message);
295				throw err;
296			}
297		},
298		[],
299	);
300
301	const generateRemediationPrompts = useCallback(
302		async (
303			evaluationId: string,
304			issues: Issue[],
305			targetFileType: "AGENTS.md" | "CLAUDE.md",
306		): Promise<RemediationPromptsResponse> => {
307			try {
308				const response = await fetch("/api/remediation/generate-prompts", {
309					method: "POST",
310					headers: { "Content-Type": "application/json" },
311					body: JSON.stringify({
312						evaluationId,
313						issues,
314						targetFileType,
315					}),
316				});
317
318				if (!response.ok) {
319					const errorData = await response.json().catch(() => ({}));
320					throw new Error(errorData.error || `HTTP error ${response.status}`);
321				}
322
323				return (await response.json()) as RemediationPromptsResponse;
324			} catch (err) {
325				const message =
326					err instanceof Error
327						? err.message
328						: "Failed to generate remediation prompts";
329				setError(message);
330				throw err;
331			}
332		},
333		[],
334	);
335
336	const executeRemediation = useCallback(
337		async (
338			evaluationId: string,
339			issues: Issue[],
340			targetFileType: "AGENTS.md" | "CLAUDE.md",
341			provider: ProviderName,
342		): Promise<ExecuteRemediationResponse> => {
343			try {
344				const response = await fetch("/api/remediation/execute", {
345					method: "POST",
346					headers: { "Content-Type": "application/json" },
347					body: JSON.stringify({
348						evaluationId,
349						issues,
350						targetFileType,
351						provider,
352					}),
353				});
354
355				if (!response.ok) {
356					const errorData = await response.json().catch(() => ({}));
357					throw new Error(errorData.error || `HTTP error ${response.status}`);
358				}
359
360				const data = await response.json();
361
362				// Ensure sseUrl is absolute
363				if (data.sseUrl && data.sseUrl.startsWith("/")) {
364					data.sseUrl = `${window.location.origin}${data.sseUrl}`;
365				}
366
367				return data as ExecuteRemediationResponse;
368			} catch (err) {
369				const message =
370					err instanceof Error ? err.message : "Failed to execute remediation";
371				setError(message);
