 1/**
 2 * Renders parsed unified diff with syntax-colored lines.
 3 */
 4
 5import { useMemo } from "react";
 6import { parseDiff } from "../lib/diff-parser";
 7
 8interface DiffViewerProps {
 9	diff: string;
10}
11
12export function DiffViewer({ diff }: DiffViewerProps) {
13	const hunks = useMemo(() => parseDiff(diff), [diff]);
14
15	if (hunks.length === 0) {
16		return (
17			<div className="diff-viewer">
18				<span className="text-slate-500 text-xs italic">No changes</span>
19			</div>
20		);
21	}
22
23	return (
24		<div className="diff-viewer">
25			{hunks.map((hunk, hi) => (
26				<div key={hi}>
27					{hunk.lines.map((line, li) => {
28						let lineClass = "diff-line";
29						let prefixClass = "diff-line-prefix";
30						let prefix = " ";
31
32						switch (line.type) {
33							case "header":
34								lineClass += " diff-line-header";
35								return (
36									<div key={li} className={lineClass}>
37										<span className="diff-line-number" />
38										<span className="diff-line-number" />
39										<span className="diff-line-content">{line.content}</span>
40									</div>
41								);
42							case "addition":
43								lineClass += " diff-line-addition";
44								prefixClass += " text-green-400";
45								prefix = "+";
46								break;
47							case "deletion":
48								lineClass += " diff-line-deletion";
49								prefixClass += " text-red-400";
50								prefix = "-";
51								break;
52							case "context":
53								break;
54						}
55
56						return (
57							<div key={li} className={lineClass}>
58								<span className="diff-line-number">
59									{line.oldLineNumber ?? ""}
60								</span>
61								<span className="diff-line-number">
62									{line.newLineNumber ?? ""}
63								</span>
64								<span className={prefixClass}>{prefix}</span>
65								<span className="diff-line-content">{line.content}</span>
66							</div>
67						);
68					})}
69				</div>
70			))}
71		</div>
72	);
73}
