 1/**
 2 * Pure utility to parse unified diff text into structured DiffHunk arrays.
 3 */
 4
 5import type { DiffHunk } from "../types/remediation";
 6
 7export function parseDiff(diffText: string): DiffHunk[] {
 8	if (!diffText.trim()) return [];
 9
10	const hunks: DiffHunk[] = [];
11	const lines = diffText.split("\n");
12
13	let currentHunk: DiffHunk | null = null;
14	let oldLine = 0;
15	let newLine = 0;
16
17	for (const line of lines) {
18		// Hunk header: @@ -oldStart,oldCount +newStart,newCount @@
19		const hunkMatch = line.match(
20			/^@@ -(\d+)(?:,\d+)? \+(\d+)(?:,\d+)? @@(.*)$/,
21		);
22		if (hunkMatch) {
23			currentHunk = {
24				header: line,
25				lines: [{ type: "header", content: line }],
26			};
27			hunks.push(currentHunk);
28			oldLine = Number.parseInt(hunkMatch[1]!, 10);
29			newLine = Number.parseInt(hunkMatch[2]!, 10);
30			continue;
31		}
32
33		if (!currentHunk) continue;
34
35		if (line.startsWith("+")) {
36			currentHunk.lines.push({
37				type: "addition",
38				content: line.slice(1),
39				newLineNumber: newLine++,
40			});
41		} else if (line.startsWith("-")) {
42			currentHunk.lines.push({
43				type: "deletion",
44				content: line.slice(1),
45				oldLineNumber: oldLine++,
46			});
47		} else if (line.startsWith(" ") || line === "") {
48			// Context line (only within a hunk that has started)
49			if (currentHunk.lines.length > 1 || line.startsWith(" ")) {
50				currentHunk.lines.push({
51					type: "context",
52					content: line.startsWith(" ") ? line.slice(1) : line,
53					oldLineNumber: oldLine++,
54					newLineNumber: newLine++,
55				});
56			}
57		}
58	}
59
60	return hunks;
61}
