  1import { useCallback, useEffect, useRef, useState } from "react";
  2import type { SSEEvent } from "../types/job";
  3
  4interface IUseSSEOptions {
  5	url: string | null;
  6	onMessage: (event: SSEEvent) => void;
  7	onOpen?: () => void;
  8	onError?: (error: Event) => void;
  9}
 10
 11interface IUseSSEReturn {
 12	isConnected: boolean;
 13	close: () => void;
 14}
 15
 16/**
 17 * Hook for Server-Sent Events (SSE) connection
 18 *
 19 * Benefits over WebSocket:
 20 * - Built-in automatic reconnection
 21 * - Simpler protocol (standard HTTP)
 22 * - Better proxy/load balancer compatibility
 23 * - Perfect for unidirectional server  client communication
 24 */
 25export function useSSE(options: IUseSSEOptions): IUseSSEReturn {
 26	const { url, onMessage, onOpen, onError } = options;
 27
 28	const [isConnected, setIsConnected] = useState(false);
 29	const eventSourceRef = useRef<EventSource | null>(null);
 30	const shouldConnectRef = useRef(true);
 31	// Track if onOpen has been called to prevent duplicate calls on reconnection
 32	const hasCalledOnOpenRef = useRef(false);
 33
 34	// Store callbacks in refs to prevent reconnection on callback changes
 35	const onMessageRef = useRef(onMessage);
 36	const onOpenRef = useRef(onOpen);
 37	const onErrorRef = useRef(onError);
 38
 39	// Update refs when callbacks change
 40	useEffect(() => {
 41		onMessageRef.current = onMessage;
 42	}, [onMessage]);
 43
 44	useEffect(() => {
 45		onOpenRef.current = onOpen;
 46	}, [onOpen]);
 47
 48	useEffect(() => {
 49		onErrorRef.current = onError;
 50	}, [onError]);
 51
 52	// Store URL in ref for stable access
 53	const urlRef = useRef(url);
 54	useEffect(() => {
 55		urlRef.current = url;
 56	}, [url]);
 57
 58	// Connect when URL changes
 59	useEffect(() => {
 60		if (!url) return;
 61
 62		shouldConnectRef.current = true;
 63		// Reset onOpen tracking when URL changes (new connection)
 64		hasCalledOnOpenRef.current = false;
 65
 66		// Clean up existing connection
 67		if (eventSourceRef.current) {
 68			eventSourceRef.current.close();
 69			eventSourceRef.current = null;
 70		}
 71
 72		console.log("[SSE] Connecting to", url);
 73		const eventSource = new EventSource(url);
 74
 75		eventSource.onopen = () => {
 76			console.log("[SSE] Connected to", url);
 77			setIsConnected(true);
 78			// Only call onOpen once per URL to prevent duplicate "Connected" logs on auto-reconnect
 79			if (!hasCalledOnOpenRef.current) {
 80				hasCalledOnOpenRef.current = true;
 81				onOpenRef.current?.();
 82			}
 83		};
 84
 85		eventSource.onmessage = (event) => {
 86			try {
 87				const data = JSON.parse(event.data);
 88				// Handle both wrapped and direct event formats
 89				if (data.type) {
 90					onMessageRef.current(data as SSEEvent);
 91				}
 92			} catch (err) {
 93				console.error("[SSE] Failed to parse message:", err);
 94			}
 95		};
 96
 97		eventSource.onerror = (error) => {
 98			console.error("[SSE] Error:", error);
 99			setIsConnected(false);
100			// Only call onError if this was a user-initiated connection (not auto-reconnect)
101			// EventSource auto-reconnects, so errors during reconnection are expected
102			if (eventSource.readyState === EventSource.CLOSED) {
103				onErrorRef.current?.(error);
104			}
105		};
106
107		eventSourceRef.current = eventSource;
108
109		return () => {
110			console.log("[SSE] Cleanup for", url);
111			shouldConnectRef.current = false;
112			hasCalledOnOpenRef.current = false;
113			if (eventSourceRef.current) {
114				eventSourceRef.current.close();
115				eventSourceRef.current = null;
116			}
117			setIsConnected(false);
118		};
119	}, [url]);
120
121	const close = useCallback(() => {
122		shouldConnectRef.current = false;
123		hasCalledOnOpenRef.current = false;
124		if (eventSourceRef.current) {
125			eventSourceRef.current.close();
126			eventSourceRef.current = null;
127		}
128		setIsConnected(false);
129	}, []);
130
131	return { isConnected, close };
132}
