  1import { useCallback, useMemo, useState } from "react";
  2import type { ProviderName } from "./useEvaluationApi";
  3
  4/**
  5 * Provider status information
  6 */
  7export interface ProviderStatus {
  8	name: ProviderName;
  9	displayName: string;
 10	available: boolean;
 11}
 12
 13/**
 14 * Detection state
 15 */
 16interface DetectionState {
 17	status: "idle" | "detecting" | "completed" | "error";
 18	providers: Map<ProviderName, ProviderStatus>;
 19	error: string | null;
 20}
 21
 22/**
 23 * Return type for the useProviderDetection hook
 24 */
 25interface IUseProviderDetectionReturn {
 26	detectProviders: () => Promise<void>;
 27	status: "idle" | "detecting" | "completed" | "error";
 28	providers: Map<ProviderName, ProviderStatus>;
 29	error: string | null;
 30	clearError: () => void;
 31	getProviderStatus: (name: ProviderName) => ProviderStatus | undefined;
 32}
 33
 34/**
 35 * Hook for detecting available AI provider CLIs
 36 *
 37 * Calls the backend /api/providers/detect endpoint to check which CLIs are installed
 38 */
 39export function useProviderDetection(): IUseProviderDetectionReturn {
 40	const [state, setState] = useState<DetectionState>({
 41		status: "idle",
 42		providers: new Map(),
 43		error: null,
 44	});
 45
 46	const detectProviders = useCallback(async (): Promise<void> => {
 47		setState((prev) => ({ ...prev, status: "detecting", error: null }));
 48
 49		try {
 50			const response = await fetch("/api/providers/detect", {
 51				method: "GET",
 52				headers: { "Content-Type": "application/json" },
 53			});
 54
 55			if (!response.ok) {
 56				const errorData = await response.json().catch(() => ({}));
 57				throw new Error(errorData.error || `HTTP error ${response.status}`);
 58			}
 59
 60			const data = await response.json();
 61			const providersArray = data.providers as ProviderStatus[];
 62
 63			// Convert array to Map for efficient lookups
 64			const providersMap = new Map<ProviderName, ProviderStatus>();
 65			for (const provider of providersArray) {
 66				providersMap.set(provider.name, provider);
 67			}
 68
 69			setState({
 70				status: "completed",
 71				providers: providersMap,
 72				error: null,
 73			});
 74		} catch (err) {
 75			const message =
 76				err instanceof Error ? err.message : "Failed to detect providers";
 77
 78			setState((prev) => ({
 79				...prev,
 80				status: "error",
 81				error: message,
 82			}));
 83		}
 84	}, []);
 85
 86	const clearError = useCallback(() => {
 87		setState((prev) => ({ ...prev, error: null }));
 88	}, []);
 89
 90	const getProviderStatus = useCallback(
 91		(name: ProviderName): ProviderStatus | undefined => {
 92			return state.providers.get(name);
 93		},
 94		[state.providers],
 95	);
 96
 97	return useMemo(
 98		() => ({
 99			detectProviders,
100			status: state.status,
101			providers: state.providers,
102			error: state.error,
103			clearError,
104			getProviderStatus,
105		}),
106		[
107			detectProviders,
108			state.status,
109			state.providers,
110			state.error,
111			clearError,
112			getProviderStatus,
113		],
114	);
115}
