  50			fileChanges: data.fileChanges ?? [],
  51			totalAdditions: data.totalAdditions ?? 0,
  52			totalDeletions: data.totalDeletions ?? 0,
  53			filesChanged: data.filesChanged ?? 0,
  54			totalDurationMs: data.totalDurationMs ?? 0,
  55			totalCostUsd: data.totalCostUsd ?? 0,
  56			totalInputTokens: data.totalInputTokens ?? 0,
  57			totalOutputTokens: data.totalOutputTokens ?? 0,
  58			summary: data.summary ?? undefined,
  59			errorFixStats: data.promptStats?.errorFixStats,
  60			suggestionEnrichStats: data.promptStats?.suggestionEnrichStats,
  61		};
  62	}
  63
  64	return null;
  65}
  66
  67type Phase = "config" | "progress" | "results";
  68
  69interface RemediateTabProps {
  70	evaluationId: string | null;
  71	evaluationData: EvaluationOutput | null;
  72	selectedIssueKeys: Set<string>;
  73	issueKeyMap: Map<string, Issue>;
  74	onRemoveIssue: (key: string) => void;
  75	onClearAll: () => void;
  76	cloudMode?: boolean;
  77}
  78
  79export function RemediateTab({
  80	evaluationId,
  81	evaluationData,
  82	selectedIssueKeys,
  83	issueKeyMap,
  84	onRemoveIssue,
  85	onClearAll,
  86	cloudMode = false,
  87}: RemediateTabProps) {
  88	const api = useEvaluationApi();
  89	const providerDetection = useProviderDetection();
  90
  91	const [phase, setPhase] = useState<Phase>("config");
  92	const [targetFileType, setTargetFileType] = useState<
  93		"AGENTS.md" | "CLAUDE.md"
  94	>("AGENTS.md");
  95	const [selectedProvider, setSelectedProvider] =
  96		useState<ProviderName>("claude");
  97	const [isExecuting, setIsExecuting] = useState(false);
  98	const [showConfirmModal, setShowConfirmModal] = useState(false);
  99	const [showDeleteModal, setShowDeleteModal] = useState(false);
 100	const [isDeleting, setIsDeleting] = useState(false);
 101	const [executeError, setExecuteError] = useState<string | null>(null);
 102
 103	// SSE state
 104	const [sseUrl, setSseUrl] = useState<string | null>(null);
 105	const [remediationId, setRemediationId] = useState<string | null>(null);
 106	const [progressState, setProgressState] = useState<IRemediationProgressState>(
 107		{
 108			status: "queued",
 109			logs: [],
 110		},
 111	);
 112
 113	// Results state
 114	const [result, setResult] = useState<RemediationResult | null>(null);
 115
 116	// Legacy prompt generation state
 117	const [isGenerating, setIsGenerating] = useState(false);
 118	const [generateError, setGenerateError] = useState<string | null>(null);
 119	const [prompts, setPrompts] = useState<RemediationPromptsResponse | null>(
 120		null,
 121	);
 122
 123	// Detect providers on mount
 124	useEffect(() => {
 125		if (providerDetection.status === "idle") {
 126			providerDetection.detectProviders();
 127		}
 128	}, [providerDetection]);
 129
 130	// Restore existing remediation on mount / evaluationId change
 131	useEffect(() => {
 132		if (!evaluationId) return;
 133
 134		let cancelled = false;
 135
 136		async function loadExistingRemediation() {
 137			const data = await api.getRemediationForEvaluation(evaluationId!);
 138			if (cancelled || !data) return;
 139
 140			if (data.status === "completed") {
 141				const restoredResult = toRemediationResult(data);
 142				if (restoredResult) {
 143					setRemediationId(data.id);
 144					setResult(restoredResult);
 145					setPhase("results");
 146				}
 147			} else if (data.status === "running" || data.status === "queued") {
 148				setRemediationId(data.id);
 149				setSseUrl(
 150					`${window.location.origin}/api/remediation/${data.id}/progress`,
 151				);
 152				setProgressState({
 153					status: data.status as "queued" | "running",
 154					logs: [],
 155				});
 156				setPhase("progress");
 157			}
 158			// "failed" or anything else: stay in config phase
 159		}
 160
 161		loadExistingRemediation();
 162
 163		return () => {
 164			cancelled = true;
 165		};
 166	}, [evaluationId, api]);
 167
 168	// Available providers for the selector
 169	const availableProviders = useMemo(() => {
 170		const providers: ProviderStatus[] = [];
 171		for (const p of providerDetection.providers.values()) {
 172			if (p.available && p.name !== "random") {
 173				providers.push(p);
 174			}
 175		}
 176		return providers;
 177	}, [providerDetection.providers]);
 178
 179	// Resolve selected issue keys to actual issues, split by type
 180	const { errorEntries, suggestionEntries } = useMemo(() => {
 181		const errors: { key: string; issue: Issue }[] = [];
 182		const suggestions: { key: string; issue: Issue }[] = [];
 183
 184		for (const key of selectedIssueKeys) {
 185			const issue = issueKeyMap.get(key);
 186			if (!issue) continue;
 187			const type = getIssueType(issue);
 188			if (type === "error") {
 189				errors.push({ key, issue });
 190			} else {
 191				suggestions.push({ key, issue });
 192			}
 193		}
 194
 195		errors.sort(
 196			(a, b) => getIssueSeverity(b.issue) - getIssueSeverity(a.issue),
 197		);
 198		const impactOrder: Record<string, number> = {
 199			High: 0,
 200			Medium: 1,
 201			Low: 2,
 202		};
 203		suggestions.sort(
 204			(a, b) =>
 205				(impactOrder[a.issue.impactLevel ?? "Low"] ?? 2) -
 206				(impactOrder[b.issue.impactLevel ?? "Low"] ?? 2),
 207		);
 208
 209		return { errorEntries: errors, suggestionEntries: suggestions };
 210	}, [selectedIssueKeys, issueKeyMap]);
 211
 212	const totalSelected = errorEntries.length + suggestionEntries.length;
 213
 214	// SSE message handler
 215	const handleSSEMessage = useCallback(
 216		// biome-ignore lint/suspicious/noExplicitAny: SSE event data varies by type
 217		(event: any) => {
 218			const timestamp = new Date().toISOString();
 219
 220			switch (event.type) {
 221				case "remediation.started":
 222					setProgressState((prev) => ({
 223						...prev,
 224						status: "running",
 225						logs: [...prev.logs, { timestamp, message: "Remediation started" }],
 226					}));
 227					break;
 228
 229				case "remediation.progress": {
 230					const d = event.data ?? {};
 231					setProgressState((prev) => ({
 232						...prev,
 233						...(d.errorCount !== undefined && {
 234							errorCount: d.errorCount as number,
 235						}),
 236						...(d.suggestionCount !== undefined && {
 237							suggestionCount: d.suggestionCount as number,
 238						}),
 239						...(d.totalBatches !== undefined && {
 240							totalBatches: d.totalBatches as number,
 241						}),
 242						...(d.completedBatches !== undefined && {
 243							completedBatches: d.completedBatches as number,
 244						}),
 245						...(d.phase !== undefined && {
 246							currentPhase: d.phase as "errors" | "suggestions",
 247						}),
 248						...(d.runningTotalCostUsd !== undefined && {
 249							runningTotalCostUsd: d.runningTotalCostUsd as number,
 250						}),
 251						...(d.runningTotalDurationMs !== undefined && {
 252							runningTotalDurationMs: d.runningTotalDurationMs as number,
 253						}),
 254						...(d.runningTotalInputTokens !== undefined && {
 255							runningTotalInputTokens: d.runningTotalInputTokens as number,
 256						}),
 257						...(d.runningTotalOutputTokens !== undefined && {
 258							runningTotalOutputTokens: d.runningTotalOutputTokens as number,
 259						}),
 260					}));
 261					break;
 262				}
 263
 264				case "remediation.step.started": {
 265					const bi = event.data?.batchInfo as
 266						| { batchNumber: number; totalBatches: number }
 267						| undefined;
 268					const batchLabel = bi
 269						? ` (batch ${bi.batchNumber}/${bi.totalBatches})`
 270						: "";
 271					const issueSummaries = event.data?.issuesSummary as
 272						| string[]
 273						| undefined;
 274					setProgressState((prev) => ({
 275						...prev,
 276						currentStep: event.data?.step,
 277						batchInfo: bi,
 278						...(issueSummaries && { currentBatchIssues: issueSummaries }),
 279						logs: [
 280							...prev.logs,
 281							{
 282								timestamp,
 283								message: `Step started: ${event.data?.step}${batchLabel}`,
 284							},
 285						],
 286					}));
 287					break;
 288				}
 289
 290				case "remediation.step.completed": {
 291					const bi2 = event.data?.batchInfo as
 292						| { batchNumber: number; totalBatches: number }
 293						| undefined;
 294					const batchLabel2 = bi2
 295						? ` (batch ${bi2.batchNumber}/${bi2.totalBatches})`
 296						: "";
 297					const step = event.data?.step as string | undefined;
 298					const batchDurationMs = event.data?.batchDurationMs as
 299						| number
 300						| undefined;
 301					const batchCostUsd = event.data?.batchCostUsd as number | undefined;
 302					const batchInputTokens = event.data?.batchInputTokens as
 303						| number
 304						| undefined;
 305					const batchOutputTokens = event.data?.batchOutputTokens as
 306						| number
 307						| undefined;
 308
 309					const hasBatchStats =
 310						batchDurationMs !== undefined && batchCostUsd !== undefined;
 311					let logMessage = `Step completed: ${step}${batchLabel2}`;
 312					if (hasBatchStats) {
 313						const phaseName =
 314							step === "executing_error_fix"
 315								? "Error fix"
 316								: "Suggestion enrich";
 317						const label = bi2
 318							? `${phaseName} batch ${bi2.batchNumber}/${bi2.totalBatches}`
 319							: phaseName;
 320						logMessage = `${label} completed: ${Math.round(batchDurationMs / 1000)}s, $${batchCostUsd.toFixed(4)}`;
 321					}
 322
 323					setProgressState((prev) => ({
 324						...prev,
 325						batchInfo: undefined,
 326						currentBatchIssues: undefined,
 327						...(hasBatchStats && {
 328							lastBatchStats: {
 329								durationMs: batchDurationMs!,
 330								costUsd: batchCostUsd!,
 331								inputTokens: batchInputTokens ?? 0,
 332								outputTokens: batchOutputTokens ?? 0,
 333							},
 334						}),
 335						logs: [...prev.logs, { timestamp, message: logMessage }],
 336					}));
 337					break;
 338				}
 339
 340				case "remediation.completed": {
 341					const remResult = event.data?.result as RemediationResult | undefined;
 342					setProgressState((prev) => ({
 343						...prev,
 344						status: "completed",
 345						result: remResult,
 346						logs: [
 347							...prev.logs,
 348							{ timestamp, message: "Remediation completed" },
 349						],
 350					}));
 351					if (remResult) {
 352						setResult(remResult);
 353					}
 354					setPhase("results");
 355					setSseUrl(null);
 356					break;
 357				}
 358
 359				case "remediation.failed":
 360					setProgressState((prev) => ({
 361						...prev,
 362						status: "failed",
 363						error: event.data?.error,
 364						logs: [
 365							...prev.logs,
 366							{
 367								timestamp,
 368								message: `Failed: ${event.data?.error?.message || "Unknown error"}`,
 369							},
 370						],
 371					}));
 372					setExecuteError(event.data?.error?.message || "Remediation failed");
 373					setPhase("config");
 374					setSseUrl(null);
 375					break;
 376			}
 377		},
 378		[],
 379	);
 380
 381	useSSE({
 382		url: sseUrl,
 383		onMessage: handleSSEMessage,
 384	});
 385
 386	// Execute remediation
 387	const handleExecute = useCallback(async () => {
 388		if (!evaluationId || totalSelected === 0) return;
 389
 390		setIsExecuting(true);
 391		setExecuteError(null);
 392		setPrompts(null);
 393
 394		try {
 395			const issues = [
 396				...errorEntries.map((e) => e.issue),
 397				...suggestionEntries.map((e) => e.issue),
 398			];
 399
 400			const response = await api.executeRemediation(
 401				evaluationId,
 402				issues,
 403				targetFileType,
 404				selectedProvider,
 405			);
 406
 407			setRemediationId(response.remediationId);
 408			setSseUrl(response.sseUrl);
 409			setProgressState({ status: "queued", logs: [] });
 410			setPhase("progress");
 411		} catch (err) {
 412			setExecuteError(
 413				err instanceof Error ? err.message : "Failed to start remediation",
 414			);
 415		} finally {
 416			setIsExecuting(false);
 417		}
 418	}, [
 419		evaluationId,
 420		totalSelected,
 421		errorEntries,
 422		suggestionEntries,
 423		targetFileType,
 424		selectedProvider,
 425		api,
 426	]);
 427
 428	// Generate prompts (legacy copy-paste mode)
 429	const handleGeneratePrompts = useCallback(async () => {
 430		if (!evaluationId || totalSelected === 0) return;
 431
 432		setIsGenerating(true);
 433		setGenerateError(null);
 434
 435		try {
 436			const issues = [
 437				...errorEntries.map((e) => e.issue),
 438				...suggestionEntries.map((e) => e.issue),
 439			];
 440			const result = await api.generateRemediationPrompts(
 441				evaluationId,
 442				issues,
 443				targetFileType,
 444			);
 445			setPrompts(result);
 446		} catch (err) {
 447			setGenerateError(
 448				err instanceof Error ? err.message : "Failed to generate prompts",
 449			);
 450		} finally {
 451			setIsGenerating(false);
 452		}
 453	}, [
 454		evaluationId,
 455		totalSelected,
 456		errorEntries,
 457		suggestionEntries,
 458		targetFileType,
 459		api,
 460	]);
 461
 462	const handleDeleteRemediation = useCallback(async () => {
 463		if (!remediationId) return;
 464		setIsDeleting(true);
 465		try {
 466			await api.deleteRemediation(remediationId);
 467			setPhase("config");
 468			setResult(null);
 469			setRemediationId(null);
 470			setSseUrl(null);
 471			setProgressState({ status: "queued", logs: [] });
 472			setShowDeleteModal(false);
 473		} catch (err) {
 474			setExecuteError(
 475				err instanceof Error ? err.message : "Failed to delete remediation",
 476			);
 477		} finally {
 478			setIsDeleting(false);
 479		}
 480	}, [remediationId, api]);
 481
 482	if (!evaluationData) {
 483		return (
 484			<div className="card text-center py-12">
 485				<p className="text-body-muted">
 486					No evaluation data. Run an evaluation first to generate remediation
 487					prompts.
 488				</p>
 489			</div>
 490		);
 491	}
 492
 493	if (totalSelected === 0 && phase === "config") {
 494		return (
 495			<div className="space-y-6">
 496				<div className="card">
 497					<h2 className="text-heading text-slate-100">Remediation</h2>
 498					<p className="text-body-muted mt-1">
 499						Execute remediation to fix issues automatically
 500					</p>
 501				</div>
 502				<div className="card text-center py-12">
 503					<svg
 504						className="w-12 h-12 text-slate-600 mx-auto mb-4"
 505						fill="none"
 506						stroke="currentColor"
 507						viewBox="0 0 24 24"
 508					>
 509						<path
 510							strokeLinecap="round"
 511							strokeLinejoin="round"
 512							strokeWidth={1.5}
 513							d="M12 4v16m8-8H4"
 514						/>
 515					</svg>
 516					<p className="text-body-muted">
 517						No issues selected. Use the{" "}
 518						<span className="inline-flex items-center justify-center w-5 h-5 rounded bg-slate-700 text-slate-300 text-xs align-middle mx-0.5">
 519							+
 520						</span>{" "}
 521						button on issue cards in the Errors and Suggestions tabs.
 522					</p>
 523				</div>
 524			</div>
 525		);
 526	}
 527
 528	//  PROGRESS PHASE 
 529	if (phase === "progress") {
 530		return (
 531			<div className="space-y-6">
 532				<div className="card">
 533					<h2 className="text-heading text-slate-100">Executing Remediation</h2>
 534					<p className="text-body-muted mt-1">
 535						Running {selectedProvider} agent to fix {totalSelected} issue
 536						{totalSelected !== 1 ? "s" : ""}...
 537					</p>
 538				</div>
 539				<RemediationProgress
 540					currentStep={progressState.currentStep}
 541					batchInfo={progressState.batchInfo}
 542					logs={progressState.logs}
 543					errorCount={progressState.errorCount}
 544					suggestionCount={progressState.suggestionCount}
 545					totalBatches={progressState.totalBatches}
 546					completedBatches={progressState.completedBatches}
 547					currentPhase={progressState.currentPhase}
 548					runningTotalCostUsd={progressState.runningTotalCostUsd}
 549					runningTotalDurationMs={progressState.runningTotalDurationMs}
 550					runningTotalInputTokens={progressState.runningTotalInputTokens}
 551					runningTotalOutputTokens={progressState.runningTotalOutputTokens}
 552					currentBatchIssues={progressState.currentBatchIssues}
 553				/>
 554			</div>
 555		);
 556	}
 557
 558	//  RESULTS PHASE 
 559	if (phase === "results" && result) {
 560		const hasPerPromptStats =
 561			result.errorFixStats || result.suggestionEnrichStats;
 562
 563		return (
 564			<div className="space-y-6">
 565				<div className="card">
 566					<div className="flex items-center justify-between">
 567						<div>
 568							<h2 className="text-heading text-slate-100">
 569								Remediation Complete
 570							</h2>
 571							<p className="text-body-muted mt-1">
 572								{result.filesChanged} file
 573								{result.filesChanged !== 1 ? "s" : ""} changed
 574								<span className="text-green-400 ml-2">
 575									+{result.totalAdditions}
 576								</span>
 577								<span className="text-red-400 ml-1">
 578									-{result.totalDeletions}
 579								</span>
 580								{result.totalDurationMs > 0 && (
 581									<span className="text-slate-500 ml-2">
 582										in {Math.round(result.totalDurationMs / 1000)}s
 583									</span>
 584								)}
 585								{result.totalCostUsd > 0 && (
 586									<span className="text-slate-500 ml-2">
 587										(${result.totalCostUsd.toFixed(4)})
 588									</span>
 589								)}
 590							</p>
 591							{(result.totalInputTokens > 0 ||
 592								result.totalOutputTokens > 0) && (
 593								<p className="text-caption mt-1">
 594									{result.totalInputTokens.toLocaleString()} input /{" "}
 595									{result.totalOutputTokens.toLocaleString()} output tokens
 596									{hasPerPromptStats && (
 597										<span className="text-slate-500">
 598											{" "}
 599											&mdash;{" "}
 600											{result.errorFixStats && (
 601												<span>
 602													Error fix: $
 603													{(result.errorFixStats.costUsd ?? 0).toFixed(4)}
 604													{result.suggestionEnrichStats && " | "}
 605												</span>
 606											)}
 607											{result.suggestionEnrichStats && (
 608												<span>
 609													Enrich: $
 610													{(result.suggestionEnrichStats.costUsd ?? 0).toFixed(
 611														4,
 612													)}
 613												</span>
 614											)}
 615										</span>
 616									)}
 617								</p>
 618							)}
 619						</div>
 620						<div className="flex items-center gap-2">
 621							{remediationId && <PatchDownload remediationId={remediationId} />}
 622							{!cloudMode && (
 623								<button
 624									onClick={() => setShowDeleteModal(true)}
 625									disabled={isDeleting}
 626									className="btn-secondary text-red-400 hover:text-red-300"
 627								>
 628									{isDeleting ? "Deleting..." : "Delete & Start Over"}
 629								</button>
 630							)}
 631						</div>
 632					</div>
 633				</div>
 634
 635				{result.summary?.parsed && (
 636					<ActionSummarySection summary={result.summary} />
 637				)}
 638
 639				{result.fileChanges.length === 0 ? (
 640					<div className="card text-center py-8">
 641						<p className="text-body-muted">
 642							No file changes were made by the agent.
 643						</p>
 644					</div>
 645				) : (
 646					<div className="space-y-3">
 647						{result.fileChanges.map((file: FileChange) => {
 648							const fileSummaries = getFileSummaries(file.path, result);
 649							return (
 650								<FileChangeCard
 651									key={file.path}
 652									file={file}
 653									defaultExpanded={result.fileChanges.length <= 3}
 654									summaries={fileSummaries}
 655								/>
 656							);
 657						})}
 658					</div>
 659				)}
 660
 661				{executeError && <p className="text-sm text-red-400">{executeError}</p>}
 662
 663				{/* Delete Confirmation Modal */}
 664				<Modal
 665					isOpen={showDeleteModal}
 666					onClose={() => setShowDeleteModal(false)}
 667					title="Delete Remediation"
 668					maxWidth="max-w-md"
 669				>
 670					<div className="space-y-4">
 671						<p className="text-sm text-slate-300">
 672							This will permanently delete the remediation result for this
 673							evaluation. You can then re-run remediation with a fresh git
 674							state.
 675						</p>
 676						<div className="flex justify-end gap-3 pt-2">
 677							<button
 678								onClick={() => setShowDeleteModal(false)}
 679								className="btn-secondary"
 680								disabled={isDeleting}
 681							>
 682								Cancel
 683							</button>
 684							<button
 685								onClick={handleDeleteRemediation}
 686								disabled={isDeleting}
 687								className="btn-primary bg-red-600 hover:bg-red-500"
 688							>
 689								{isDeleting ? "Deleting..." : "Delete"}
 690							</button>
 691						</div>
 692					</div>
 693				</Modal>
 694			</div>
 695		);
 696	}
 697
 698	//  CONFIG PHASE 
 699	return (
 700		<div className="space-y-6">
 701			<div className="card">
 702				<h2 className="text-heading text-slate-100">Remediation</h2>
 703				<p className="text-body-muted mt-1">
 704					Execute remediation to fix issues automatically, or generate
 705					copy-paste prompts
 706				</p>
 707			</div>
 708
 709			<div className="card space-y-5">
 710				{/* Target File Type */}
 711				<div>
 712					<label className="text-label text-slate-300 block mb-2">
 713						Target file type
 714					</label>
 715					<div className="flex gap-2">
 716						<button
 717							onClick={() => setTargetFileType("AGENTS.md")}
 718							className={`px-3 py-1.5 rounded text-sm transition-colors ${
 719								targetFileType === "AGENTS.md"
 720									? "bg-blue-600 text-white"
 721									: "bg-slate-700 text-slate-300 hover:bg-slate-600"
 722							}`}
 723						>
 724							AGENTS.md
 725						</button>
 726						<button
 727							onClick={() => setTargetFileType("CLAUDE.md")}
 728							className={`px-3 py-1.5 rounded text-sm transition-colors ${
 729								targetFileType === "CLAUDE.md"
 730									? "bg-blue-600 text-white"
 731									: "bg-slate-700 text-slate-300 hover:bg-slate-600"
 732							}`}
 733						>
 734							CLAUDE.md
 735						</button>
 736					</div>
 737				</div>
 738
 739				{/* Provider Selector */}
 740				<div>
 741					<label className="text-label text-slate-300 block mb-2">
 742						AI Provider
 743					</label>
 744					{providerDetection.status === "detecting" ? (
 745						<div className="text-sm text-slate-400">Detecting providers...</div>
 746					) : availableProviders.length > 0 ? (
 747						<div className="flex flex-wrap gap-2">
 748							{availableProviders.map((p) => (
 749								<button
 750									key={p.name}
 751									onClick={() => setSelectedProvider(p.name)}
 752									className={`px-3 py-1.5 rounded text-sm transition-colors ${
 753										selectedProvider === p.name
 754											? "bg-blue-600 text-white"
 755											: "bg-slate-700 text-slate-300 hover:bg-slate-600"
 756									}`}
 757								>
 758									{p.displayName}
 759								</button>
 760							))}
 761						</div>
 762					) : (
 763						<div className="text-sm text-slate-400">
 764							No providers detected. Defaulting to Claude.
 765						</div>
 766					)}
 767				</div>
 768
 769				{/* Selected Issues */}
 770				<div className="space-y-4">
 771					{errorEntries.length > 0 && (
 772						<IssueSection
 773							title="Errors"
 774							count={errorEntries.length}
 775							entries={errorEntries}
 776							onRemove={onRemoveIssue}
 777						/>
 778					)}
 779					{suggestionEntries.length > 0 && (
 780						<IssueSection
 781							title="Suggestions"
 782							count={suggestionEntries.length}
 783							entries={suggestionEntries}
 784							onRemove={onRemoveIssue}
 785						/>
 786					)}
 787					<div className="flex items-center justify-between">
 788						<span className="text-caption">
 789							{totalSelected} issue{totalSelected !== 1 ? "s" : ""} selected
 790						</span>
 791						<button
 792							onClick={onClearAll}
 793							className="text-sm text-slate-400 hover:text-slate-200 transition-colors"
 794						>
 795							Clear all
 796						</button>
 797					</div>
 798				</div>
 799
 800				{/* Action Buttons */}
 801				<div className="flex flex-wrap gap-3">
 802					<button
 803						onClick={() => setShowConfirmModal(true)}
 804						disabled={isExecuting || isGenerating}
 805						className="btn-primary"
 806					>
 807						{isExecuting ? (
 808							<span className="flex items-center gap-2">
 809								<svg
 810									className="animate-spin h-4 w-4"
 811									fill="none"
 812									viewBox="0 0 24 24"
 813								>
 814									<circle
 815										className="opacity-25"
 816										cx="12"
 817										cy="12"
 818										r="10"
 819										stroke="currentColor"
 820										strokeWidth="4"
 821									/>
 822									<path
 823										className="opacity-75"
 824										fill="currentColor"
 825										d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
 826									/>
 827								</svg>
 828								Starting...
 829							</span>
 830						) : (
 831							`Execute Remediation (${totalSelected})`
 832						)}
 833					</button>
 834					<button
 835						onClick={handleGeneratePrompts}
 836						disabled={isExecuting || isGenerating}
 837						className="btn-secondary"
 838					>
 839						{isGenerating ? "Generating..." : "Generate Prompts"}
 840					</button>
 841				</div>
 842
 843				{executeError && (
 844					<p className="text-sm text-red-400 mt-2">{executeError}</p>
 845				)}
 846				{generateError && (
 847					<p className="text-sm text-red-400 mt-2">{generateError}</p>
 848				)}
 849			</div>
 850
 851			{/* Confirmation Modal */}
 852			<Modal
 853				isOpen={showConfirmModal}
 854				onClose={() => setShowConfirmModal(false)}
 855				title="Confirm Remediation"
 856				maxWidth="max-w-md"
 857			>
 858				<div className="space-y-4">
 859					<p className="text-sm text-slate-300">
 860						This will run the AI provider to remediate the selected issues.
 861						Please confirm the details below.
 862					</p>
 863					<div className="bg-slate-800/50 rounded-lg border border-slate-700/50 p-3 space-y-2 text-sm">
 864						{errorEntries.length > 0 && (
 865							<div className="flex justify-between text-slate-300">
 866								<span>Errors</span>
 867								<span className="font-semibold">{errorEntries.length}</span>
 868							</div>
 869						)}
 870						{suggestionEntries.length > 0 && (
 871							<div className="flex justify-between text-slate-300">
 872								<span>Suggestions</span>
 873								<span className="font-semibold">
 874									{suggestionEntries.length}
 875								</span>
 876							</div>
 877						)}
 878						<div className="flex justify-between text-slate-300">
 879							<span>Target file</span>
 880							<span className="font-semibold">{targetFileType}</span>
 881						</div>
 882						<div className="flex justify-between text-slate-300">
 883							<span>Provider</span>
 884							<span className="font-semibold">{selectedProvider}</span>
 885						</div>
 886					</div>
 887					<div className="flex justify-end gap-3 pt-2">
 888						<button
 889							onClick={() => setShowConfirmModal(false)}
 890							className="btn-secondary"
 891						>
 892							Cancel
 893						</button>
 894						<button
 895							onClick={() => {
 896								setShowConfirmModal(false);
 897								handleExecute();
 898							}}
 899							className="btn-primary"
 900						>
 901							Confirm
 902						</button>
 903					</div>
 904				</div>
 905			</Modal>
 906
 907			{/* Generated Prompts (legacy mode) */}
 908			{prompts && (
 909				<div className="space-y-6">
 910					{prompts.errorFixPrompt && (
 911						<PromptDisplay
 912							title={`Fix Errors Prompt (${prompts.errorCount} issues)`}
 913							content={prompts.errorFixPrompt}
 914						/>
 915					)}
 916					{prompts.suggestionEnrichPrompt && (
 917						<PromptDisplay
 918							title={`Enrich Suggestions Prompt (${prompts.suggestionCount} issues)`}
 919							content={prompts.suggestionEnrichPrompt}
 920						/>
 921					)}
 922				</div>
 923			)}
 924		</div>
 925	);
 926}
 927
 928function IssueSection({
 929	title,
 930	count,
 931	entries,
 932	onRemove,
 933}: {
 934	title: string;
 935	count: number;
 936	entries: { key: string; issue: Issue }[];
 937	onRemove: (key: string) => void;
 938}) {
 939	return (
 940		<div className="bg-slate-800/50 rounded-lg border border-slate-700/50 p-3">
 941			<div className="flex items-center justify-between mb-2">
 942				<span className="text-sm font-semibold text-slate-200">
 943					{title} ({count})
 944				</span>
 945			</div>
 946			<div className="space-y-1 max-h-48 overflow-y-auto custom-scrollbar">
 947				{entries.map(({ key, issue }) => {
 948					const numericSeverity = getIssueSeverity(issue);
 949					const severityLevel = getSeverityLevel(numericSeverity);
 950					const issueTitle =
 951						issue.title || issue.problem || issue.description || issue.category;
 952
 953					return (
 954						<div
 955							key={key}
 956							className="flex items-center gap-2 py-1 px-1 rounded hover:bg-slate-700/30 group"
 957						>
 958							<div
 959								className={`severity-dot severity-dot-${severityLevel} flex-shrink-0`}
 960							/>
 961							<div className="flex-shrink-0 w-[72px]">
 962								{issue.issueType === "error" ? (
 963									<span className={getSeverityColor(issue.severity)}>
 964										{severityLevel.charAt(0).toUpperCase() +
 965											severityLevel.slice(1)}
 966									</span>
 967								) : (
 968									<span className={getImpactBadgeClass(issue.impactLevel)}>
 969										{getImpactLabel(issue.impactLevel)}
 970									</span>
 971								)}
 972							</div>
 973							<span className="flex-1 min-w-0 text-sm text-slate-300 truncate">
 974								{issueTitle}
 975							</span>
 976							{issue.evaluatorName && (
 977								<span className="flex-shrink-0 text-xs text-slate-500 hidden sm:block">
 978									{issue.evaluatorName}
 979								</span>
 980							)}
 981							<button
 982								onClick={() => onRemove(key)}
 983								className="flex-shrink-0 text-slate-500 hover:text-red-400 transition-colors opacity-0 group-hover:opacity-100"
 984								title="Remove from remediation"
 985							>
 986								<svg
 987									className="h-4 w-4"
 988									fill="none"
 989									viewBox="0 0 24 24"
 990									stroke="currentColor"
 991								>
 992									<path
 993										strokeLinecap="round"
 994										strokeLinejoin="round"
 995										strokeWidth={2}
 996										d="M6 18L18 6M6 6l12 12"
 997									/>
 998								</svg>
 999							</button>
1000						</div>
1001					);
1002				})}
1003			</div>
1004		</div>
1005	);
1006}
1007
1008function PromptDisplay({ title, content }: { title: string; content: string }) {
1009	return (
1010		<div className="card">
1011			<div className="flex items-center justify-between mb-3">
1012				<h3 className="text-subheading text-slate-200">{title}</h3>
1013				<CopyButton text={content} variant="text" />
1014			</div>
1015			<div className="bg-slate-900/50 rounded-lg p-4 border border-slate-700/50 font-mono text-xs text-slate-300 max-h-96 overflow-y-auto custom-scrollbar whitespace-pre-wrap">
1016				{content}
1017			</div>
1018		</div>
1019	);
1020}
1021
1022function ActionSummarySection({
1023	summary,
1024}: {
1025	summary: NonNullable<RemediationResult["summary"]>;
1026}) {
1027	const allActions = [
1028		...summary.errorFixActions,
1029		...summary.suggestionEnrichActions,
1030	];
1031
1032	return (
1033		<div className="card">
1034			<div className="flex items-center justify-between mb-3">
1035				<h3 className="text-subheading text-slate-200">Action Summary</h3>
1036				<span className="text-caption">
1037					{summary.addressedCount} addressed
1038					{summary.skippedCount > 0 && (
1039						<span className="text-slate-500">
1040							, {summary.skippedCount} skipped
1041						</span>
1042					)}
1043				</span>
1044			</div>
1045			<div className="space-y-1">
1046				{allActions.map((action) => (
1047					<div
1048						key={`${action.issueIndex}-${action.status}`}
1049						className="flex items-start gap-2 py-1 px-1"
1050					>
1051						{action.status === "skipped" ? (
1052							<span className="text-slate-500 flex-shrink-0 mt-0.5">
1053								<svg
1054									className="w-4 h-4"
1055									fill="none"
1056									viewBox="0 0 24 24"
1057									stroke="currentColor"
1058								>
1059									<path
1060										strokeLinecap="round"
1061										strokeLinejoin="round"
1062										strokeWidth={2}
1063										d="M20 12H4"
1064									/>
1065								</svg>
1066							</span>
1067						) : (
1068							<span className="text-green-400 flex-shrink-0 mt-0.5">
1069								<svg
1070									className="w-4 h-4"
1071									fill="none"
1072									viewBox="0 0 24 24"
1073									stroke="currentColor"
1074								>
1075									<path
1076										strokeLinecap="round"
1077										strokeLinejoin="round"
1078										strokeWidth={2}
1079										d="M5 13l4 4L19 7"
1080									/>
1081								</svg>
1082							</span>
1083						)}
1084						<span className="text-sm text-slate-300 flex-1">
1085							{action.issueTitle ? (
1086								<span className="text-slate-400 text-xs mr-1.5">
1087									{action.issueTitle}
1088								</span>
1089							) : (
1090								<span className="text-slate-400 font-mono text-xs mr-1.5">
1091									#{action.issueIndex}
1092								</span>
1093							)}
1094							{action.summary}
1095						</span>
1096						{action.file && (
1097							<span className="text-xs text-slate-500 font-mono flex-shrink-0">
1098								{action.file}
1099							</span>
1100						)}
1101					</div>
1102				))}
1103			</div>
1104		</div>
1105	);
1106}
1107
1108/**
1109 * Get action summaries relevant to a specific file path.
1110 */
1111function getFileSummaries(
1112	filePath: string,
1113	result: RemediationResult,
1114): RemediationAction[] {
1115	if (!result.summary?.parsed) return [];
1116
1117	const allActions = [
1118		...result.summary.errorFixActions,
1119		...result.summary.suggestionEnrichActions,
1120	];
1121
1122	return allActions.filter(
1123		(a) => a.file && a.status !== "skipped" && filePath.endsWith(a.file),
1124	);
1125}
