import { useCallback, useEffect, useMemo, useState } from "react";
import type {
	ProviderName,
	RemediationForEvaluationResponse,
	RemediationPromptsResponse,
} from "../hooks/useEvaluationApi";
import { useEvaluationApi } from "../hooks/useEvaluationApi";
import {
	type ProviderStatus,
	useProviderDetection,
} from "../hooks/useProviderDetection";
import { useSSE } from "../hooks/useSSE";
import type { EvaluationOutput, Issue } from "../types/evaluation";
import {
	getImpactBadgeClass,
	getImpactLabel,
	getIssueSeverity,
	getIssueType,
	getSeverityColor,
	getSeverityLevel,
} from "../types/evaluation";
import type {
	FileChange,
	IRemediationProgressState,
	RemediationAction,
	RemediationResult,
} from "../types/remediation";
import { FileChangeCard } from "./FileChangeCard";
import { PatchDownload } from "./PatchDownload";
import { RemediationProgress } from "./RemediationProgress";
import { CopyButton } from "./shared/CopyButton";
import { Modal } from "./shared/Modal";

/**
 * Transform a remediation API response (which may be an in-memory job or DB record)
 * into the frontend RemediationResult shape.
 */
function toRemediationResult(
	data: RemediationForEvaluationResponse,
): RemediationResult | null {
	// In-memory job wraps result under `result`
	if (data.result) {
		return data.result;
	}

	// DB record has flat fields
	if (data.fileChanges !== undefined) {
		return {
			fullPatch: data.fullPatch ?? "",
			fileChanges: data.fileChanges ?? [],
			totalAdditions: data.totalAdditions ?? 0,
			totalDeletions: data.totalDeletions ?? 0,
			filesChanged: data.filesChanged ?? 0,
			totalDurationMs: data.totalDurationMs ?? 0,
			totalCostUsd: data.totalCostUsd ?? 0,
			totalInputTokens: data.totalInputTokens ?? 0,
			totalOutputTokens: data.totalOutputTokens ?? 0,
			summary: data.summary ?? undefined,
			errorFixStats: data.promptStats?.errorFixStats,
			suggestionEnrichStats: data.promptStats?.suggestionEnrichStats,
		};
	}

	return null;
}

type Phase = "config" | "progress" | "results";

interface RemediateTabProps {
	evaluationId: string | null;
	evaluationData: EvaluationOutput | null;
	selectedIssueKeys: Set<string>;
	issueKeyMap: Map<string, Issue>;
	onRemoveIssue: (key: string) => void;
	onClearAll: () => void;
	cloudMode?: boolean;
}

export function RemediateTab({
	evaluationId,
	evaluationData,
	selectedIssueKeys,
	issueKeyMap,
	onRemoveIssue,
	onClearAll,
	cloudMode = false,
}: RemediateTabProps) {
	const api = useEvaluationApi();
	const providerDetection = useProviderDetection();

	const [phase, setPhase] = useState<Phase>("config");
	const [targetFileType, setTargetFileType] = useState<
		"AGENTS.md" | "CLAUDE.md"
	>("AGENTS.md");
	const [selectedProvider, setSelectedProvider] =
		useState<ProviderName>("claude");
	const [isExecuting, setIsExecuting] = useState(false);
	const [showConfirmModal, setShowConfirmModal] = useState(false);
	const [showDeleteModal, setShowDeleteModal] = useState(false);
	const [isDeleting, setIsDeleting] = useState(false);
	const [executeError, setExecuteError] = useState<string | null>(null);

	// SSE state
	const [sseUrl, setSseUrl] = useState<string | null>(null);
	const [remediationId, setRemediationId] = useState<string | null>(null);
	const [progressState, setProgressState] = useState<IRemediationProgressState>(
		{
			status: "queued",
			logs: [],
		},
	);

	// Results state
	const [result, setResult] = useState<RemediationResult | null>(null);

	// Legacy prompt generation state
	const [isGenerating, setIsGenerating] = useState(false);
	const [generateError, setGenerateError] = useState<string | null>(null);
	const [prompts, setPrompts] = useState<RemediationPromptsResponse | null>(
		null,
	);

	// Detect providers on mount
	useEffect(() => {
		if (providerDetection.status === "idle") {
			providerDetection.detectProviders();
		}
	}, [providerDetection]);

	// Restore existing remediation on mount / evaluationId change
	useEffect(() => {
		if (!evaluationId) return;

		let cancelled = false;

		async function loadExistingRemediation() {
			const data = await api.getRemediationForEvaluation(evaluationId!);
			if (cancelled || !data) return;

			if (data.status === "completed") {
				const restoredResult = toRemediationResult(data);
				if (restoredResult) {
					setRemediationId(data.id);
					setResult(restoredResult);
					setPhase("results");
				}
			} else if (data.status === "running" || data.status === "queued") {
				setRemediationId(data.id);
				setSseUrl(
					`${window.location.origin}/api/remediation/${data.id}/progress`,
				);
				setProgressState({
					status: data.status as "queued" | "running",
					logs: [],
				});
				setPhase("progress");
			}
			// "failed" or anything else: stay in config phase
		}

		loadExistingRemediation();

		return () => {
			cancelled = true;
		};
	}, [evaluationId, api]);

	// Available providers for the selector
	const availableProviders = useMemo(() => {
		const providers: ProviderStatus[] = [];
		for (const p of providerDetection.providers.values()) {
			if (p.available && p.name !== "random") {
				providers.push(p);
			}
		}
		return providers;
	}, [providerDetection.providers]);

	// Resolve selected issue keys to actual issues, split by type
	const { errorEntries, suggestionEntries } = useMemo(() => {
		const errors: { key: string; issue: Issue }[] = [];
		const suggestions: { key: string; issue: Issue }[] = [];

		for (const key of selectedIssueKeys) {
			const issue = issueKeyMap.get(key);
			if (!issue) continue;
			const type = getIssueType(issue);
			if (type === "error") {
				errors.push({ key, issue });
			} else {
				suggestions.push({ key, issue });
			}
		}

		errors.sort(
			(a, b) => getIssueSeverity(b.issue) - getIssueSeverity(a.issue),
		);
		const impactOrder: Record<string, number> = {
			High: 0,
			Medium: 1,
			Low: 2,
		};
		suggestions.sort(
			(a, b) =>
				(impactOrder[a.issue.impactLevel ?? "Low"] ?? 2) -
				(impactOrder[b.issue.impactLevel ?? "Low"] ?? 2),
		);

		return { errorEntries: errors, suggestionEntries: suggestions };
	}, [selectedIssueKeys, issueKeyMap]);

	const totalSelected = errorEntries.length + suggestionEntries.length;

	// SSE message handler
	const handleSSEMessage = useCallback(
		// biome-ignore lint/suspicious/noExplicitAny: SSE event data varies by type
		(event: any) => {
			const timestamp = new Date().toISOString();

			switch (event.type) {
				case "remediation.started":
					setProgressState((prev) => ({
						...prev,
						status: "running",
						logs: [...prev.logs, { timestamp, message: "Remediation started" }],
					}));
					break;

				case "remediation.progress": {
					const d = event.data ?? {};
					setProgressState((prev) => ({
						...prev,
						...(d.errorCount !== undefined && {
							errorCount: d.errorCount as number,
						}),
						...(d.suggestionCount !== undefined && {
							suggestionCount: d.suggestionCount as number,
						}),
						...(d.totalBatches !== undefined && {
							totalBatches: d.totalBatches as number,
						}),
						...(d.completedBatches !== undefined && {
							completedBatches: d.completedBatches as number,
						}),
						...(d.phase !== undefined && {
							currentPhase: d.phase as "errors" | "suggestions",
						}),
						...(d.runningTotalCostUsd !== undefined && {
							runningTotalCostUsd: d.runningTotalCostUsd as number,
						}),
						...(d.runningTotalDurationMs !== undefined && {
							runningTotalDurationMs: d.runningTotalDurationMs as number,
						}),
						...(d.runningTotalInputTokens !== undefined && {
							runningTotalInputTokens: d.runningTotalInputTokens as number,
						}),
						...(d.runningTotalOutputTokens !== undefined && {
							runningTotalOutputTokens: d.runningTotalOutputTokens as number,
						}),
					}));
					break;
				}

				case "remediation.step.started": {
					const bi = event.data?.batchInfo as
						| { batchNumber: number; totalBatches: number }
						| undefined;
					const batchLabel = bi
						? ` (batch ${bi.batchNumber}/${bi.totalBatches})`
						: "";
					const issueSummaries = event.data?.issuesSummary as
						| string[]
						| undefined;
					setProgressState((prev) => ({
						...prev,
						currentStep: event.data?.step,
						batchInfo: bi,
						...(issueSummaries && { currentBatchIssues: issueSummaries }),
						logs: [
							...prev.logs,
							{
								timestamp,
								message: `Step started: ${event.data?.step}${batchLabel}`,
							},
						],
					}));
					break;
				}

				case "remediation.step.completed": {
					const bi2 = event.data?.batchInfo as
						| { batchNumber: number; totalBatches: number }
						| undefined;
					const batchLabel2 = bi2
						? ` (batch ${bi2.batchNumber}/${bi2.totalBatches})`
						: "";
					const step = event.data?.step as string | undefined;
					const batchDurationMs = event.data?.batchDurationMs as
						| number
						| undefined;
					const batchCostUsd = event.data?.batchCostUsd as number | undefined;
					const batchInputTokens = event.data?.batchInputTokens as
						| number
						| undefined;
					const batchOutputTokens = event.data?.batchOutputTokens as
						| number
						| undefined;

					const hasBatchStats =
						batchDurationMs !== undefined && batchCostUsd !== undefined;
					let logMessage = `Step completed: ${step}${batchLabel2}`;
					if (hasBatchStats) {
						const phaseName =
							step === "executing_error_fix"
								? "Error fix"
								: "Suggestion enrich";
						const label = bi2
							? `${phaseName} batch ${bi2.batchNumber}/${bi2.totalBatches}`
							: phaseName;
						logMessage = `${label} completed: ${Math.round(batchDurationMs / 1000)}s, $${batchCostUsd.toFixed(4)}`;
					}

					setProgressState((prev) => ({
						...prev,
						batchInfo: undefined,
						currentBatchIssues: undefined,
						...(hasBatchStats && {
							lastBatchStats: {
								durationMs: batchDurationMs!,
								costUsd: batchCostUsd!,
								inputTokens: batchInputTokens ?? 0,
								outputTokens: batchOutputTokens ?? 0,
							},
						}),
						logs: [...prev.logs, { timestamp, message: logMessage }],
					}));
					break;
				}

				case "remediation.completed": {
					const remResult = event.data?.result as RemediationResult | undefined;
					setProgressState((prev) => ({
						...prev,
						status: "completed",
						result: remResult,
						logs: [
							...prev.logs,
							{ timestamp, message: "Remediation completed" },
						],
					}));
					if (remResult) {
						setResult(remResult);
					}
					setPhase("results");
					setSseUrl(null);
					break;
				}

				case "remediation.failed":
					setProgressState((prev) => ({
						...prev,
						status: "failed",
						error: event.data?.error,
						logs: [
							...prev.logs,
							{
								timestamp,
								message: `Failed: ${event.data?.error?.message || "Unknown error"}`,
							},
						],
					}));
					setExecuteError(event.data?.error?.message || "Remediation failed");
					setPhase("config");
					setSseUrl(null);
					break;
			}
		},
		[],
	);

	useSSE({
		url: sseUrl,
		onMessage: handleSSEMessage,
	});

	// Execute remediation
	const handleExecute = useCallback(async () => {
		if (!evaluationId || totalSelected === 0) return;

		setIsExecuting(true);
		setExecuteError(null);
		setPrompts(null);

		try {
			const issues = [
				...errorEntries.map((e) => e.issue),
				...suggestionEntries.map((e) => e.issue),
			];

			const response = await api.executeRemediation(
				evaluationId,
				issues,
				targetFileType,
				selectedProvider,
			);

			setRemediationId(response.remediationId);
			setSseUrl(response.sseUrl);
			setProgressState({ status: "queued", logs: [] });
			setPhase("progress");
		} catch (err) {
			setExecuteError(
				err instanceof Error ? err.message : "Failed to start remediation",
			);
		} finally {
			setIsExecuting(false);
		}
	}, [
		evaluationId,
		totalSelected,
		errorEntries,
		suggestionEntries,
		targetFileType,
		selectedProvider,
		api,
	]);

	// Generate prompts (legacy copy-paste mode)
	const handleGeneratePrompts = useCallback(async () => {
		if (!evaluationId || totalSelected === 0) return;

		setIsGenerating(true);
		setGenerateError(null);

		try {
			const issues = [
				...errorEntries.map((e) => e.issue),
				...suggestionEntries.map((e) => e.issue),
			];
			const result = await api.generateRemediationPrompts(
				evaluationId,
				issues,
				targetFileType,
			);
			setPrompts(result);
		} catch (err) {
			setGenerateError(
				err instanceof Error ? err.message : "Failed to generate prompts",
			);
		} finally {
			setIsGenerating(false);
		}
	}, [
		evaluationId,
		totalSelected,
		errorEntries,
		suggestionEntries,
		targetFileType,
		api,
	]);

	const handleDeleteRemediation = useCallback(async () => {
		if (!remediationId) return;
		setIsDeleting(true);
		try {
			await api.deleteRemediation(remediationId);
			setPhase("config");
			setResult(null);
			setRemediationId(null);
			setSseUrl(null);
			setProgressState({ status: "queued", logs: [] });
			setShowDeleteModal(false);
		} catch (err) {
			setExecuteError(
				err instanceof Error ? err.message : "Failed to delete remediation",
			);
		} finally {
			setIsDeleting(false);
		}
	}, [remediationId, api]);

	if (!evaluationData) {
		return (
			<div className="card text-center py-12">
				<p className="text-body-muted">
					No evaluation data. Run an evaluation first to generate remediation
					prompts.
				</p>
			</div>
		);
	}

	if (totalSelected === 0 && phase === "config") {
		return (
			<div className="space-y-6">
				<div className="card">
					<h2 className="text-heading text-slate-100">Remediation</h2>
					<p className="text-body-muted mt-1">
						Execute remediation to fix issues automatically
					</p>
				</div>
				<div className="card text-center py-12">
					<svg
						className="w-12 h-12 text-slate-600 mx-auto mb-4"
						fill="none"
						stroke="currentColor"
						viewBox="0 0 24 24"
					>
						<path
							strokeLinecap="round"
							strokeLinejoin="round"
							strokeWidth={1.5}
							d="M12 4v16m8-8H4"
						/>
					</svg>
					<p className="text-body-muted">
						No issues selected. Use the{" "}
						<span className="inline-flex items-center justify-center w-5 h-5 rounded bg-slate-700 text-slate-300 text-xs align-middle mx-0.5">
							+
						</span>{" "}
						button on issue cards in the Errors and Suggestions tabs.
					</p>
				</div>
			</div>
		);
	}

	//  PROGRESS PHASE 
	if (phase === "progress") {
		return (
			<div className="space-y-6">
				<div className="card">
					<h2 className="text-heading text-slate-100">Executing Remediation</h2>
					<p className="text-body-muted mt-1">
						Running {selectedProvider} agent to fix {totalSelected} issue
						{totalSelected !== 1 ? "s" : ""}...
					</p>
				</div>
				<RemediationProgress
					currentStep={progressState.currentStep}
					batchInfo={progressState.batchInfo}
					logs={progressState.logs}
					errorCount={progressState.errorCount}
					suggestionCount={progressState.suggestionCount}
					totalBatches={progressState.totalBatches}
					completedBatches={progressState.completedBatches}
					currentPhase={progressState.currentPhase}
					runningTotalCostUsd={progressState.runningTotalCostUsd}
					runningTotalDurationMs={progressState.runningTotalDurationMs}
					runningTotalInputTokens={progressState.runningTotalInputTokens}
					runningTotalOutputTokens={progressState.runningTotalOutputTokens}
					currentBatchIssues={progressState.currentBatchIssues}
				/>
			</div>
		);
	}

	//  RESULTS PHASE 
	if (phase === "results" && result) {
		const hasPerPromptStats =
			result.errorFixStats || result.suggestionEnrichStats;

		return (
			<div className="space-y-6">
				<div className="card">
					<div className="flex items-center justify-between">
						<div>
							<h2 className="text-heading text-slate-100">
								Remediation Complete
							</h2>
							<p className="text-body-muted mt-1">
								{result.filesChanged} file
								{result.filesChanged !== 1 ? "s" : ""} changed
								<span className="text-green-400 ml-2">
									+{result.totalAdditions}
								</span>
								<span className="text-red-400 ml-1">
									-{result.totalDeletions}
								</span>
								{result.totalDurationMs > 0 && (
									<span className="text-slate-500 ml-2">
										in {Math.round(result.totalDurationMs / 1000)}s
									</span>
								)}
								{result.totalCostUsd > 0 && (
									<span className="text-slate-500 ml-2">
										(${result.totalCostUsd.toFixed(4)})
									</span>
								)}
							</p>
							{(result.totalInputTokens > 0 ||
								result.totalOutputTokens > 0) && (
								<p className="text-caption mt-1">
									{result.totalInputTokens.toLocaleString()} input /{" "}
									{result.totalOutputTokens.toLocaleString()} output tokens
									{hasPerPromptStats && (
										<span className="text-slate-500">
											{" "}
											&mdash;{" "}
											{result.errorFixStats && (
												<span>
													Error fix: $
													{(result.errorFixStats.costUsd ?? 0).toFixed(4)}
													{result.suggestionEnrichStats && " | "}
												</span>
											)}
											{result.suggestionEnrichStats && (
												<span>
													Enrich: $
													{(result.suggestionEnrichStats.costUsd ?? 0).toFixed(
														4,
													)}
												</span>
											)}
										</span>
									)}
								</p>
							)}
						</div>
						<div className="flex items-center gap-2">
							{remediationId && <PatchDownload remediationId={remediationId} />}
							{!cloudMode && (
								<button
									onClick={() => setShowDeleteModal(true)}
									disabled={isDeleting}
									className="btn-secondary text-red-400 hover:text-red-300"
								>
									{isDeleting ? "Deleting..." : "Delete & Start Over"}
								</button>
							)}
						</div>
					</div>
				</div>

				{result.summary?.parsed && (
					<ActionSummarySection summary={result.summary} />
				)}

				{result.fileChanges.length === 0 ? (
					<div className="card text-center py-8">
						<p className="text-body-muted">
							No file changes were made by the agent.
						</p>
					</div>
				) : (
					<div className="space-y-3">
						{result.fileChanges.map((file: FileChange) => {
							const fileSummaries = getFileSummaries(file.path, result);
							return (
								<FileChangeCard
									key={file.path}
									file={file}
									defaultExpanded={result.fileChanges.length <= 3}
									summaries={fileSummaries}
								/>
							);
						})}
					</div>
				)}

				{executeError && <p className="text-sm text-red-400">{executeError}</p>}

				{/* Delete Confirmation Modal */}
				<Modal
					isOpen={showDeleteModal}
					onClose={() => setShowDeleteModal(false)}
					title="Delete Remediation"
					maxWidth="max-w-md"
				>
					<div className="space-y-4">
						<p className="text-sm text-slate-300">
							This will permanently delete the remediation result for this
							evaluation. You can then re-run remediation with a fresh git
							state.
						</p>
						<div className="flex justify-end gap-3 pt-2">
							<button
								onClick={() => setShowDeleteModal(false)}
								className="btn-secondary"
								disabled={isDeleting}
							>
								Cancel
							</button>
							<button
								onClick={handleDeleteRemediation}
								disabled={isDeleting}
								className="btn-primary bg-red-600 hover:bg-red-500"
							>
								{isDeleting ? "Deleting..." : "Delete"}
							</button>
						</div>
					</div>
				</Modal>
			</div>
		);
	}

	//  CONFIG PHASE 
	return (
		<div className="space-y-6">
			<div className="card">
				<h2 className="text-heading text-slate-100">Remediation</h2>
				<p className="text-body-muted mt-1">
					Execute remediation to fix issues automatically, or generate
					copy-paste prompts
				</p>
			</div>

			<div className="card space-y-5">
				{/* Target File Type */}
				<div>
					<label className="text-label text-slate-300 block mb-2">
						Target file type
					</label>
					<div className="flex gap-2">
						<button
							onClick={() => setTargetFileType("AGENTS.md")}
							className={`px-3 py-1.5 rounded text-sm transition-colors ${
								targetFileType === "AGENTS.md"
									? "bg-blue-600 text-white"
									: "bg-slate-700 text-slate-300 hover:bg-slate-600"
							}`}
						>
							AGENTS.md
						</button>
						<button
							onClick={() => setTargetFileType("CLAUDE.md")}
							className={`px-3 py-1.5 rounded text-sm transition-colors ${
								targetFileType === "CLAUDE.md"
									? "bg-blue-600 text-white"
									: "bg-slate-700 text-slate-300 hover:bg-slate-600"
							}`}
						>
							CLAUDE.md
						</button>
					</div>
				</div>

				{/* Provider Selector */}
				<div>
					<label className="text-label text-slate-300 block mb-2">
						AI Provider
					</label>
					{providerDetection.status === "detecting" ? (
						<div className="text-sm text-slate-400">Detecting providers...</div>
					) : availableProviders.length > 0 ? (
						<div className="flex flex-wrap gap-2">
							{availableProviders.map((p) => (
								<button
									key={p.name}
									onClick={() => setSelectedProvider(p.name)}
									className={`px-3 py-1.5 rounded text-sm transition-colors ${
										selectedProvider === p.name
											? "bg-blue-600 text-white"
											: "bg-slate-700 text-slate-300 hover:bg-slate-600"
									}`}
								>
									{p.displayName}
								</button>
							))}
						</div>
					) : (
						<div className="text-sm text-slate-400">
							No providers detected. Defaulting to Claude.
						</div>
					)}
				</div>

				{/* Selected Issues */}
				<div className="space-y-4">
					{errorEntries.length > 0 && (
						<IssueSection
							title="Errors"
							count={errorEntries.length}
							entries={errorEntries}
							onRemove={onRemoveIssue}
						/>
					)}
					{suggestionEntries.length > 0 && (
						<IssueSection
							title="Suggestions"
							count={suggestionEntries.length}
							entries={suggestionEntries}
							onRemove={onRemoveIssue}
						/>
					)}
					<div className="flex items-center justify-between">
						<span className="text-caption">
							{totalSelected} issue{totalSelected !== 1 ? "s" : ""} selected
						</span>
						<button
							onClick={onClearAll}
							className="text-sm text-slate-400 hover:text-slate-200 transition-colors"
						>
							Clear all
						</button>
					</div>
				</div>

				{/* Action Buttons */}
				<div className="flex flex-wrap gap-3">
					<button
						onClick={() => setShowConfirmModal(true)}
						disabled={isExecuting || isGenerating}
						className="btn-primary"
					>
						{isExecuting ? (
							<span className="flex items-center gap-2">
								<svg
									className="animate-spin h-4 w-4"
									fill="none"
									viewBox="0 0 24 24"
								>
									<circle
										className="opacity-25"
										cx="12"
										cy="12"
										r="10"
										stroke="currentColor"
										strokeWidth="4"
									/>
									<path
										className="opacity-75"
										fill="currentColor"
										d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
									/>
								</svg>
								Starting...
							</span>
						) : (
							`Execute Remediation (${totalSelected})`
						)}
					</button>
					<button
						onClick={handleGeneratePrompts}
						disabled={isExecuting || isGenerating}
						className="btn-secondary"
					>
						{isGenerating ? "Generating..." : "Generate Prompts"}
					</button>
				</div>

				{executeError && (
					<p className="text-sm text-red-400 mt-2">{executeError}</p>
				)}
				{generateError && (
					<p className="text-sm text-red-400 mt-2">{generateError}</p>
				)}
			</div>

			{/* Confirmation Modal */}
			<Modal
				isOpen={showConfirmModal}
				onClose={() => setShowConfirmModal(false)}
				title="Confirm Remediation"
				maxWidth="max-w-md"
			>
				<div className="space-y-4">
					<p className="text-sm text-slate-300">
						This will run the AI provider to remediate the selected issues.
						Please confirm the details below.
					</p>
					<div className="bg-slate-800/50 rounded-lg border border-slate-700/50 p-3 space-y-2 text-sm">
						{errorEntries.length > 0 && (
							<div className="flex justify-between text-slate-300">
								<span>Errors</span>
								<span className="font-semibold">{errorEntries.length}</span>
							</div>
						)}
						{suggestionEntries.length > 0 && (
							<div className="flex justify-between text-slate-300">
								<span>Suggestions</span>
								<span className="font-semibold">
									{suggestionEntries.length}
								</span>
							</div>
						)}
						<div className="flex justify-between text-slate-300">
							<span>Target file</span>
							<span className="font-semibold">{targetFileType}</span>
						</div>
						<div className="flex justify-between text-slate-300">
							<span>Provider</span>
							<span className="font-semibold">{selectedProvider}</span>
						</div>
					</div>
					<div className="flex justify-end gap-3 pt-2">
						<button
							onClick={() => setShowConfirmModal(false)}
							className="btn-secondary"
						>
							Cancel
						</button>
						<button
							onClick={() => {
								setShowConfirmModal(false);
								handleExecute();
							}}
							className="btn-primary"
						>
							Confirm
						</button>
					</div>
				</div>
			</Modal>

			{/* Generated Prompts (legacy mode) */}
			{prompts && (
				<div className="space-y-6">
					{prompts.errorFixPrompt && (
						<PromptDisplay
							title={`Fix Errors Prompt (${prompts.errorCount} issues)`}
							content={prompts.errorFixPrompt}
						/>
					)}
					{prompts.suggestionEnrichPrompt && (
						<PromptDisplay
							title={`Enrich Suggestions Prompt (${prompts.suggestionCount} issues)`}
							content={prompts.suggestionEnrichPrompt}
						/>
					)}
				</div>
			)}
		</div>
	);
}

function IssueSection({
	title,
	count,
	entries,
	onRemove,
}: {
	title: string;
	count: number;
	entries: { key: string; issue: Issue }[];
	onRemove: (key: string) => void;
}) {
	return (
		<div className="bg-slate-800/50 rounded-lg border border-slate-700/50 p-3">
			<div className="flex items-center justify-between mb-2">
				<span className="text-sm font-semibold text-slate-200">
					{title} ({count})
				</span>
			</div>
			<div className="space-y-1 max-h-48 overflow-y-auto custom-scrollbar">
				{entries.map(({ key, issue }) => {
					const numericSeverity = getIssueSeverity(issue);
					const severityLevel = getSeverityLevel(numericSeverity);
					const issueTitle =
						issue.title || issue.problem || issue.description || issue.category;

					return (
						<div
							key={key}
							className="flex items-center gap-2 py-1 px-1 rounded hover:bg-slate-700/30 group"
						>
							<div
								className={`severity-dot severity-dot-${severityLevel} flex-shrink-0`}
							/>
							<div className="flex-shrink-0 w-[72px]">
								{issue.issueType === "error" ? (
									<span className={getSeverityColor(issue.severity)}>
										{severityLevel.charAt(0).toUpperCase() +
											severityLevel.slice(1)}
									</span>
								) : (
									<span className={getImpactBadgeClass(issue.impactLevel)}>
										{getImpactLabel(issue.impactLevel)}
									</span>
								)}
							</div>
							<span className="flex-1 min-w-0 text-sm text-slate-300 truncate">
								{issueTitle}
							</span>
							{issue.evaluatorName && (
								<span className="flex-shrink-0 text-xs text-slate-500 hidden sm:block">
									{issue.evaluatorName}
								</span>
							)}
							<button
								onClick={() => onRemove(key)}
								className="flex-shrink-0 text-slate-500 hover:text-red-400 transition-colors opacity-0 group-hover:opacity-100"
								title="Remove from remediation"
							>
								<svg
									className="h-4 w-4"
									fill="none"
									viewBox="0 0 24 24"
									stroke="currentColor"
								>
									<path
										strokeLinecap="round"
										strokeLinejoin="round"
										strokeWidth={2}
										d="M6 18L18 6M6 6l12 12"
									/>
								</svg>
							</button>
						</div>
					);
				})}
			</div>
		</div>
	);
}

function PromptDisplay({ title, content }: { title: string; content: string }) {
	return (
		<div className="card">
			<div className="flex items-center justify-between mb-3">
				<h3 className="text-subheading text-slate-200">{title}</h3>
				<CopyButton text={content} variant="text" />
			</div>
			<div className="bg-slate-900/50 rounded-lg p-4 border border-slate-700/50 font-mono text-xs text-slate-300 max-h-96 overflow-y-auto custom-scrollbar whitespace-pre-wrap">
				{content}
			</div>
		</div>
	);
}

function ActionSummarySection({
	summary,
}: {
	summary: NonNullable<RemediationResult["summary"]>;
}) {
	const allActions = [
		...summary.errorFixActions,
		...summary.suggestionEnrichActions,
	];

	return (
		<div className="card">
			<div className="flex items-center justify-between mb-3">
				<h3 className="text-subheading text-slate-200">Action Summary</h3>
				<span className="text-caption">
					{summary.addressedCount} addressed
					{summary.skippedCount > 0 && (
						<span className="text-slate-500">
							, {summary.skippedCount} skipped
						</span>
					)}
				</span>
			</div>
			<div className="space-y-1">
				{allActions.map((action) => (
					<div
						key={`${action.issueIndex}-${action.status}`}
						className="flex items-start gap-2 py-1 px-1"
					>
						{action.status === "skipped" ? (
							<span className="text-slate-500 flex-shrink-0 mt-0.5">
								<svg
									className="w-4 h-4"
									fill="none"
									viewBox="0 0 24 24"
									stroke="currentColor"
								>
									<path
										strokeLinecap="round"
										strokeLinejoin="round"
										strokeWidth={2}
										d="M20 12H4"
									/>
								</svg>
							</span>
						) : (
							<span className="text-green-400 flex-shrink-0 mt-0.5">
								<svg
									className="w-4 h-4"
									fill="none"
									viewBox="0 0 24 24"
									stroke="currentColor"
								>
									<path
										strokeLinecap="round"
										strokeLinejoin="round"
										strokeWidth={2}
										d="M5 13l4 4L19 7"
									/>
								</svg>
							</span>
						)}
						<span className="text-sm text-slate-300 flex-1">
							{action.issueTitle ? (
								<span className="text-slate-400 text-xs mr-1.5">
									{action.issueTitle}
								</span>
							) : (
								<span className="text-slate-400 font-mono text-xs mr-1.5">
									#{action.issueIndex}
								</span>
							)}
							{action.summary}
						</span>
						{action.file && (
							<span className="text-xs text-slate-500 font-mono flex-shrink-0">
								{action.file}
							</span>
						)}
					</div>
				))}
			</div>
		</div>
	);
}

/**
 * Get action summaries relevant to a specific file path.
 */
function getFileSummaries(
	filePath: string,
	result: RemediationResult,
): RemediationAction[] {
	if (!result.summary?.parsed) return [];

	const allActions = [
		...result.summary.errorFixActions,
		...result.summary.suggestionEnrichActions,
	];

	return allActions.filter(
		(a) => a.file && a.status !== "skipped" && filePath.endsWith(a.file),
	);
}

