import React from "react";

interface SelectionSummaryBarProps {
	selectedCount: number;
	onClearSelection: () => void;
	onReviewSelected: () => void;
}

export const SelectionSummaryBar: React.FC<SelectionSummaryBarProps> = ({
	selectedCount,
	onClearSelection,
	onReviewSelected,
