   1→@tailwind base;
   2→@tailwind components;
   3→@tailwind utilities;
   4→
   5→/* ============================================
   6→   CENTRALIZED COLOR PALETTE
   7→   ============================================
   8→   Keep colors minimal and purposeful:
   9→   - Primary: Purple for interactive elements
  10→   - Semantic: Red/Orange/Yellow for severity only
  11→   - Neutral: Purple-tinted for everything else
  12→   ============================================ */
  13→
  14→:root {
  15→	/* Primary accent - used for buttons, links, focus states */
  16→	--color-primary: 99, 102, 241; /* indigo-500 */
  17→	--color-primary-hover: 79, 70, 229; /* indigo-600 */
  18→
  19→	/* Severity colors - used only for severity indicators (3-level system) */
  20→	--color-severity-high: 249, 115, 22; /* orange-500 */
  21→	--color-severity-medium: 234, 179, 8; /* yellow-500 */
  22→	--color-severity-low: 148, 163, 184; /* slate-400 */
  23→
  24→	/* Semantic section backgrounds - unified muted style */
  25→	--color-section-bg: 51, 65, 85; /* slate-700 */
  26→	--color-section-border: 71, 85, 105; /* slate-600 */
  27→	--color-section-text: 203, 213, 225; /* slate-300 */
  28→
  29→	/* Code highlight */
  30→	--color-highlight-bg: 234, 179, 8; /* yellow-500 for code highlights */
  31→
  32→	/* ============================================
  33→     TYPOGRAPHY SCALE
  34→     ============================================
  35→     Using Tailwind's default scale:
  36→     - 2xs:  0.6875rem (11px)
  37→     - xs:   0.75rem (12px)
  38→     - sm:   0.875rem (14px) - issue descriptions
  39→     - base: 1rem (16px)
  40→     - lg:   1.125rem (18px)
  41→     - xl:   1.25rem (20px)
  42→     - 2xl:  1.5rem (24px)
  43→     - 3xl:  1.875rem (30px)
  44→     ============================================ */
  45→	--font-size-2xs: 0.6875rem;
  46→	--font-size-xs: 0.75rem;
  47→	--font-size-sm: 0.875rem;
  48→	--font-size-base: 1rem;
  49→	--font-size-lg: 1.125rem;
  50→	--font-size-xl: 1.25rem;
  51→	--font-size-2xl: 1.5rem;
  52→	--font-size-3xl: 1.875rem;
  53→
  54→	/* Animation timing */
  55→	--transition-fast: 150ms;
  56→	--transition-base: 200ms;
  57→	--transition-slow: 300ms;
  58→
  59→	/* Elevation system */
  60→	--shadow-xs: 0 1px 2px rgba(0, 0, 0, 0.2);
  61→	--shadow-sm: 0 2px 4px rgba(0, 0, 0, 0.25);
  62→	--shadow-md: 0 4px 8px -2px rgba(0, 0, 0, 0.3);
  63→	--shadow-lg: 0 8px 16px -4px rgba(0, 0, 0, 0.35);
  64→
  65→	/* Semantic action colors */
  66→	--color-success: 34, 197, 94; /* green-500 */
  67→	--color-bookmark: 234, 179, 8; /* yellow-500 */
  68→	--color-error: 239, 68, 68; /* red-500 */
  69→}
  70→
  71→/* ============================================
  72→   KEYFRAME ANIMATIONS
  73→   ============================================ */
  74→
  75→/* Shimmer effect for loading skeletons */
  76→@keyframes shimmer {
  77→	0% {
  78→		background-position: -200% 0;
  79→	}
  80→	100% {
  81→		background-position: 200% 0;
  82→	}
  83→}
  84→
  85→/* Subtle floating animation for hero icons */
  86→@keyframes float {
  87→	0%,
  88→	100% {
  89→		transform: translateY(0);
  90→	}
  91→	50% {
  92→		transform: translateY(-6px);
  93→	}
  94→}
  95→
  96→/* Dropdown reveal animation */
  97→@keyframes slideDown {
  98→	from {
  99→		opacity: 0;
 100→		transform: translateY(-8px);
 101→	}
 102→	to {
 103→		opacity: 1;
 104→		transform: translateY(0);
 105→	}
 106→}
 107→
 108→/* Slide up animation */
 109→@keyframes slideUp {
 110→	from {
 111→		opacity: 0;
 112→		transform: translateY(8px);
 113→	}
 114→	to {
 115→		opacity: 1;
 116→		transform: translateY(0);
 117→	}
 118→}
 119→
 120→/* Bounce in for badges and success states */
 121→@keyframes bounceIn {
 122→	0% {
 123→		opacity: 0;
 124→		transform: scale(0.3);
 125→	}
 126→	50% {
 127→		transform: scale(1.05);
 128→	}
 129→	70% {
 130→		transform: scale(0.9);
 131→	}
 132→	100% {
 133→		opacity: 1;
 134→		transform: scale(1);
 135→	}
 136→}
 137→
 138→/* Animated gradient text */
 139→@keyframes gradientShift {
 140→	0%,
 141→	100% {
 142→		background-position: 0% 50%;
 143→	}
 144→	50% {
 145→		background-position: 100% 50%;
 146→	}
 147→}
 148→
 149→/* AI scanning line effect */
 150→@keyframes scanLine {
 151→	0% {
 152→		transform: translateY(-100%);
 153→		opacity: 0;
 154→	}
 155→	10% {
 156→		opacity: 1;
 157→	}
 158→	90% {
 159→		opacity: 1;
 160→	}
 161→	100% {
 162→		transform: translateY(100%);
 163→		opacity: 0;
 164→	}
 165→}
 166→
 167→/* Pulse glow effect */
 168→@keyframes pulseGlow {
 169→	0%,
 170→	100% {
 171→		box-shadow: 0 0 5px rgba(168, 85, 247, 0.3);
 172→	}
 173→	50% {
 174→		box-shadow: 0 0 20px rgba(168, 85, 247, 0.6);
 175→	}
 176→}
 177→
 178→/* Count up number animation helper */
 179→@keyframes countUp {
 180→	from {
