					rawOutputType as NonNullable<
						IRemediationAction["outputType"]
					>,
				)
					? (rawOutputType as NonNullable<
							IRemediationAction["outputType"]
						>)
					: undefined;

				return {
					issueIndex: a.issueIndex as number,
					status,
					file: typeof a.file === "string" ? a.file : undefined,
					summary,
