     1→import { useCallback, useEffect, useMemo, useRef, useState } from "react";
     2→import type {
     3→	ProviderName,
     4→	RemediationPromptsResponse,
     5→	TargetAgent,
     6→} from "../hooks/useEvaluationApi";
     7→import { useEvaluationApi } from "../hooks/useEvaluationApi";
     8→import {
     9→	type ProviderStatus,
    10→	useProviderDetection,
    11→} from "../hooks/useProviderDetection";
    12→import { useSSE } from "../hooks/useSSE";
    13→import type { EvaluationOutput, Issue } from "../types/evaluation";
    14→import {
    15→	getImpactBadgeClass,
    16→	getImpactLabel,
    17→	getIssueSeverity,
    18→	getIssueType,
    19→	getSeverityColor,
    20→	getSeverityLevel,
    21→} from "../types/evaluation";
    22→import type {
    23→	IRemediationProgressState,
    24→	RemediationHistoryItem,
    25→	RemediationResult,
    26→} from "../types/remediation";
    27→import { RemediationHistory } from "./RemediationHistory";
    28→import { RemediationProgress } from "./RemediationProgress";
    29→import { CopyButton } from "./shared/CopyButton";
    30→import { Modal } from "./shared/Modal";
    31→
    32→type Phase = "idle" | "progress";
    33→
    34→interface RemediateTabProps {
    35→	evaluationId: string | null;
    36→	evaluationData: EvaluationOutput | null;
    37→	selectedIssueKeys: Set<string>;
    38→	issueKeyMap: Map<string, Issue>;
    39→	onRemoveIssue: (key: string) => void;
    40→	onClearAll: () => void;
    41→	cloudMode?: boolean;
    42→}
    43→
    44→export function RemediateTab({
    45→	evaluationId,
    46→	evaluationData,
    47→	selectedIssueKeys,
    48→	issueKeyMap,
    49→	onRemoveIssue,
    50→	onClearAll,
    51→	cloudMode = false,
    52→}: RemediateTabProps) {
    53→	const api = useEvaluationApi();
    54→	const providerDetection = useProviderDetection();
    55→
    56→	// Phase: idle (config visible) or progress (SSE running)
    57→	const [phase, setPhase] = useState<Phase>("idle");
    58→	const [targetAgent, setTargetAgent] = useState<TargetAgent>("agents-md");
    59→	const [selectedProvider, setSelectedProvider] =
    60→		useState<ProviderName>("claude");
    61→	const [isExecuting, setIsExecuting] = useState(false);
    62→	const [showConfirmModal, setShowConfirmModal] = useState(false);
    63→	const [executeError, setExecuteError] = useState<string | null>(null);
    64→
    65→	// History state
    66→	const [remediations, setRemediations] = useState<RemediationHistoryItem[]>(
    67→		[],
    68→	);
    69→
    70→	// SSE state
    71→	const [sseUrl, setSseUrl] = useState<string | null>(null);
    72→	const [, setActiveRemediationId] = useState<string | null>(null);
    73→	const activeRemediationIdRef = useRef<string | null>(null);
    74→	const [autoExpandRemediationId, setAutoExpandRemediationId] = useState<
    75→		string | null
    76→	>(null);
    77→	const [progressState, setProgressState] = useState<IRemediationProgressState>(
    78→		{
    79→			status: "queued",
    80→			logs: [],
    81→		},
    82→	);
    83→
    84→	// Legacy prompt generation state
    85→	const [isGenerating, setIsGenerating] = useState(false);
    86→	const [generateError, setGenerateError] = useState<string | null>(null);
    87→	const [prompts, setPrompts] = useState<RemediationPromptsResponse | null>(
    88→		null,
    89→	);
    90→
    91→	// Detect providers on mount
    92→	useEffect(() => {
    93→		if (providerDetection.status === "idle") {
    94→			providerDetection.detectProviders();
    95→		}
    96→	}, [providerDetection]);
    97→
    98→	// Load remediation history on mount / evaluationId change
    99→	const refreshHistory = useCallback(async () => {
   100→		if (!evaluationId) return;
   101→		const data = await api.getRemediationsForEvaluation(evaluationId);
   102→		setRemediations(data.remediations);
   103→		return data;
   104→	}, [evaluationId, api]);
   105→
   106→	useEffect(() => {
   107→		if (!evaluationId) return;
   108→
   109→		let cancelled = false;
   110→
   111→		async function loadRemediations() {
   112→			const data = await api.getRemediationsForEvaluation(evaluationId!);
   113→			if (cancelled) return;
   114→
   115→			setRemediations(data.remediations);
   116→
   117→			// If there's an active job, connect SSE
   118→			if (data.activeJob) {
   119→				activeRemediationIdRef.current = data.activeJob.id;
   120→				setActiveRemediationId(data.activeJob.id);
   121→				setSseUrl(
   122→					`${window.location.origin}/api/remediation/${data.activeJob.id}/progress`,
   123→				);
   124→				setProgressState({
   125→					status: data.activeJob.status as "queued" | "running",
   126→					logs: [],
   127→				});
   128→				setPhase("progress");
   129→			}
   130→		}
   131→
   132→		loadRemediations();
   133→
   134→		return () => {
   135→			cancelled = true;
   136→		};
   137→	}, [evaluationId, api]);
   138→
   139→	// Available providers for the selector
   140→	const availableProviders = useMemo(() => {
   141→		const providers: ProviderStatus[] = [];
   142→		for (const p of providerDetection.providers.values()) {
   143→			if (p.available && p.name !== "random") {
   144→				providers.push(p);
   145→			}
   146→		}
   147→		return providers;
   148→	}, [providerDetection.providers]);
   149→
   150→	// Resolve selected issue keys to actual issues, split by type
   151→	const { errorEntries, suggestionEntries } = useMemo(() => {
   152→		const errors: { key: string; issue: Issue }[] = [];
   153→		const suggestions: { key: string; issue: Issue }[] = [];
   154→
   155→		for (const key of selectedIssueKeys) {
   156→			const issue = issueKeyMap.get(key);
   157→			if (!issue) continue;
   158→			const type = getIssueType(issue);
   159→			if (type === "error") {
   160→				errors.push({ key, issue });
   161→			} else {
   162→				suggestions.push({ key, issue });
   163→			}
   164→		}
   165→
   166→		errors.sort(
   167→			(a, b) => getIssueSeverity(b.issue) - getIssueSeverity(a.issue),
   168→		);
   169→		const impactOrder: Record<string, number> = {
   170→			High: 0,
   171→			Medium: 1,
   172→			Low: 2,
   173→		};
   174→		suggestions.sort(
   175→			(a, b) =>
   176→				(impactOrder[a.issue.impactLevel ?? "Low"] ?? 2) -
   177→				(impactOrder[b.issue.impactLevel ?? "Low"] ?? 2),
   178→		);
   179→
   180→		return { errorEntries: errors, suggestionEntries: suggestions };
   181→	}, [selectedIssueKeys, issueKeyMap]);
   182→
   183→	const totalSelected = errorEntries.length + suggestionEntries.length;
   184→
   185→	// SSE message handler
   186→	const handleSSEMessage = useCallback(
   187→		// biome-ignore lint/suspicious/noExplicitAny: SSE event data varies by type
   188→		(event: any) => {
   189→			const timestamp = new Date().toISOString();
   190→
   191→			switch (event.type) {
   192→				case "remediation.started":
   193→					setProgressState((prev) => ({
   194→						...prev,
   195→						status: "running",
   196→						logs: [...prev.logs, { timestamp, message: "Remediation started" }],
   197→					}));
   198→					break;
   199→
   200→				case "remediation.progress": {
   201→					const d = event.data ?? {};
   202→					setProgressState((prev) => ({
   203→						...prev,
   204→						...(d.errorCount !== undefined && {
   205→							errorCount: d.errorCount as number,
   206→						}),
   207→						...(d.suggestionCount !== undefined && {
   208→							suggestionCount: d.suggestionCount as number,
   209→						}),
   210→						...(d.totalBatches !== undefined && {
   211→							totalBatches: d.totalBatches as number,
   212→						}),
   213→						...(d.completedBatches !== undefined && {
   214→							completedBatches: d.completedBatches as number,
   215→						}),
   216→						...(d.phase !== undefined && {
   217→							currentPhase: d.phase as "errors" | "suggestions",
   218→						}),
   219→						...(d.runningTotalCostUsd !== undefined && {
   220→							runningTotalCostUsd: d.runningTotalCostUsd as number,
   221→						}),
   222→						...(d.runningTotalDurationMs !== undefined && {
   223→							runningTotalDurationMs: d.runningTotalDurationMs as number,
   224→						}),
   225→						...(d.runningTotalInputTokens !== undefined && {
   226→							runningTotalInputTokens: d.runningTotalInputTokens as number,
   227→						}),
   228→						...(d.runningTotalOutputTokens !== undefined && {
   229→							runningTotalOutputTokens: d.runningTotalOutputTokens as number,
   230→						}),
   231→					}));
   232→					break;
   233→				}
   234→
   235→				case "remediation.step.started": {
   236→					const bi = event.data?.batchInfo as
   237→						| { batchNumber: number; totalBatches: number }
   238→						| undefined;
   239→					const batchLabel = bi
   240→						? ` (batch ${bi.batchNumber}/${bi.totalBatches})`
   241→						: "";
   242→					const issueSummaries = event.data?.issuesSummary as
   243→						| string[]
   244→						| undefined;
   245→					setProgressState((prev) => ({
   246→						...prev,
   247→						currentStep: event.data?.step,
   248→						batchInfo: bi,
   249→						...(issueSummaries && {
   250→							currentBatchIssues: issueSummaries,
   251→						}),
   252→						logs: [
   253→							...prev.logs,
   254→							{
   255→								timestamp,
   256→								message: `Step started: ${event.data?.step}${batchLabel}`,
   257→							},
   258→						],
   259→					}));
   260→					break;
   261→				}
   262→
   263→				case "remediation.step.completed": {
   264→					const bi2 = event.data?.batchInfo as
   265→						| { batchNumber: number; totalBatches: number }
   266→						| undefined;
   267→					const batchLabel2 = bi2
   268→						? ` (batch ${bi2.batchNumber}/${bi2.totalBatches})`
   269→						: "";
   270→					const step = event.data?.step as string | undefined;
   271→					const batchDurationMs = event.data?.batchDurationMs as
   272→						| number
   273→						| undefined;
   274→					const batchCostUsd = event.data?.batchCostUsd as number | undefined;
   275→					const batchInputTokens = event.data?.batchInputTokens as
   276→						| number
   277→						| undefined;
   278→					const batchOutputTokens = event.data?.batchOutputTokens as
   279→						| number
   280→						| undefined;
   281→
   282→					const hasBatchStats =
   283→						batchDurationMs !== undefined && batchCostUsd !== undefined;
   284→					let logMessage = `Step completed: ${step}${batchLabel2}`;
   285→					if (hasBatchStats) {
   286→						const phaseName =
   287→							step === "executing_error_fix"
   288→								? "Error fix"
   289→								: "Suggestion enrich";
   290→						const label = bi2
   291→							? `${phaseName} batch ${bi2.batchNumber}/${bi2.totalBatches}`
   292→							: phaseName;
   293→						logMessage = `${label} completed: ${Math.round(batchDurationMs / 1000)}s, $${batchCostUsd.toFixed(4)}`;
   294→					}
   295→
   296→					setProgressState((prev) => ({
   297→						...prev,
   298→						batchInfo: undefined,
   299→						currentBatchIssues: undefined,
   300→						...(hasBatchStats && {
   301→							lastBatchStats: {
   302→								durationMs: batchDurationMs!,
   303→								costUsd: batchCostUsd!,
   304→								inputTokens: batchInputTokens ?? 0,
   305→								outputTokens: batchOutputTokens ?? 0,
   306→							},
   307→						}),
   308→						logs: [...prev.logs, { timestamp, message: logMessage }],
   309→					}));
   310→					break;
   311→				}
   312→
   313→				case "remediation.completed": {
   314→					setProgressState((prev) => ({
   315→						...prev,
   316→						status: "completed",
   317→						result: event.data?.result as RemediationResult | undefined,
   318→						logs: [
   319→							...prev.logs,
   320→							{ timestamp, message: "Remediation completed" },
   321→						],
   322→					}));
   323→					// Reset to idle, refresh history, clear selection
   324→					// Capture completed remediation ID for auto-expand
   325→					if (activeRemediationIdRef.current) {
   326→						setAutoExpandRemediationId(activeRemediationIdRef.current);
   327→					}
   328→					activeRemediationIdRef.current = null;
   329→					setActiveRemediationId(null);
   330→					setSseUrl(null);
   331→					setPhase("idle");
   332→					onClearAll();
   333→					refreshHistory();
   334→					break;
   335→				}
   336→
   337→				case "remediation.failed":
   338→					setProgressState((prev) => ({
   339→						...prev,
   340→						status: "failed",
   341→						error: event.data?.error,
   342→						logs: [
   343→							...prev.logs,
   344→							{
   345→								timestamp,
   346→								message: `Failed: ${event.data?.error?.message || "Unknown error"}`,
   347→							},
   348→						],
   349→					}));
   350→					setExecuteError(event.data?.error?.message || "Remediation failed");
   351→					setSseUrl(null);
   352→					activeRemediationIdRef.current = null;
   353→					setActiveRemediationId(null);
   354→					setPhase("idle");
   355→					refreshHistory();
   356→					break;
   357→			}
   358→		},
   359→		[onClearAll, refreshHistory],
   360→	);
   361→
   362→	useSSE({
   363→		url: sseUrl,
   364→		onMessage: handleSSEMessage,
   365→	});
   366→
   367→	// Execute remediation
   368→	const handleExecute = useCallback(async () => {
   369→		if (!evaluationId || totalSelected === 0) return;
   370→
   371→		setIsExecuting(true);
   372→		setExecuteError(null);
   373→		setPrompts(null);
   374→
   375→		try {
   376→			const issues = [
   377→				...errorEntries.map((e) => e.issue),
   378→				...suggestionEntries.map((e) => e.issue),
   379→			];
   380→
   381→			const response = await api.executeRemediation(
   382→				evaluationId,
   383→				issues,
   384→				targetAgent,
   385→				selectedProvider,
   386→			);
   387→
   388→			activeRemediationIdRef.current = response.remediationId;
   389→			setActiveRemediationId(response.remediationId);
   390→			setSseUrl(response.sseUrl);
   391→			setProgressState({ status: "queued", logs: [] });
   392→			setPhase("progress");
   393→		} catch (err) {
   394→			const message =
   395→				err instanceof Error ? err.message : "Failed to start remediation";
   396→			setExecuteError(message);
   397→		} finally {
   398→			setIsExecuting(false);
   399→		}
   400→	}, [
   401→		evaluationId,
   402→		totalSelected,
   403→		errorEntries,
   404→		suggestionEntries,
   405→		targetAgent,
   406→		selectedProvider,
   407→		api,
   408→	]);
   409→
   410→	// Generate prompts (legacy copy-paste mode)
   411→	const handleGeneratePrompts = useCallback(async () => {
   412→		if (!evaluationId || totalSelected === 0) return;
   413→
   414→		setIsGenerating(true);
   415→		setGenerateError(null);
   416→
   417→		try {
   418→			const issues = [
   419→				...errorEntries.map((e) => e.issue),
   420→				...suggestionEntries.map((e) => e.issue),
   421→			];
   422→			const result = await api.generateRemediationPrompts(
   423→				evaluationId,
   424→				issues,
   425→				targetAgent,
   426→			);
   427→			setPrompts(result);
   428→		} catch (err) {
   429→			setGenerateError(
   430→				err instanceof Error ? err.message : "Failed to generate prompts",
   431→			);
   432→		} finally {
   433→			setIsGenerating(false);
   434→		}
   435→	}, [
   436→		evaluationId,
   437→		totalSelected,
   438→		errorEntries,
   439→		suggestionEntries,
   440→		targetAgent,
   441→		api,
   442→	]);
   443→
   444→	// Delete a remediation from history
   445→	const handleDeleteRemediation = useCallback(
   446→		async (remediationId: string) => {
   447→			await api.deleteRemediation(remediationId);
   448→			setRemediations((prev) => prev.filter((r) => r.id !== remediationId));
   449→		},
   450→		[api],
   451→	);
   452→
   453→	if (!evaluationData) {
   454→		return (
   455→			<div className="card text-center py-12">
   456→				<p className="text-body-muted">
   457→					No evaluation data. Run an evaluation first to generate remediation
   458→					prompts.
   459→				</p>
   460→			</div>
   461→		);
   462→	}
   463→
   464→	// ─── PROGRESS PHASE ────────────────────────────────────────────────
   465→	if (phase === "progress") {
   466→		return (
   467→			<div className="space-y-6">
   468→				<div className="card">
   469→					<h2 className="text-heading text-slate-100">Executing Remediation</h2>
   470→					<p className="text-body-muted mt-1">
   471→						Running {selectedProvider} agent to fix{" "}
   472→						{totalSelected > 0 ? totalSelected : "selected"} issue
   473→						{totalSelected !== 1 ? "s" : ""}...
   474→					</p>
   475→				</div>
   476→				<RemediationProgress
   477→					currentStep={progressState.currentStep}
   478→					batchInfo={progressState.batchInfo}
   479→					logs={progressState.logs}
   480→					errorCount={progressState.errorCount}
   481→					suggestionCount={progressState.suggestionCount}
   482→					totalBatches={progressState.totalBatches}
   483→					completedBatches={progressState.completedBatches}
   484→					currentPhase={progressState.currentPhase}
   485→					runningTotalCostUsd={progressState.runningTotalCostUsd}
   486→					runningTotalDurationMs={progressState.runningTotalDurationMs}
   487→					runningTotalInputTokens={progressState.runningTotalInputTokens}
   488→					runningTotalOutputTokens={progressState.runningTotalOutputTokens}
   489→					currentBatchIssues={progressState.currentBatchIssues}
   490→				/>
   491→			</div>
   492→		);
   493→	}
   494→
   495→	// ─── IDLE PHASE (config + history) ─────────────────────────────────
   496→	return (
   497→		<div className="space-y-6">
   498→			<div className="card">
   499→				<h2 className="text-heading text-slate-100">Remediation</h2>
   500→				<p className="text-body-muted mt-1">
   501→					Execute remediation to fix issues automatically, or generate
   502→					copy-paste prompts
   503→				</p>
   504→			</div>
   505→
   506→			{/* Past Remediations History */}
   507→			<RemediationHistory
   508→				remediations={remediations}
   509→				onDelete={handleDeleteRemediation}
   510→				onRefresh={refreshHistory}
   511→				cloudMode={cloudMode}
   512→				autoExpandId={autoExpandRemediationId}
   513→				onAutoExpandHandled={() => setAutoExpandRemediationId(null)}
   514→				parentScore={evaluationData?.metadata?.contextScore?.score}
   515→				parentGrade={evaluationData?.metadata?.contextScore?.grade}
   516→				hasRepoUrl={(() => {
   517→					// repositoryUrl is present at runtime but not in frontend Metadata type
   518→					// biome-ignore lint/suspicious/noExplicitAny: runtime metadata field
   519→					const url = (evaluationData?.metadata as any)?.repositoryUrl as
   520→						| string
   521→						| undefined;
   522→					return (
   523→						!!url &&
   524→						url !== "unknown" &&
   525→						(url.startsWith("http") || url.startsWith("git@"))
   526→					);
   527→				})()}
   528→			/>
   529→
   530→			{/* Empty state when no issues selected */}
   531→			{totalSelected === 0 ? (
   532→				<div className="card text-center py-12">
   533→					<svg
   534→						className="w-12 h-12 text-slate-600 mx-auto mb-4"
   535→						fill="none"
   536→						stroke="currentColor"
   537→						viewBox="0 0 24 24"
   538→					>
   539→						<path
   540→							strokeLinecap="round"
   541→							strokeLinejoin="round"
   542→							strokeWidth={1.5}
   543→							d="M12 4v16m8-8H4"
   544→						/>
   545→					</svg>
   546→					<p className="text-body-muted">
   547→						No issues selected. Use the{" "}
   548→						<span className="inline-flex items-center justify-center w-5 h-5 rounded bg-slate-700 text-slate-300 text-xs align-middle mx-0.5">
   549→							+
   550→						</span>{" "}
   551→						button on issue cards in the Errors and Suggestions tabs.
   552→					</p>
   553→				</div>
   554→			) : (
   555→				<div className="card space-y-5">
   556→					{/* Target Agent */}
   557→					<div>
   558→						<label className="text-label text-slate-300 block mb-2">
   559→							Target for markdown file rendering
   560→						</label>
   561→						<div className="flex gap-2">
   562→							{(
   563→								[
   564→									["agents-md", "AGENTS.md"],
   565→									["claude-code", "Claude Code"],
   566→									["github-copilot", "GitHub Copilot"],
   567→									["cursor", "Cursor"],
   568→								] as const
   569→							).map(([value, label]) => (
   570→								<button
   571→									key={value}
   572→									onClick={() => setTargetAgent(value)}
   573→									className={`px-3 py-1.5 rounded text-sm transition-colors ${
   574→										targetAgent === value
   575→											? "bg-blue-600 text-white"
   576→											: "bg-slate-700 text-slate-300 hover:bg-slate-600"
   577→									}`}
   578→								>
   579→									{label}
   580→								</button>
   581→							))}
   582→						</div>
   583→					</div>
   584→
   585→					{/* Provider Selector */}
   586→					<div>
   587→						<label className="text-label text-slate-300 block mb-2">
   588→							Pick the AI agent to execute the remediation
   589→						</label>
   590→						{providerDetection.status === "detecting" ? (
   591→							<div className="text-sm text-slate-400">
   592→								Detecting providers...
   593→							</div>
   594→						) : availableProviders.length > 0 ? (
   595→							<div className="flex flex-wrap gap-2">
