   784→function IssueSection({
   785→	title,
   786→	count,
   787→	entries,
   788→	onRemove,
   789→}: {
   790→	title: string;
   791→	count: number;
   792→	entries: { key: string; issue: Issue }[];
   793→	onRemove: (key: string) => void;
   794→}) {
   795→	return (
   796→		<div className="bg-slate-800/50 rounded-lg border border-slate-700/50 p-3">
   797→			<div className="flex items-center justify-between mb-2">
   798→				<span className="text-sm font-semibold text-slate-200">
   799→					{title} ({count})
   800→				</span>
   801→			</div>
   802→			<div className="space-y-1 max-h-48 overflow-y-auto custom-scrollbar">
   803→				{entries.map(({ key, issue }) => {
   804→					const numericSeverity = getIssueSeverity(issue);
   805→					const severityLevel = getSeverityLevel(numericSeverity);
   806→					const issueTitle =
   807→						issue.title || issue.problem || issue.description || issue.category;
   808→
   809→					return (
   810→						<div
   811→							key={key}
   812→							className="flex items-center gap-2 py-1 px-1 rounded hover:bg-slate-700/30 group"
   813→						>
   814→							<div
   815→								className={`severity-dot severity-dot-${severityLevel} flex-shrink-0`}
   816→							/>
   817→							<div className="flex-shrink-0 w-[72px]">
   818→								{issue.issueType === "error" ? (
   819→									<span className={getSeverityColor(issue.severity)}>
   820→										{severityLevel.charAt(0).toUpperCase() +
   821→											severityLevel.slice(1)}
   822→									</span>
   823→								) : (
   824→									<span className={getImpactBadgeClass(issue.impactLevel)}>
   825→										{getImpactLabel(issue.impactLevel)}
   826→									</span>
   827→								)}
   828→							</div>
   829→							<span className="flex-1 min-w-0 text-sm text-slate-300 truncate">
   830→								{issueTitle}
   831→							</span>
   832→							{issue.evaluatorName && (
   833→								<span className="flex-shrink-0 text-xs text-slate-500 hidden sm:block">
   834→									{issue.evaluatorName}
   835→								</span>
   836→							)}
   837→							<button
   838→								onClick={() => onRemove(key)}
   839→								className="flex-shrink-0 text-slate-500 hover:text-red-400 transition-colors opacity-0 group-hover:opacity-100"
   840→								title="Remove from remediation"
   841→							>
   842→								<svg
   843→									className="h-4 w-4"
   844→									fill="none"
   845→									viewBox="0 0 24 24"
   846→									stroke="currentColor"
   847→								>
   848→									<path
   849→										strokeLinecap="round"
   850→										strokeLinejoin="round"
   851→										strokeWidth={2}
   852→										d="M6 18L18 6M6 6l12 12"
   853→									/>
   854→								</svg>
   855→							</button>
   856→						</div>
   857→					);
   858→				})}
   859→			</div>
   860→		</div>
   861→	);
   862→}
   863→
   864→function PromptDisplay({ title, content }: { title: string; content: string }) {
   865→	return (
   866→		<div className="card">
   867→			<div className="flex items-center justify-between mb-3">
   868→				<h3 className="text-subheading text-slate-200">{title}</h3>
   869→				<CopyButton text={content} variant="text" />
   870→			</div>
   871→			<div className="bg-slate-900/50 rounded-lg p-4 border border-slate-700/50 font-mono text-xs text-slate-300 max-h-96 overflow-y-auto custom-scrollbar whitespace-pre-wrap">
   872→				{content}
   873→			</div>
   874→		</div>
   875→	);
   876→}
