   121→export const ContextTab: React.FC<ContextTabProps> = ({ metadata }) => {
   122→	const [showSkillsModal, setShowSkillsModal] = useState(false);
   123→	const [showCopilotSkillsModal, setShowCopilotSkillsModal] = useState(false);
   124→	const [showCursorSkillsModal, setShowCursorSkillsModal] = useState(false);
   125→	const [showLinkedDocsModal, setShowLinkedDocsModal] = useState(false);
   126→	const [contextFilesModalType, setContextFilesModalType] = useState<
   127→		| "agents"
   128→		| "claude"
   129→		| "copilot"
   130→		| "rules"
   131→		| "claude-code"
   132→		| "cursor-rules"
   133→		| "skills"
   134→		| null
   135→	>(null);
   136→
   137→	// Context files derivation
   138→	const agentsFilePaths = metadata.projectContext?.agentsFilePaths ?? [];
   139→	const contextFilesFromApi = metadata.projectContext?.contextFiles ?? [];
   140→
   141→	const contextFiles =
   142→		contextFilesFromApi.length > 0
   143→			? contextFilesFromApi
   144→			: agentsFilePaths.map((path) => {
   145→					const lowerPath = path.toLowerCase();
   146→					let type: "agents" | "claude" | "copilot" = "agents";
   147→					if (lowerPath.includes("claude")) {
   148→						type = "claude";
   149→					} else if (lowerPath.includes("copilot")) {
   150→						type = "copilot";
   151→					}
   152→					return {
   153→						path,
   154→						type,
   155→						content: "",
   156→						summary: "Re-run evaluation to load content and summary.",
   157→					};
   158→				});
   159→
   160→	const agentsMdPaths =
   161→		contextFilesFromApi.length > 0
   162→			? contextFilesFromApi
   163→					.filter((f) => f.type === "agents")
   164→					.map((f) => f.path)
   165→			: agentsFilePaths.filter((p) => p.toLowerCase().includes("agents"));
   166→	const claudeMdPaths =
   167→		contextFilesFromApi.length > 0
   168→			? contextFilesFromApi
   169→					.filter((f) => f.type === "claude")
   170→					.map((f) => f.path)
   171→			: agentsFilePaths.filter((p) => p.toLowerCase().includes("claude"));
   172→	const rulesPaths =
   173→		contextFilesFromApi.length > 0
   174→			? contextFilesFromApi.filter((f) => f.type === "rules").map((f) => f.path)
   175→			: agentsFilePaths.filter((p) => p.includes(".claude/rules/"));
   176→	const copilotInstructionsPaths =
   177→		contextFilesFromApi.length > 0
   178→			? contextFilesFromApi
   179→					.filter((f) => f.type === "copilot")
   180→					.map((f) => f.path)
   181→			: agentsFilePaths.filter((p) =>
   182→					p.toLowerCase().includes("copilot-instructions"),
   183→				);
   184→	const cursorRulesPaths =
   185→		contextFilesFromApi.length > 0
   186→			? contextFilesFromApi
   187→					.filter((f) => f.type === "cursor-rules")
   188→					.map((f) => f.path)
   189→			: agentsFilePaths.filter((p) => p.includes(".cursor/rules/"));
   190→
   191→	const agentsMdCount = agentsMdPaths.length;
   192→	const claudeMdCount = claudeMdPaths.length;
   193→	const rulesCount = rulesPaths.length;
   194→	const copilotInstructionsCount = copilotInstructionsPaths.length;
   195→	const cursorRulesCount = cursorRulesPaths.length;
   196→	const skills = metadata.projectContext?.skills ?? [];
   197→	const linkedDocs = metadata.projectContext?.linkedDocs ?? [];
   198→	const linkedDocsCount = linkedDocs.length;
   199→
   200→	// Sorted for display
   201→	const sortedAgentsMdPaths = [...agentsMdPaths].sort((a, b) =>
   202→		a.localeCompare(b),
   203→	);
   204→	const sortedClaudeMdPaths = [...claudeMdPaths].sort((a, b) =>
   205→		a.localeCompare(b),
   206→	);
   207→	const sortedRulesPaths = [...rulesPaths].sort((a, b) => a.localeCompare(b));
   208→	const sortedCopilotInstructionsPaths = [...copilotInstructionsPaths].sort(
   209→		(a, b) => a.localeCompare(b),
   210→	);
   211→	const sortedCursorRulesPaths = [...cursorRulesPaths].sort((a, b) =>
   212→		a.localeCompare(b),
   213→	);
   214→	const sortedSkills = [...skills].sort((a, b) => a.name.localeCompare(b.name));
   215→
   216→	// Split skills by agent path prefix
   217→	const claudeSkills = sortedSkills.filter((s) =>
   218→		s.path.startsWith(".claude/skills/"),
   219→	);
   220→	const copilotSkills = sortedSkills.filter((s) =>
   221→		s.path.startsWith(".github/skills/"),
   222→	);
   223→	const cursorSkills = sortedSkills.filter((s) =>
   224→		s.path.startsWith(".cursor/skills/"),
   225→	);
   226→	const genericSkills = sortedSkills.filter(
   227→		(s) =>
   228→			!s.path.startsWith(".claude/skills/") &&
   229→			!s.path.startsWith(".github/skills/") &&
   230→			!s.path.startsWith(".cursor/skills/"),
   231→	);
   232→	const claudeSkillsCount = claudeSkills.length + genericSkills.length;
   233→
   234→	const sortedLinkedDocs = [...linkedDocs].sort((a, b) =>
   235→		a.path.localeCompare(b.path),
   236→	);
   237→
   238→	// Section visibility
   239→	const hasAgentsMd = agentsMdCount > 0;
