     1→/**
     2→ * Collapsible container for past remediation history.
     3→ * Accordion behavior: expanding one card collapses others.
     4→ */
     5→
     6→import { useCallback, useEffect, useRef, useState } from "react";
     7→import { useEvaluationApi } from "../hooks/useEvaluationApi";
     8→import type { RemediationHistoryItem } from "../types/remediation";
     9→import {
    10→	type ImpactEvalStatus,
    11→	RemediationHistoryCard,
    12→} from "./RemediationHistoryCard";
    13→import { Modal } from "./shared/Modal";
    14→
    15→interface ImpactEvalState {
    16→	jobId?: string;
    17→	status: ImpactEvalStatus;
    18→	score?: number;
    19→	grade?: string;
    20→}
    21→
    22→interface RemediationHistoryProps {
    23→	remediations: RemediationHistoryItem[];
    24→	onDelete: (id: string) => void;
    25→	onRefresh?: () => void;
    26→	cloudMode?: boolean;
    27→	autoExpandId?: string | null;
    28→	onAutoExpandHandled?: () => void;
    29→	parentScore?: number;
    30→	parentGrade?: string;
    31→	hasRepoUrl?: boolean;
    32→}
    33→
    34→export function RemediationHistory({
    35→	remediations,
    36→	onDelete,
    37→	onRefresh,
    38→	cloudMode = false,
    39→	autoExpandId,
    40→	onAutoExpandHandled,
    41→	parentScore,
    42→	hasRepoUrl = true,
    43→}: RemediationHistoryProps) {
    44→	const api = useEvaluationApi();
    45→	const [collapsed, setCollapsed] = useState(true);
    46→	const [expandedId, setExpandedId] = useState<string | null>(null);
    47→	const [deleteTargetId, setDeleteTargetId] = useState<string | null>(null);
    48→	const [isDeleting, setIsDeleting] = useState(false);
    49→	const [impactEvals, setImpactEvals] = useState<Map<string, ImpactEvalState>>(
    50→		new Map(),
    51→	);
    52→	const sseCleanupRef = useRef<Map<string, () => void>>(new Map());
    53→
    54→	// Auto-expand a specific card when requested (e.g., after remediation completes)
    55→	useEffect(() => {
    56→		if (autoExpandId) {
    57→			setCollapsed(false);
    58→			setExpandedId(autoExpandId);
    59→			onAutoExpandHandled?.();
    60→		}
    61→	}, [autoExpandId, onAutoExpandHandled]);
    62→
    63→	// Load scores for remediations that already have a result evaluation
    64→	useEffect(() => {
    65→		for (const item of remediations) {
    66→			if (item.resultEvaluationId) {
    67→				// Skip if we already have score for this one
    68→				const existing = impactEvals.get(item.id);
    69→				if (existing?.score !== undefined) continue;
    70→
    71→				api.getEvaluationScore(item.resultEvaluationId).then((data) => {
    72→					if (data.contextScore !== undefined) {
    73→						setImpactEvals((prev) => {
    74→							const next = new Map(prev);
    75→							next.set(item.id, {
    76→								status: "completed",
    77→								score: data.contextScore,
    78→								grade: data.contextGrade,
    79→							});
    80→							return next;
    81→						});
    82→					}
    83→				});
    84→			}
    85→		}
    86→	}, [remediations, api, impactEvals]);
    87→
    88→	// Cleanup SSE connections on unmount
    89→	useEffect(() => {
    90→		return () => {
    91→			for (const cleanup of sseCleanupRef.current.values()) {
    92→				cleanup();
    93→			}
    94→		};
    95→	}, []);
    96→
    97→	const handleEvaluateImpact = useCallback(
    98→		async (remediationId: string) => {
    99→			try {
   100→				setImpactEvals((prev) => {
   101→					const next = new Map(prev);
   102→					next.set(remediationId, { status: "running" });
   103→					return next;
   104→				});
   105→
   106→				const result = await api.evaluateRemediationImpact(remediationId);
   107→
   108→				if (result.status === "already_exists") {
   109→					// Fetch score directly
   110→					const scoreData = await api.getEvaluationScore(result.jobId);
   111→					setImpactEvals((prev) => {
   112→						const next = new Map(prev);
   113→						next.set(remediationId, {
   114→							jobId: result.jobId,
   115→							status: "completed",
   116→							score: scoreData.contextScore,
   117→							grade: scoreData.contextGrade,
   118→						});
   119→						return next;
   120→					});
   121→					return;
   122→				}
   123→
   124→				// Connect to evaluation SSE for progress
   125→				const sseUrl = result.sseUrl.startsWith("/")
   126→					? `${window.location.origin}${result.sseUrl}`
   127→					: result.sseUrl;
   128→
   129→				const eventSource = new EventSource(sseUrl);
   130→
   131→				const cleanup = () => {
   132→					eventSource.close();
   133→					sseCleanupRef.current.delete(remediationId);
   134→				};
   135→				sseCleanupRef.current.set(remediationId, cleanup);
   136→
   137→				eventSource.onmessage = (event) => {
   138→					try {
   139→						const data = JSON.parse(event.data);
   140→
   141→						if (data.type === "job.completed") {
   142→							cleanup();
   143→							// Fetch the score from the completed evaluation
   144→							api.getEvaluationScore(result.jobId).then((scoreData) => {
   145→								setImpactEvals((prev) => {
   146→									const next = new Map(prev);
   147→									next.set(remediationId, {
   148→										jobId: result.jobId,
   149→										status: "completed",
   150→										score: scoreData.contextScore,
   151→										grade: scoreData.contextGrade,
   152→									});
   153→									return next;
   154→								});
   155→							});
   156→							onRefresh?.();
   157→						} else if (data.type === "job.failed") {
   158→							cleanup();
   159→							setImpactEvals((prev) => {
   160→								const next = new Map(prev);
   161→								next.set(remediationId, {
   162→									jobId: result.jobId,
   163→									status: "failed",
   164→								});
   165→								return next;
   166→							});
   167→						}
   168→					} catch {
   169→						// Ignore parse errors
   170→					}
   171→				};
   172→
   173→				eventSource.onerror = () => {
   174→					cleanup();
   175→					setImpactEvals((prev) => {
   176→						const next = new Map(prev);
   177→						const current = next.get(remediationId);
   178→						if (current?.status === "running") {
   179→							next.set(remediationId, {
   180→								...current,
   181→								status: "failed",
   182→							});
   183→						}
   184→						return next;
   185→					});
   186→				};
   187→			} catch {
   188→				setImpactEvals((prev) => {
   189→					const next = new Map(prev);
   190→					next.set(remediationId, { status: "failed" });
   191→					return next;
   192→				});
   193→			}
   194→		},
   195→		[api, onRefresh],
   196→	);
   197→
   198→	const handleToggleCard = useCallback((id: string) => {
   199→		setExpandedId((prev) => (prev === id ? null : id));
   200→	}, []);
   201→
   202→	const handleConfirmDelete = useCallback(async () => {
   203→		if (!deleteTargetId) return;
   204→		setIsDeleting(true);
   205→		try {
   206→			await onDelete(deleteTargetId);
   207→			if (expandedId === deleteTargetId) {
   208→				setExpandedId(null);
   209→			}
   210→		} finally {
   211→			setIsDeleting(false);
   212→			setDeleteTargetId(null);
   213→		}
   214→	}, [deleteTargetId, expandedId, onDelete]);
   215→
   216→	if (remediations.length === 0) return null;
   217→
   218→	return (
   219→		<div className="card">
   220→			<button
   221→				onClick={() => setCollapsed(!collapsed)}
   222→				className="w-full flex items-center justify-between text-left"
   223→			>
   224→				<span className="text-sm font-semibold text-slate-200">
   225→					Past Remediations ({remediations.length})
   226→				</span>
   227→				<svg
   228→					className={`w-4 h-4 text-slate-400 transition-transform ${collapsed ? "" : "rotate-180"}`}
   229→					fill="none"
   230→					viewBox="0 0 24 24"
   231→					stroke="currentColor"
   232→				>
   233→					<path
   234→						strokeLinecap="round"
   235→						strokeLinejoin="round"
   236→						strokeWidth={2}
   237→						d="M19 9l-7 7-7-7"
   238→					/>
   239→				</svg>
   240→			</button>
   241→
   242→			{!collapsed && (
   243→				<div className="mt-3 space-y-2">
   244→					{remediations.map((item, idx) => {
   245→						const evalState = impactEvals.get(item.id);
   246→						return (
   247→							<RemediationHistoryCard
   248→								key={item.id}
   249→								item={item}
   250→								index={idx}
   251→								total={remediations.length}
   252→								expanded={expandedId === item.id}
   253→								onToggle={() => handleToggleCard(item.id)}
   254→								onDelete={() => setDeleteTargetId(item.id)}
   255→								onEvaluateImpact={() => handleEvaluateImpact(item.id)}
   256→								cloudMode={cloudMode}
   257→								parentScore={parentScore}
   258→								impactEvalStatus={evalState?.status}
   259→								impactScore={evalState?.score}
   260→								impactGrade={evalState?.grade}
   261→								hasRepoUrl={hasRepoUrl}
   262→							/>
   263→						);
   264→					})}
   265→				</div>
   266→			)}
   267→
   268→			{/* Delete Confirmation Modal */}
   269→			<Modal
   270→				isOpen={deleteTargetId !== null}
   271→				onClose={() => setDeleteTargetId(null)}
   272→				title="Delete Remediation"
   273→				maxWidth="max-w-md"
   274→			>
   275→				<div className="space-y-4">
   276→					<p className="text-sm text-slate-300">
   277→						This will permanently delete this remediation record and its patch.
   278→						This action cannot be undone.
   279→					</p>
   280→					<div className="flex justify-end gap-3 pt-2">
   281→						<button
   282→							onClick={() => setDeleteTargetId(null)}
   283→							className="btn-secondary"
   284→							disabled={isDeleting}
   285→						>
   286→							Cancel
   287→						</button>
   288→						<button
   289→							onClick={handleConfirmDelete}
   290→							disabled={isDeleting}
   291→							className="btn-primary bg-red-600 hover:bg-red-500"
   292→						>
   293→							{isDeleting ? "Deleting..." : "Delete"}
   294→						</button>
   295→					</div>
   296→				</div>
   297→			</Modal>
   298→		</div>
   299→	);
   300→}
