    14→async function copyAssets() {
    15→	if (existsSync(ASSETS_DIR)) {
    16→		cpSync(ASSETS_DIR, join(DIST_DIR, "assets"), { recursive: true });
    17→	}
    18→	// Copy public folder (favicon, logo, etc.)
    19→	if (existsSync(PUBLIC_DIR)) {
    20→		cpSync(PUBLIC_DIR, join(DIST_DIR, "public"), { recursive: true });
    21→	}
    22→}
    23→
    24→async function build() {
    25→	const buildResult = await Bun.build({
    26→		entrypoints: [join(FRONTEND_DIR, "index.html")],
    27→		outdir: DIST_DIR,
    28→		target: "browser",
    29→		minify: false,
    30→	});
    31→
    32→	if (!buildResult.success) {
    33→		console.error("Build failed:", buildResult.logs);
    34→		return false;
    35→	}
    36→
    37→	// Copy static assets after build
    38→	await copyAssets();
    39→
    40→	buildVersion = Date.now();
    41→	return true;
    42→}
