    44→// Initial build
    45→if (!(await build())) {
    46→	process.exit(1);
    47→}
    48→
    49→// Watch for changes and rebuild
    50→let rebuildTimeout: Timer | null = null;
    51→watch(SRC_DIR, { recursive: true }, (_event, filename) => {
    52→	if (
    53→		filename &&
    54→		(filename.endsWith(".ts") ||
    55→			filename.endsWith(".tsx") ||
    56→			filename.endsWith(".css"))
    57→	) {
    58→		// Debounce rebuilds
    59→		if (rebuildTimeout) clearTimeout(rebuildTimeout);
    60→		rebuildTimeout = setTimeout(async () => {
    61→			console.log(`\n🔄 Change detected in ${filename}, rebuilding...`);
    62→			if (await build()) {
    63→				console.log("✅ Rebuild complete");
    64→			}
    65→		}, 100);
    66→	}
    67→});
    68→
    69→// API backend URL (can be configured via environment variable)
    70→const API_URL = process.env.API_URL || "http://localhost:3001";
    71→
    72→Bun.serve({
    73→	port: 3000,
    74→	async fetch(req) {
    75→		const url = new URL(req.url);
    76→		const path = url.pathname;
    77→
    78→		// Live reload polling endpoint
    79→		if (path === "/__reload") {
    80→			return new Response(JSON.stringify({ version: buildVersion }), {
    81→				headers: { "Content-Type": "application/json" },
    82→			});
    83→		}
    84→
    85→		// Proxy /api/ requests to the backend
    86→		if (path.startsWith("/api/")) {
    87→			const backendUrl = `${API_URL}${path}${url.search}`;
    88→			console.log(`[Proxy] ${req.method} ${path} -> ${backendUrl}`);
    89→
    90→			try {
    91→				// Forward the request to the backend
    92→				const backendResponse = await fetch(backendUrl, {
    93→					method: req.method,
    94→					headers: req.headers,
    95→					body:
    96→						req.method !== "GET" && req.method !== "HEAD"
    97→							? req.body
    98→							: undefined,
    99→					// Don't follow redirects, let the client handle them
   100→					redirect: "manual",
   101→				});
   102→
   103→				// Clone the response headers and add CORS
   104→				const headers = new Headers(backendResponse.headers);
   105→				headers.set("Access-Control-Allow-Origin", "*");
   106→
   107→				// Return the proxied response
   108→				return new Response(backendResponse.body, {
   109→					status: backendResponse.status,
   110→					statusText: backendResponse.statusText,
   111→					headers,
   112→				});
   113→			} catch (error) {
   114→				console.error(`[Proxy] Error forwarding to ${backendUrl}:`, error);
   115→				return new Response(JSON.stringify({ error: "Backend unavailable" }), {
   116→					status: 502,
   117→					headers: { "Content-Type": "application/json" },
   118→				});
   119→			}
   120→		}
   121→
   122→		// Live reload script to inject in dev mode
   123→		const liveReloadScript = `
   124→<script>
   125→(function() {
   126→  let lastVersion = ${buildVersion};
   127→  setInterval(async () => {
   128→    try {
   129→      const res = await fetch('/__reload');
   130→      const { version } = await res.json();
   131→      if (version !== lastVersion) {
   132→        console.log('🔄 Reloading...');
   133→        location.reload();
   134→      }
   135→    } catch (e) {}
   136→  }, 500);
   137→})();
   138→</script>
   139→`;
   140→
   141→		// Serve index.html for root or SPA routes
   142→		if (path === "/" || path === "/index.html" || !path.includes(".")) {
   143→			const htmlFile = Bun.file(join(DIST_DIR, "index.html"));
   144→			if (await htmlFile.exists()) {
   145→				let html = await htmlFile.text();
   146→				// Fix relative paths that conflict with <base href="/">
   147→				html = html.replace(/href="\.\/([^"]+)"/g, 'href="/$1"');
   148→				html = html.replace(/src="\.\/([^"]+)"/g, 'src="/$1"');
   149→				// Inject live reload script before </body>
   150→				html = html.replace("</body>", `${liveReloadScript}</body>`);
   151→				return new Response(html, {
   152→					headers: { "Content-Type": "text/html" },
   153→				});
   154→			}
   155→		}
   156→
   157→		// Try to serve from dist folder
   158→		const file = Bun.file(join(DIST_DIR, path));
   159→		if (await file.exists()) {
   160→			return new Response(file);
   161→		}
   162→
   163→		// Try to serve static assets from frontend root (e.g., /assets/images/)
   164→		const assetFile = Bun.file(join(FRONTEND_DIR, path));
   165→		if (await assetFile.exists()) {
   166→			return new Response(assetFile);
   167→		}
   168→
   169→		return new Response("Not Found", { status: 404 });
   170→	},
   171→});
   172→
