     1→import { readdir, readFile, stat, writeFile } from "fs/promises";
     2→
     3→const html = await readFile("index.html", "utf-8");
     4→
     5→// Find the generated App.*.js entry file (the largest one, which is the entry point)
     6→const files = await readdir("dist");
     7→const appJsFiles = files.filter(
     8→	(f) => f.startsWith("App.") && f.endsWith(".js") && !f.includes(".map"),
     9→);
    10→
    11→if (appJsFiles.length === 0) {
    12→	throw new Error("No App.*.js bundle found in dist/");
    13→}
    14→
    15→// Get file sizes and pick the largest (entry point is always largest)
    16→const filesWithSizes = await Promise.all(
    17→	appJsFiles.map(async (f) => ({
    18→		name: f,
    19→		size: (await stat(`dist/${f}`)).size,
    20→	})),
    21→);
    22→
    23→filesWithSizes.sort((a, b) => b.size - a.size);
    24→const jsFile = filesWithSizes[0].name;
    25→
    26→// Update HTML with production assets
    27→let prodHtml = html;
    28→
    29→// Remove ALL old CSS references (dev and any chunks)
    30→prodHtml = prodHtml.replace(
    31→	/<link[^>]*rel="stylesheet"[^>]*href="[^"]*output\.css"[^>]*>/g,
    32→	"",
    33→);
    34→prodHtml = prodHtml.replace(
    35→	/<link[^>]*rel="stylesheet"[^>]*href="[^"]*chunk-[^"]*\.css"[^>]*>/g,
    36→	"",
    37→);
    38→
    39→// Remove old script references
    40→prodHtml = prodHtml.replace(
    41→	/<script[^>]*src="\.\/src\/App\.tsx"[^>]*><\/script>/g,
    42→	"",
    43→);
    44→
    45→// Transform favicon/asset paths from relative (./public/) to absolute (/)
    46→// This fixes deep link asset resolution (e.g., /evaluation/:id won't resolve ./public/logo.svg correctly)
    47→prodHtml = prodHtml.replace(/href="\.\/public\//g, 'href="/');
    48→
    49→// Add production CSS and JS (find insertion point before </head>)
    50→const headCloseIndex = prodHtml.indexOf("</head>");
    51→if (headCloseIndex === -1) {
    52→	throw new Error("No </head> tag found in HTML");
    53→}
    54→
    55→const assetsHtml = `  <link rel="stylesheet" href="/styles.css">\n  <script type="module" src="/${jsFile}"></script>\n`;
    56→prodHtml =
    57→	prodHtml.slice(0, headCloseIndex) +
    58→	assetsHtml +
    59→	prodHtml.slice(headCloseIndex);
    60→
    61→await writeFile("dist/index.html", prodHtml);
    62→console.log(`✓ Built dist/index.html (CSS: styles.css, JS: ${jsFile})`);
    63→

