   103→/** GitHub Copilot official logo */
   104→const CopilotLogo: React.FC<{ className?: string }> = ({
   105→	className = "w-4 h-4",
   106→}) => (
   107→	<svg className={className} fill="currentColor" viewBox="0 0 16 16">
   108→		<path d="M8 1C4.14 1 1 4.14 1 8s3.14 7 7 7 7-3.14 7-7-3.14-7-7-7zm2.5 5.5a1 1 0 1 1 0 2 1 1 0 0 1 0-2zm-5 0a1 1 0 1 1 0 2 1 1 0 0 1 0-2zm6 3.5c-.55.83-1.4 1.5-3.5 1.5S5.05 10.83 4.5 10h7z" />
   109→	</svg>
   110→);
   111→
   112→/** Cursor official logo */
   113→const CursorLogo: React.FC<{ className?: string }> = ({
   114→	className = "w-4 h-4",
   115→}) => (
   116→	<svg className={className} fill="currentColor" viewBox="0 0 24 24">
   117→		<path d="M4.5 3a1.5 1.5 0 0 0-1.5 1.5v15A1.5 1.5 0 0 0 4.5 21h15a1.5 1.5 0 0 0 1.5-1.5v-15A1.5 1.5 0 0 0 19.5 3h-15Zm2.25 3.75h1.5v9.5h-1.5v-9.5Zm3.5 0h1.5l3.5 6.5V6.75h1.5v9.5h-1.5l-3.5-6.5v6.5h-1.5v-9.5Z" />
   118→	</svg>
   119→);
   120→
   121→export const ContextTab: React.FC<ContextTabProps> = ({ metadata }) => {
   122→	const [showSkillsModal, setShowSkillsModal] = useState(false);
   123→	const [showCopilotSkillsModal, setShowCopilotSkillsModal] = useState(false);
   124→	const [showCursorSkillsModal, setShowCursorSkillsModal] = useState(false);
   125→	const [showLinkedDocsModal, setShowLinkedDocsModal] = useState(false);
   126→	const [contextFilesModalType, setContextFilesModalType] = useState<
   127→		| "agents"
   128→		| "claude"
   129→		| "copilot"
   130→		| "rules"
   131→		| "claude-code"
   132→		| "cursor-rules"
   133→		| "skills"
   134→		| null
   135→	>(null);
   136→
   137→	// Context files derivation
   138→	const agentsFilePaths = metadata.projectContext?.agentsFilePaths ?? [];
   139→	const contextFilesFromApi = metadata.projectContext?.contextFiles ?? [];
   140→
   141→	const contextFiles =
   142→		contextFilesFromApi.length > 0
   143→			? contextFilesFromApi
   144→			: agentsFilePaths.map((path) => {
   145→					const lowerPath = path.toLowerCase();
   146→					let type: "agents" | "claude" | "copilot" = "agents";
   147→					if (lowerPath.includes("claude")) {
   148→						type = "claude";
   149→					} else if (lowerPath.includes("copilot")) {
   150→						type = "copilot";
   151→					}
   152→					return {
   153→						path,
   154→						type,
   155→						content: "",
   156→						summary: "Re-run evaluation to load content and summary.",
   157→					};
   158→				});
   159→
   160→	const agentsMdPaths =
   161→		contextFilesFromApi.length > 0
   162→			? contextFilesFromApi
   163→					.filter((f) => f.type === "agents")
   164→					.map((f) => f.path)
   165→			: agentsFilePaths.filter((p) => p.toLowerCase().includes("agents"));
   166→	const claudeMdPaths =
   167→		contextFilesFromApi.length > 0
   168→			? contextFilesFromApi
   169→					.filter((f) => f.type === "claude")
   170→					.map((f) => f.path)
   171→			: agentsFilePaths.filter((p) => p.toLowerCase().includes("claude"));
   172→	const rulesPaths =
   173→		contextFilesFromApi.length > 0
   174→			? contextFilesFromApi.filter((f) => f.type === "rules").map((f) => f.path)
   175→			: agentsFilePaths.filter((p) => p.includes(".claude/rules/"));
   176→	const copilotInstructionsPaths =
   177→		contextFilesFromApi.length > 0
   178→			? contextFilesFromApi
   179→					.filter((f) => f.type === "copilot")
   180→					.map((f) => f.path)
   181→			: agentsFilePaths.filter((p) =>
   182→					p.toLowerCase().includes("copilot-instructions"),
   183→				);
   184→	const cursorRulesPaths =
   185→		contextFilesFromApi.length > 0
   186→			? contextFilesFromApi
   187→					.filter((f) => f.type === "cursor-rules")
   188→					.map((f) => f.path)
   189→			: agentsFilePaths.filter((p) => p.includes(".cursor/rules/"));
   190→
   191→	const agentsMdCount = agentsMdPaths.length;
   192→	const claudeMdCount = claudeMdPaths.length;
   193→	const rulesCount = rulesPaths.length;
   194→	const copilotInstructionsCount = copilotInstructionsPaths.length;
   195→	const cursorRulesCount = cursorRulesPaths.length;
   196→	const skills = metadata.projectContext?.skills ?? [];
   197→	const linkedDocs = metadata.projectContext?.linkedDocs ?? [];
   198→	const linkedDocsCount = linkedDocs.length;
   199→
   200→	// Sorted for display
   201→	const sortedAgentsMdPaths = [...agentsMdPaths].sort((a, b) =>
   202→		a.localeCompare(b),
   203→	);
   204→	const sortedClaudeMdPaths = [...claudeMdPaths].sort((a, b) =>
   205→		a.localeCompare(b),
   206→	);
   207→	const sortedRulesPaths = [...rulesPaths].sort((a, b) => a.localeCompare(b));
   208→	const sortedCopilotInstructionsPaths = [...copilotInstructionsPaths].sort(
   209→		(a, b) => a.localeCompare(b),
   210→	);
   211→	const sortedCursorRulesPaths = [...cursorRulesPaths].sort((a, b) =>
   212→		a.localeCompare(b),
   213→	);
   214→	const sortedSkills = [...skills].sort((a, b) => a.name.localeCompare(b.name));
   215→
   216→	// Split skills by agent path prefix
   217→	const claudeSkills = sortedSkills.filter((s) =>
   218→		s.path.startsWith(".claude/skills/"),
   219→	);
   220→	const copilotSkills = sortedSkills.filter((s) =>
   221→		s.path.startsWith(".github/skills/"),
   222→	);
   223→	const cursorSkills = sortedSkills.filter((s) =>
   224→		s.path.startsWith(".cursor/skills/"),
   225→	);
   226→	const genericSkills = sortedSkills.filter(
   227→		(s) =>
   228→			!s.path.startsWith(".claude/skills/") &&
   229→			!s.path.startsWith(".github/skills/") &&
   230→			!s.path.startsWith(".cursor/skills/"),
   231→	);
   232→	const claudeSkillsCount = claudeSkills.length + genericSkills.length;
   233→
   234→	const sortedLinkedDocs = [...linkedDocs].sort((a, b) =>
   235→		a.path.localeCompare(b.path),
   236→	);
   237→
   238→	// Section visibility
   239→	const hasAgentsMd = agentsMdCount > 0;
   240→	const hasClaudeCode = claudeMdCount + rulesCount + claudeSkillsCount > 0;
   241→	const hasCopilot = copilotInstructionsCount > 0 || copilotSkills.length > 0;
   242→	const hasCursor = cursorRulesCount > 0 || cursorSkills.length > 0;
   243→	const hasLinkedDocs = linkedDocsCount > 0;
   244→	const hasAnything =
   245→		hasAgentsMd || hasClaudeCode || hasCopilot || hasCursor || hasLinkedDocs;
   246→
   247→	if (!hasAnything) {
   248→		return (
   249→			<div className="glass-card p-12 text-center">
   250→				<DocIcon className="w-12 h-12 text-slate-600 mx-auto mb-3" />
   251→				<p className="text-sm text-slate-400">
   252→					No context files found in this repository.
   253→				</p>
   254→			</div>
   255→		);
   256→	}
   257→
   258→	return (
   259→		<div className="space-y-4 animate-fade-in">
   260→			{/* AGENTS.md section */}
   261→			{hasAgentsMd && (
   262→				<SectionCard
   263→					icon={<DocIcon className="w-4 h-4 text-slate-300" />}
   264→					title="AGENTS.md"
   265→					count={agentsMdCount}
   266→					countLabel="file"
   267→				>
   268→					<FileRow
   269→						label="AGENTS.md"
   270→						icon={<DocIcon className="h-4 w-4 text-slate-500" />}
   271→						count={agentsMdCount}
   272→						paths={sortedAgentsMdPaths}
   273→						onAction={() => setContextFilesModalType("agents")}
   274→						actionLabel="Browse"
   275→					/>
   276→				</SectionCard>
   277→			)}
   278→
   279→			{/* Claude Code section */}
   280→			{hasClaudeCode && (
   281→				<SectionCard
   282→					icon={
   283→						<svg
   284→							className="w-4 h-4 text-slate-300"
   285→							fill="currentColor"
   286→							viewBox="0 0 16 16"
   287→						>
   288→							<path d="m3.127 10.604 3.135-1.76.053-.153-.053-.085H6.11l-.525-.032-1.791-.048-1.554-.065-1.505-.08-.38-.081L0 7.832l.036-.234.32-.214.455.04 1.009.069 1.513.105 1.097.064 1.626.17h.259l.036-.105-.089-.065-.068-.064-1.566-1.062-1.695-1.121-.887-.646-.48-.327-.243-.306-.104-.67.435-.48.585.04.15.04.593.456 1.267.981 1.654 1.218.242.202.097-.068.012-.049-.109-.181-.9-1.626-.96-1.655-.428-.686-.113-.411a2 2 0 0 1-.068-.484l.496-.674L4.446 0l.662.089.279.242.411.94.666 1.48 1.033 2.014.302.597.162.553.06.17h.105v-.097l.085-1.134.157-1.392.154-1.792.052-.504.25-.605.497-.327.387.186.319.456-.045.294-.19 1.23-.37 1.93-.243 1.29h.142l.161-.16.654-.868 1.097-1.372.484-.545.565-.601.363-.287h.686l.505.751-.226.775-.707.895-.585.759-.839 1.13-.524.904.048.072.125-.012 1.897-.403 1.024-.186 1.223-.21.553.258.06.263-.218.536-1.307.323-1.533.307-2.284.54-.028.02.032.04 1.029.098.44.024h1.077l2.005.15.525.346.315.424-.053.323-.807.411-3.631-.863-.872-.218h-.12v.073l.726.71 1.331 1.202 1.667 1.55.084.383-.214.302-.226-.032-1.464-1.101-.565-.497-1.28-1.077h-.084v.113l.295.432 1.557 2.34.08.718-.112.234-.404.141-.444-.08-.911-1.28-.94-1.44-.759-1.291-.093.053-.448 4.821-.21.246-.484.186-.403-.307-.214-.496.214-.98.258-1.28.21-1.016.19-1.263.112-.42-.008-.028-.092.012-.953 1.307-1.448 1.957-1.146 1.227-.274.109-.477-.247.045-.44.266-.39 1.586-2.018.956-1.25.617-.723-.004-.105h-.036l-4.212 2.736-.75.096-.324-.302.04-.496.154-.162 1.267-.871z" />
   289→						</svg>
   290→					}
   291→					title="Claude Code"
   292→					count={claudeMdCount + rulesCount + claudeSkillsCount}
   293→					countLabel="item"
   294→				>
   295→					<FileRow
   296→						label="CLAUDE.md"
   297→						icon={<DocIcon className="h-4 w-4 text-slate-500" />}
   298→						count={claudeMdCount}
   299→						paths={sortedClaudeMdPaths}
   300→						onAction={() => setContextFilesModalType("claude")}
   301→						actionLabel="Browse"
   302→					/>
   303→					<FileRow
   304→						label="Rules"
   305→						icon={<RulesIcon className="h-4 w-4 text-slate-500" />}
   306→						count={rulesCount}
   307→						paths={sortedRulesPaths}
   308→						onAction={() => setContextFilesModalType("rules")}
   309→						actionLabel="Browse"
   310→					/>
   311→					{claudeSkillsCount > 0 && (
   312→						<ItemRow
   313→							label="Skills"
   314→							icon={<SparklesIcon className="h-4 w-4 text-slate-500" />}
   315→							count={claudeSkillsCount}
   316→							chips={[...claudeSkills, ...genericSkills]
   317→								.slice(0, 5)
   318→								.map((s) => s.name)}
   319→							overflowCount={
   320→								claudeSkillsCount > 5 ? claudeSkillsCount - 5 : undefined
   321→							}
   322→							onAction={() => setShowSkillsModal(true)}
   323→							actionLabel="Display all"
   324→						/>
   325→					)}
   326→				</SectionCard>
   327→			)}
   328→
   329→			{/* GitHub Copilot section */}
   330→			{hasCopilot && (
   331→				<SectionCard
   332→					icon={<CopilotLogo className="w-4 h-4 text-slate-300" />}
   333→					title="GitHub Copilot"
   334→					count={copilotInstructionsCount + copilotSkills.length}
   335→					countLabel="item"
   336→				>
   337→					<FileRow
   338→						label="Copilot Instructions"
   339→						icon={<DocIcon className="h-4 w-4 text-slate-500" />}
   340→						count={copilotInstructionsCount}
   341→						paths={sortedCopilotInstructionsPaths}
   342→						onAction={() => setContextFilesModalType("copilot")}
   343→						actionLabel="Browse"
   344→					/>
   345→					{copilotSkills.length > 0 && (
   346→						<ItemRow
   347→							label="Skills"
   348→							icon={<SparklesIcon className="h-4 w-4 text-slate-500" />}
   349→							count={copilotSkills.length}
   350→							chips={copilotSkills.slice(0, 5).map((s) => s.name)}
   351→							overflowCount={
   352→								copilotSkills.length > 5 ? copilotSkills.length - 5 : undefined
   353→							}
   354→							onAction={() => setShowCopilotSkillsModal(true)}
   355→							actionLabel="Display all"
   356→						/>
   357→					)}
   358→				</SectionCard>
   359→			)}
   360→
   361→			{/* Cursor section */}
   362→			{hasCursor && (
   363→				<SectionCard
   364→					icon={<CursorLogo className="w-4 h-4 text-slate-300" />}
   365→					title="Cursor"
   366→					count={cursorRulesCount + cursorSkills.length}
   367→					countLabel="item"
   368→				>
   369→					<FileRow
   370→						label="Cursor Rules"
   371→						icon={<RulesIcon className="h-4 w-4 text-slate-500" />}
   372→						count={cursorRulesCount}
   373→						paths={sortedCursorRulesPaths}
   374→						onAction={() => setContextFilesModalType("cursor-rules")}
   375→						actionLabel="Browse"
   376→					/>
   377→					{cursorSkills.length > 0 && (
   378→						<ItemRow
   379→							label="Skills"
   380→							icon={<SparklesIcon className="h-4 w-4 text-slate-500" />}
   381→							count={cursorSkills.length}
   382→							chips={cursorSkills.slice(0, 5).map((s) => s.name)}
   383→							overflowCount={
   384→								cursorSkills.length > 5 ? cursorSkills.length - 5 : undefined
   385→							}
   386→							onAction={() => setShowCursorSkillsModal(true)}
   387→							actionLabel="Display all"
   388→						/>
   389→					)}
   390→				</SectionCard>
   391→			)}
   392→
   393→			{/* Linked Docs section */}
   394→			{hasLinkedDocs && (
   395→				<SectionCard
   396→					icon={<LinkIcon className="w-4 h-4 text-slate-300" />}
   397→					title="Linked Docs"
   398→					count={linkedDocsCount}
   399→					countLabel="doc"
   400→				>
   401→					<ItemRow
   402→						label="Linked Docs"
   403→						icon={<LinkIcon className="h-4 w-4 text-slate-500" />}
   404→						count={linkedDocsCount}
   405→						chips={sortedLinkedDocs.slice(0, 3).map((d) => d.path)}
   406→						overflowCount={
   407→							linkedDocsCount > 3 ? linkedDocsCount - 3 : undefined
   408→						}
   409→						onAction={() => setShowLinkedDocsModal(true)}
   410→						actionLabel="Display all"
   411→					/>
   412→				</SectionCard>
   413→			)}
   414→
   415→			{/* Modals */}
   416→			<SkillsBrowserModal
   417→				isOpen={showSkillsModal}
   418→				onClose={() => setShowSkillsModal(false)}
   419→				skills={[...claudeSkills, ...genericSkills]}
   420→			/>
   421→			<SkillsBrowserModal
   422→				isOpen={showCopilotSkillsModal}
   423→				onClose={() => setShowCopilotSkillsModal(false)}
   424→				skills={copilotSkills}
   425→			/>
   426→			<SkillsBrowserModal
   427→				isOpen={showCursorSkillsModal}
   428→				onClose={() => setShowCursorSkillsModal(false)}
   429→				skills={cursorSkills}
   430→			/>
   431→			<ContextFilesBrowserModal
   432→				isOpen={contextFilesModalType !== null}
   433→				onClose={() => setContextFilesModalType(null)}
   434→				contextFiles={contextFiles}
   435→				filterType={contextFilesModalType ?? undefined}
   436→			/>
   437→			<LinkedDocsBrowserModal
   438→				isOpen={showLinkedDocsModal}
   439→				onClose={() => setShowLinkedDocsModal(false)}
   440→				linkedDocs={linkedDocs}
   441→			/>
   442→		</div>
   443→	);
   444→};
   445→
   446→/** Section card with header */
   447→const SectionCard: React.FC<{
   448→	icon: React.ReactNode;
   449→	title: string;
   450→	count: number;
   451→	countLabel: string;
   452→	children: React.ReactNode;
   453→}> = ({ icon, title, count, countLabel, children }) => (
   454→	<div className="glass-card p-6">
   455→		<div className="flex items-center gap-2 mb-4">
   456→			<span className="w-6 h-6 bg-slate-700 rounded-lg flex items-center justify-center">
   457→				{icon}
   458→			</span>
   459→			<h3 className="text-base font-bold text-slate-100">{title}</h3>
   460→			<span className="text-xs text-slate-500 ml-1">
   461→				{count} {count === 1 ? countLabel : `${countLabel}s`}
   462→			</span>
   463→		</div>
   464→		<div className="space-y-1">{children}</div>
   465→	</div>
   466→);
   467→
   468→/** A row for a file-type context item (with paths) */
   469→const FileRow: React.FC<{
   470→	label: string;
   471→	icon: React.ReactNode;
   472→	count: number;
   473→	paths: string[];
   474→	onAction: () => void;
   475→	actionLabel: string;
   476→}> = ({ label, icon, count, paths, onAction, actionLabel }) => {
