     1→import React, { useCallback, useEffect, useMemo, useState } from "react";
     2→import { parseMarkdownFrontmatter } from "../../lib/markdown-utils";
     3→import type { ISkill } from "../../types/evaluation";
     4→import { MarkdownPreview } from "./MarkdownPreview";
     5→import { SkillMetadataBar } from "./SkillMetadataBar";
     6→
     7→interface SkillsBrowserModalProps {
     8→	/** Whether the modal is open */
     9→	isOpen: boolean;
    10→	/** Callback when the modal should close */
    11→	onClose: () => void;
    12→	/** Skills to display */
    13→	skills: ISkill[];
    14→}
    15→
    16→/**
    17→ * A two-panel browser modal for viewing skills.
    18→ * Left panel: Searchable list of skills
    19→ * Right panel: Markdown preview of selected skill content
    20→ */
    21→export const SkillsBrowserModal: React.FC<SkillsBrowserModalProps> = ({
    22→	isOpen,
    23→	onClose,
    24→	skills,
    25→}) => {
    26→	const [searchQuery, setSearchQuery] = useState("");
    27→	const [selectedSkillIndex, setSelectedSkillIndex] = useState<number | null>(
    28→		null,
    29→	);
    30→
    31→	// Filter and sort skills alphabetically by name
    32→	const filteredSkills = useMemo(() => {
    33→		let result = [...skills];
    34→
    35→		// Filter by search query
    36→		if (searchQuery.trim()) {
    37→			const query = searchQuery.toLowerCase();
    38→			result = result.filter(
    39→				(skill) =>
    40→					skill.name.toLowerCase().includes(query) ||
    41→					skill.description.toLowerCase().includes(query) ||
    42→					(skill.summary && skill.summary.toLowerCase().includes(query)),
    43→			);
    44→		}
    45→
    46→		// Sort alphabetically by name
    47→		result.sort((a, b) => a.name.localeCompare(b.name));
    48→
    49→		return result;
    50→	}, [skills, searchQuery]);
    51→
    52→	// Get currently selected skill
    53→	const selectedSkill = useMemo(() => {
    54→		if (selectedSkillIndex === null) return null;
    55→		return filteredSkills[selectedSkillIndex] ?? null;
    56→	}, [filteredSkills, selectedSkillIndex]);
    57→
    58→	// Parse frontmatter from selected skill content
    59→	const parsedContent = useMemo(() => {
    60→		if (!selectedSkill?.content) return null;
    61→		return parseMarkdownFrontmatter(selectedSkill.content);
    62→	}, [selectedSkill?.content]);
    63→
    64→	// Reset selection when filter changes and selected item is no longer visible
    65→	useEffect(() => {
    66→		if (
    67→			selectedSkillIndex !== null &&
    68→			selectedSkillIndex >= filteredSkills.length
    69→		) {
    70→			setSelectedSkillIndex(filteredSkills.length > 0 ? 0 : null);
    71→		}
    72→	}, [filteredSkills, selectedSkillIndex]);
    73→
    74→	// Handle Escape key
    75→	const handleKeyDown = useCallback(
    76→		(e: KeyboardEvent) => {
    77→			if (e.key === "Escape") {
    78→				onClose();
    79→			}
    80→		},
    81→		[onClose],
    82→	);
    83→
    84→	// Lock body scroll and add key listener when open
    85→	useEffect(() => {
    86→		if (isOpen) {
    87→			document.body.style.overflow = "hidden";
    88→			document.addEventListener("keydown", handleKeyDown);
    89→		} else {
    90→			document.body.style.overflow = "";
    91→		}
    92→
    93→		return () => {
    94→			document.body.style.overflow = "";
    95→			document.removeEventListener("keydown", handleKeyDown);
    96→		};
    97→	}, [isOpen, handleKeyDown]);
    98→
    99→	// Reset state when modal opens
   100→	useEffect(() => {
   101→		if (isOpen) {
   102→			setSearchQuery("");
   103→			setSelectedSkillIndex(null);
   104→		}
   105→	}, [isOpen]);
   106→
   107→	if (!isOpen) return null;
   108→
   109→	return (
   110→		<div
   111→			className="fixed inset-0 z-50 flex items-center justify-center p-4"
   112→			onClick={onClose}
   113→		>
   114→			{/* Backdrop */}
   115→			<div className="absolute inset-0 bg-slate-900/80 backdrop-blur-sm" />
   116→
   117→			{/* Modal */}
   118→			<div
   119→				className="relative max-w-6xl w-full h-[80vh] glass-card animate-fade-in flex flex-col"
   120→				onClick={(e) => e.stopPropagation()}
   121→			>
