   148→						aria-label="Close modal"
   149→					>
   150→						<svg
   151→							className="w-5 h-5"
   152→							fill="none"
   153→							stroke="currentColor"
   154→							viewBox="0 0 24 24"
   155→						>
   156→							<path
   157→								strokeLinecap="round"
   158→								strokeLinejoin="round"
   159→								strokeWidth={2}
   160→								d="M6 18L18 6M6 6l12 12"
   161→							/>
   162→						</svg>
   163→					</button>
   164→				</div>
   165→
   166→				{/* Two-panel content */}
   167→				<div className="flex flex-1 min-h-0">
   168→					{/* Left panel - Skills list */}
   169→					<div className="w-2/5 border-r border-slate-700/50 flex flex-col">
   170→						{/* Search */}
   171→						<div className="p-3 border-b border-slate-700/50">
   172→							<div className="relative">
   173→								<svg
