   289→	errorsCuratedCount?: number;
   290→	suggestionsCuratedCount?: number;
   291→	errorCurationCostUsd?: number;
   292→	suggestionCurationCostUsd?: number;
   293→	errorCurationDurationMs?: number;
   294→	suggestionCurationDurationMs?: number;
   295→	totalCuratedCount?: number;
   296→	totalCurationCostUsd?: number;
   297→	totalCurationDurationMs?: number;
   298→	// Deduplication metadata (three-phase)
   299→	deduplicationEnabled?: boolean;
   300→	duplicatesRemoved?: number;
   301→	deduplicationClusters?: number;
