   340→// Independent evaluation mode format
   341→export interface IndependentEvaluationOutput {
   342→	metadata: Metadata;
   343→	files: Record<string, FileResult>;
   344→	crossFileIssues: Issue[];
   345→	curation?: ICurationOutput;
   346→}
   347→
   348→// Unified evaluation mode format
   349→export interface UnifiedEvaluatorResult {
   350→	evaluator: string;
   351→	output?: EvaluatorOutput;
   352→	error?: string;
   353→}
   354→
   355→export interface UnifiedEvaluationOutput {
   356→	metadata: Metadata;
   357→	results: UnifiedEvaluatorResult[];
   358→	crossFileIssues?: Issue[];
   359→	curation?: ICurationOutput;
