   361→
   362→// Union type for both formats
   363→export type EvaluationOutput =
   364→	| IndependentEvaluationOutput
   365→	| UnifiedEvaluationOutput;
   366→
   367→// Type guards
   368→export function isUnifiedFormat(
   369→	output: EvaluationOutput,
   370→): output is UnifiedEvaluationOutput {
   371→	return "results" in output && Array.isArray(output.results);
   372→}
   373→
   374→export function isIndependentFormat(
   375→	output: EvaluationOutput,
   376→): output is IndependentEvaluationOutput {
   377→	return "files" in output && typeof output.files === "object";
   378→}
   379→
   380→// Severity helpers
   381→// Thresholds: High (8-10), Medium (6-7), Low (5)
   382→// Note: Evaluator prompts instruct AI to not report issues with severity ≤4
   383→export function getSeverityLevel(severity: number): "high" | "medium" | "low" {
   384→	if (severity >= 8) return "high";
   385→	if (severity >= 6) return "medium";
   386→	return "low";
   387→}
   388→
   389→export function getSeverityColor(severity: number): string {
   390→	// Returns CSS class names defined in styles.css
   391→	if (severity >= 8) return "severity-badge severity-high";
   392→	if (severity >= 6) return "severity-badge severity-medium";
   393→	return "severity-badge severity-low";
   394→}
   395→
   396→export function getSeverityBorderColor(severity: number): string {
   397→	// Returns just the border color for card left borders
   398→	if (severity >= 8) return "rgb(249, 115, 22)"; // orange-500
   399→	if (severity >= 6) return "rgb(234, 179, 8)"; // yellow-500
   400→	return "rgb(148, 163, 184)"; // slate-400
   401→}
   402→
   403→export function getSeverityEmoji(severity: number): string {
   404→	if (severity >= 8) return "🟠";
   405→	if (severity >= 6) return "🟡";
   406→	return "⚪";
   407→}
   408→
   409→// Impact level helpers for suggestions
   410→export function getImpactLabel(impactLevel: "High" | "Medium" | "Low"): string {
   411→	return impactLevel; // Return capitalized: "High", "Medium", "Low"
   412→}
   413→
   414→export function getImpactBadgeClass(
   415→	impactLevel: "High" | "Medium" | "Low",
   416→): string {
   417→	// Reuse existing severity classes for consistent styling
   418→	if (impactLevel === "High") return "severity-badge severity-high"; // ORANGE
   419→	if (impactLevel === "Medium") return "severity-badge severity-medium"; // YELLOW
   420→	return "severity-badge severity-low"; // GRAY
   421→}
   422→
   423→// For sorting/filtering - convert impact to numeric value
   424→// Updated to align with 3-level severity thresholds:
   425→// High: 7-10, Medium: 5-6, Low: 1-4
   426→export function impactToSeverity(
   427→	impactLevel: "High" | "Medium" | "Low",
   428→): number {
   429→	if (impactLevel === "High") return 9; // Maps to high (7-10)
   430→	if (impactLevel === "Medium") return 6; // Maps to medium (5-6)
   431→	return 3; // Maps to low (1-4)
   432→}
   433→
   434→// Helper to get sortable severity value from any issue
   435→export function getIssueSeverity(issue: Issue): number {
   436→	if (issue.issueType === "error") {
   437→		return issue.severity;
   438→	}
   439→	return impactToSeverity(issue.impactLevel);
   440→}
   441→
   442→// Parse evaluator result string to extract issues
   443→export function parseEvaluatorResult(resultString: string): Issue[] {
   444→	try {
   445→		// First, try to parse as JSON directly
   446→		const parsed = JSON.parse(resultString);
   447→
   448→		// Handle unified format: {perFileIssues: {...}, crossFileIssues: [...]}
   449→		if (parsed && typeof parsed === "object" && !Array.isArray(parsed)) {
   450→			const allIssues: Issue[] = [];
   451→
   452→			// Extract per-file issues
   453→			if (parsed.perFileIssues && typeof parsed.perFileIssues === "object") {
   454→				for (const fileIssues of Object.values(parsed.perFileIssues)) {
   455→					if (Array.isArray(fileIssues)) {
   456→						allIssues.push(...(fileIssues as Issue[]));
   457→					}
   458→				}
   459→			}
   460→
   461→			// Extract cross-file issues
   462→			if (Array.isArray(parsed.crossFileIssues)) {
   463→				allIssues.push(...parsed.crossFileIssues);
   464→			}
   465→
   466→			return allIssues;
   467→		}
   468→
   469→		// Handle array format directly
   470→		if (Array.isArray(parsed)) {
   471→			return parsed as Issue[];
   472→		}
   473→
   474→		return [];
   475→	} catch {
   476→		// If JSON parse fails, try to find JSON array in the result
   477→		// The result might contain markdown or other text before the JSON array
   478→		try {
   479→			const jsonMatch = resultString.match(/\[[\s\S]*\]/);
   480→			if (!jsonMatch) {
   481→				return [];
   482→			}
   483→
   484→			const issues = JSON.parse(jsonMatch[0]) as Issue[];
   485→			return Array.isArray(issues) ? issues : [];
   486→		} catch {
   487→			return [];
   488→		}
   489→	}
   490→}
   491→
   492→// Format location for display
   493→export function formatLocation(location: Location | Location[]): string {
   494→	if (Array.isArray(location)) {
   495→		return location
   496→			.map((loc) => {
   497→				if (loc.file) {
   498→					return `${loc.file}:${loc.start}-${loc.end}`;
   499→				}
   500→				return `Lines ${loc.start}-${loc.end}`;
   501→			})
   502→			.join(", ");
   503→	} else {
   504→		if (location.file) {
   505→			return `${location.file}:${location.start}-${location.end}`;
   506→		}
   507→		return `Lines ${location.start}-${location.end}`;
   508→	}
   509→}
   510→
   511→// Evaluation history types (for listing past evaluations)
   512→export interface IEvaluationHistoryItem {
   513→	id: string;
