   519→	totalIssues: number;
   520→	highCount: number;
   521→	mediumCount: number;
   522→	lowCount: number;
   523→	curatedCount: number;
   524→	totalCostUsd: number;
   525→	totalDurationMs: number;
   526→	contextScore?: number;
   527→	contextGrade?: ContextScoreGrade;
   528→	failedEvaluatorCount: number;
   529→	isImported?: boolean;
   530→	errorMessage?: string;
   531→	errorCode?: string;
   532→	createdAt: string;
   533→	completedAt: string;
   534→}
   535→
   536→// Full evaluation record (includes result data)
   537→export interface IEvaluationRecord extends IEvaluationHistoryItem {
   538→	result?: EvaluationOutput;
   539→}
   540→
   541→// Issue type helpers
   542→
   543→/**
   544→ * Evaluators categorized as "suggestions" (opportunities for improvement).
   545→ * All other evaluators are categorized as "errors" (problems to fix).
   546→ */
   547→const SUGGESTION_EVALUATORS = new Set([
   548→	"11-subdirectory-coverage",
   549→	"12-context-gaps",
   550→]);
   551→
   552→/**
   553→ * Get the issue type for a given issue, with backward compatibility.
   554→ * If the issue has an explicit issueType, use that.
   555→ * Otherwise, infer from the evaluator name.
   556→ */
   557→export function getIssueType(issue: Issue): "error" | "suggestion" {
   558→	// Use explicit issueType if available
   559→	if (issue.issueType) {
   560→		return issue.issueType;
   561→	}
   562→	// Infer from evaluator name for backward compatibility
   563→	if (issue.evaluatorName && SUGGESTION_EVALUATORS.has(issue.evaluatorName)) {
   564→		return "suggestion";
   565→	}
   566→	return "error";
   567→}
   568→
   569→/**
   570→ * Determines if an issue affects multiple files or has no specific file
   571→ */
   572→export function isCrossFileIssue(issue: Issue): boolean {
   573→	if (issue.affectedFiles && issue.affectedFiles.length > 1) return true;
   574→	if (issue.isMultiFile) return true;
   575→
   576→	if (Array.isArray(issue.location)) {
   577→		const files = issue.location
   578→			.map((loc) => loc.file)
   579→			.filter((f): f is string => f !== undefined);
   580→		if (new Set(files).size > 1) return true;
   581→	}
   582→
   583→	return false;
   584→}
   585→
   586→/**
   587→ * Gets the primary file path for an issue (null for cross-file issues)
   588→ */
   589→export function getIssueFile(issue: Issue): string | null {
   590→	if (isCrossFileIssue(issue)) return null;
   591→
   592→	if (!Array.isArray(issue.location) && issue.location.file) {
   593→		return issue.location.file;
   594→	}
   595→
   596→	if (Array.isArray(issue.location) && issue.location[0]?.file) {
   597→		return issue.location[0].file;
   598→	}
   599→
   600→	if (issue.affectedFiles && issue.affectedFiles.length === 1) {
   601→		return issue.affectedFiles[0];
   602→	}
   603→
   604→	return null;
   605→}
   606→
   607→/**
   608→ * Gets the maximum severity from a group of issues
   609→ */
   610→export function getMaxIssueSeverity(issues: Issue[]): number {
   611→	if (issues.length === 0) return 0;
   612→	return Math.max(...issues.map(getIssueSeverity));
   613→}
   614→
   615→/**
   616→ * Category group for nested display
   617→ */
   618→export interface CategoryGroup {
