} from "../lib/build-context-tree";
import type {
	IContextFile,
	ILinkedDocSummary,
	ISkill,
} from "../types/evaluation";

interface ContextTreeViewProps {
	contextFiles: IContextFile[];
	skills: ISkill[];
	linkedDocs: ILinkedDocSummary[];
	onFileClick: (path: string, fileType: string) => void;
}

/** Folder icon */
const FolderIcon: React.FC<{ className?: string }> = ({
	className = "w-4 h-4",
}) => (
	<svg
		className={className}
		fill="none"
		viewBox="0 0 24 24"
		stroke="currentColor"
	>
		<path
			strokeLinecap="round"
			strokeLinejoin="round"
			strokeWidth={2}
			d="M3 7v10a2 2 0 002 2h14a2 2 0 002-2V9a2 2 0 00-2-2h-6l-2-2H5a2 2 0 00-2 2z"
		/>
	</svg>
);

/** File icon */
const FileIcon: React.FC<{ className?: string }> = ({
	className = "w-4 h-4",
}) => (
	<svg
		className={className}
		fill="none"
		viewBox="0 0 24 24"
		stroke="currentColor"
	>
		<path
			strokeLinecap="round"
			strokeLinejoin="round"
			strokeWidth={2}
			d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"
		/>
	</svg>
);

/** Chevron icon for branch expand/collapse */
const ChevronIcon: React.FC<{ className?: string }> = ({
	className = "w-3 h-3",
}) => (
	<svg
		className={className}
		fill="none"
		viewBox="0 0 24 24"
		stroke="currentColor"
	>
		<path
			strokeLinecap="round"
			strokeLinejoin="round"
			strokeWidth={2}
			d="M9 5l7 7-7 7"
		/>
	</svg>
);

/** Get a badge label for file type */
function getFileTypeBadge(
	fileType?: string,
): { label: string; className: string } | null {
	switch (fileType) {
		case "agents":
			return {
				label: "AGENTS.md",
				className: "bg-indigo-500/20 text-indigo-300",
			};
		case "claude":
			return {
				label: "CLAUDE.md",
				className: "bg-slate-600/40 text-slate-300",
			};
		case "copilot":
			return {
				label: "Copilot",
				className: "bg-slate-600/40 text-slate-300",
			};
		case "rules":
			return {
