  1→import React, { useEffect, useRef, useState } from "react";
  2→
  3→export interface TabItem {
  4→	id: string;
  5→	label: string;
  6→	icon?: React.ReactNode;
  7→	count?: number;
  8→}
  9→
 10→interface TabsProps {
 11→	tabs: TabItem[];
 12→	activeTab: string;
 13→	onTabChange: (tabId: string) => void;
 14→}
 15→
 16→interface TabPanelProps {
 17→	id: string;
 18→	activeTab: string;
 19→	children: React.ReactNode;
 20→}
 21→
 22→export const Tabs: React.FC<TabsProps> = ({ tabs, activeTab, onTabChange }) => {
 23→	const tabsRef = useRef<HTMLDivElement>(null);
 24→	const [indicatorStyle, setIndicatorStyle] = useState<{
 25→		left: number;
 26→		width: number;
 27→	}>({ left: 0, width: 0 });
 28→
 29→	// Update indicator position when active tab changes or resizes
 30→	useEffect(() => {
 31→		if (!tabsRef.current) return;
 32→
 33→		const activeTabElement = tabsRef.current.querySelector(
 34→			`[data-tab-id="${activeTab}"]`,
 35→		) as HTMLButtonElement | null;
 36→
 37→		if (!activeTabElement) return;
 38→
 39→		const updateIndicator = () => {
 40→			if (!tabsRef.current) return;
 41→			const containerRect = tabsRef.current.getBoundingClientRect();
 42→			const tabRect = activeTabElement.getBoundingClientRect();
 43→
 44→			setIndicatorStyle({
 45→				left: tabRect.left - containerRect.left,
 46→				width: tabRect.width,
 47→			});
 48→		};
 49→
 50→		updateIndicator();
 51→
 52→		const observer = new ResizeObserver(updateIndicator);
 53→		observer.observe(activeTabElement);
 54→
 55→		return () => observer.disconnect();
 56→	}, [activeTab]);
 57→
 58→	return (
 59→		<div ref={tabsRef} className="tab-navigation relative" role="tablist">
 60→			{/* Animated indicator */}
 61→			<div
 62→				className="tab-indicator"
 63→				style={{
 64→					left: indicatorStyle.left,
 65→					width: indicatorStyle.width,
 66→					height: "calc(100% - 12px)",
 67→					top: "6px",
 68→				}}
 69→			/>
 70→
 71→			{tabs.map((tab) => {
 72→				const isActive = tab.id === activeTab;
 73→				return (
 74→					<button
 75→						key={tab.id}
 76→						role="tab"
 77→						data-tab-id={tab.id}
 78→						aria-selected={isActive}
 79→						aria-controls={`tabpanel-${tab.id}`}
 80→						className={`tab-button ${isActive ? "tab-button-active" : ""}`}
 81→						onClick={() => onTabChange(tab.id)}
 82→					>
 83→						{tab.icon && <span className="tab-icon">{tab.icon}</span>}
 84→						<span>{tab.label}</span>
 85→						{tab.count !== undefined && (
 86→							<span
 87→								className={`tab-count ${isActive ? "tab-count-active" : ""}`}
 88→							>
 89→								{tab.count}
 90→							</span>
 91→						)}
 92→					</button>
 93→				);
 94→			})}
 95→		</div>
 96→	);
 97→};
 98→
 99→export const TabPanel: React.FC<TabPanelProps> = ({
100→	id,
101→	activeTab,
102→	children,
103→}) => {
104→	if (id !== activeTab) return null;
105→
106→	return (
107→		<div
108→			id={`tabpanel-${id}`}
109→			role="tabpanel"
110→			aria-labelledby={`tab-${id}`}
111→			className="tab-content-enter"
112→		>
113→			{children}
114→		</div>
115→	);
116→};
