  1→import React from "react";
  2→import { useNavigate } from "react-router-dom";
  3→import { useFeatureFlags } from "../contexts/FeatureFlagContext";
  4→import { useActiveJobs } from "../hooks/useActiveJobs";
  5→import { useEvaluationHistory } from "../hooks/useEvaluationHistory";
  6→import { useVersion } from "../hooks/useVersion";
  7→import {
  8→	extractRepoName,
  9→	formatDuration,
 10→	formatRelativeDate,
 11→	getGradeBadgeClass,
 12→} from "../lib/formatters";
 13→import type { IEvaluationHistoryItem } from "../types/evaluation";
 14→import { ActiveJobCard } from "./ActiveJobCard";
 15→import { AppHeader } from "./AppHeader";
 16→
 17→export function RecentEvaluationsPage() {
 18→	const navigate = useNavigate();
 19→	const { cloudMode } = useFeatureFlags();
 20→	const { version } = useVersion();
 21→	const {
 22→		history,
 23→		isLoading: isLoadingHistory,
 24→		deleteEvaluation,
 25→		clearAllEvaluations,
 26→		refresh: refreshHistory,
 27→	} = useEvaluationHistory();
 28→	const {
 29→		activeJobs,
 30→		isLoading: isLoadingActive,
 31→		refresh: refreshActive,
 32→	} = useActiveJobs();
 33→	const [searchQuery, setSearchQuery] = React.useState("");
 34→	const [sortField, setSortField] = React.useState<
 35→		"date" | "name" | "grade" | "errors"
 36→	>("date");
 37→	const [sortDirection, setSortDirection] = React.useState<"asc" | "desc">(
 38→		"desc",
 39→	);
 40→
 41→	// Combined loading state
 42→	const isLoading = isLoadingHistory || isLoadingActive;
 43→
 44→	// Combined refresh function
 45→	const refresh = async () => {
 46→		await Promise.all([refreshHistory(), refreshActive()]);
 47→	};
 48→
 49→	// Filter history based on search query
 50→	const filteredHistory = history.filter((item) => {
 51→		if (!searchQuery.trim()) return true;
 52→		const repoName = extractRepoName(item.repositoryUrl).toLowerCase();
 53→		return repoName.includes(searchQuery.toLowerCase());
 54→	});
 55→
 56→	const handleSort = (field: "date" | "name" | "grade" | "errors") => {
 57→		if (sortField === field) {
 58→			setSortDirection((d) => (d === "asc" ? "desc" : "asc"));
 59→		} else {
 60→			setSortField(field);
 61→			const defaults: Record<string, "asc" | "desc"> = {
 62→				date: "desc",
 63→				name: "asc",
 64→				grade: "desc",
 65→				errors: "desc",
 66→			};
 67→			setSortDirection(defaults[field]);
 68→		}
 69→	};
 70→
 71→	const sortedHistory = React.useMemo(() => {
 72→		const sorted = [...filteredHistory].sort((a, b) => {
 73→			const dir = sortDirection === "asc" ? 1 : -1;
 74→
 75→			if (sortField === "date") {
 76→				return (
 77→					dir *
 78→					(new Date(a.completedAt).getTime() -
 79→						new Date(b.completedAt).getTime())
 80→				);
 81→			}
 82→
 83→			if (sortField === "name") {
 84→				const nameA = extractRepoName(a.repositoryUrl).toLowerCase();
 85→				const nameB = extractRepoName(b.repositoryUrl).toLowerCase();
 86→				const cmp = nameA.localeCompare(nameB);
 87→				if (cmp !== 0) return dir * cmp;
 88→				// Secondary sort: newest first
 89→				return (
 90→					new Date(b.completedAt).getTime() - new Date(a.completedAt).getTime()
 91→				);
 92→			}
 93→
 94→			if (sortField === "grade") {
 95→				// Failed evaluations and null scores always sort last
 96→				const scoreA =
 97→					a.status === "failed" || a.contextScore == null
 98→						? null
 99→						: a.contextScore;
100→				const scoreB =
101→					b.status === "failed" || b.contextScore == null
102→						? null
103→						: b.contextScore;
104→				if (scoreA == null && scoreB == null)
105→					return (
106→						new Date(b.completedAt).getTime() -
107→						new Date(a.completedAt).getTime()
108→					);
109→				if (scoreA == null) return 1;
110→				if (scoreB == null) return -1;
111→				const cmp = scoreA - scoreB;
112→				if (cmp !== 0) return dir * cmp;
113→				return (
114→					new Date(b.completedAt).getTime() - new Date(a.completedAt).getTime()
115→				);
116→			}
117→
118→			// errors
119→			const isFailed = (item: IEvaluationHistoryItem) =>
120→				item.status === "failed";
121→			if (isFailed(a) && isFailed(b))
122→				return (
123→					new Date(b.completedAt).getTime() - new Date(a.completedAt).getTime()
124→				);
125→			if (isFailed(a)) return 1;
126→			if (isFailed(b)) return -1;
127→			const cmp = a.totalIssues - b.totalIssues;
128→			if (cmp !== 0) return dir * cmp;
129→			return (
130→				new Date(b.completedAt).getTime() - new Date(a.completedAt).getTime()
131→			);
132→		});
133→		return sorted;
134→	}, [filteredHistory, sortField, sortDirection]);
135→
136→	const handleSelect = (item: IEvaluationHistoryItem) => {
137→		if (item.status === "completed") {
138→			navigate(`/evaluation/${item.id}?tab=summary`);
139→		}
140→	};
141→
142→	return (
143→		<div className="min-h-screen bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 text-slate-100 animate-fade-in">
144→			{/* Header */}
145→			<AppHeader
146→				currentPage="recent"
147→				logoNavigatesHome={true}
148→				historyCount={history.length}
149→			/>
150→
151→			{/* Main Content */}
152→			<main className="max-w-[1400px] mx-auto px-6 py-6">
153→				<div className="glass-card">
154→					{/* Header */}
155→					<div className="flex items-center justify-between px-6 py-4 border-b border-slate-700">
156→						<div className="flex items-center gap-3">
157→							<svg
158→								className="w-6 h-6 text-slate-300"
159→								fill="none"
160→								stroke="currentColor"
161→								viewBox="0 0 24 24"
162→							>
163→								<path
164→									strokeLinecap="round"
165→									strokeLinejoin="round"
166→									strokeWidth={2}
167→									d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"
168→								/>
169→							</svg>
170→							<h2 className="text-xl font-semibold text-slate-100">
171→								Latest Evaluations
172→							</h2>
173→							{history.length > 0 && (
174→								<span className="bg-indigo-600 text-white text-sm font-medium px-2.5 py-0.5 rounded-full">
175→									{history.length}
176→								</span>
177→							)}
178→						</div>
179→						<div className="flex items-center gap-3">
180→							{/* Search input */}
181→							{history.length > 0 && (
182→								<div className="flex items-center bg-slate-700/50 border border-slate-600 rounded-lg focus-within:ring-2 focus-within:ring-indigo-500 focus-within:border-transparent">
183→									<svg
184→										className="w-4 h-4 text-slate-400 ml-3 flex-shrink-0"
185→										fill="none"
186→										stroke="currentColor"
187→										viewBox="0 0 24 24"
188→									>
189→										<path
190→											strokeLinecap="round"
191→											strokeLinejoin="round"
192→											strokeWidth={2}
193→											d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
194→										/>
195→									</svg>
196→									<input
197→										type="text"
198→										placeholder="Search repositories..."
199→										value={searchQuery}
200→										onChange={(e) => setSearchQuery(e.target.value)}
201→										className="pl-2 pr-3 py-1.5 bg-transparent text-sm text-slate-200 placeholder-slate-500 focus:outline-none w-56"
202→									/>
203→								</div>
204→							)}
205→							{/* Sort controls */}
206→							{history.length > 0 && (
207→								<div className="flex items-center gap-1">
208→									{(
209→										[
210→											{ field: "date", label: "Date" },
211→											{ field: "name", label: "Name" },
212→											{ field: "grade", label: "Grade" },
213→											{ field: "errors", label: "Errors" },
214→										] as const
215→									).map(({ field, label }) => (
216→										<button
217→											key={field}
218→											onClick={() => handleSort(field)}
219→											className={`px-2.5 py-1 text-xs font-medium rounded-md transition-colors ${
220→												sortField === field
221→													? "bg-indigo-600/80 text-white"
222→													: "text-slate-400 hover:text-slate-200 hover:bg-slate-700"
223→											}`}
224→										>
225→											{label}
226→											{sortField === field && (
227→												<span className="ml-1">
228→													{sortDirection === "asc" ? "↑" : "↓"}
229→												</span>
230→											)}
231→										</button>
232→									))}
233→								</div>
234→							)}
235→							{!cloudMode && history.length > 0 && (
236→								<button
237→									onClick={() => {
238→										if (
239→											confirm(
240→												`Delete all ${history.length} evaluation${history.length > 1 ? "s" : ""} from history?`,
241→											)
242→										) {
243→											clearAllEvaluations();
244→										}
245→									}}
246→									className="px-3 py-1.5 text-sm text-slate-400 hover:text-red-300 hover:bg-slate-700 rounded-lg transition-colors"
247→								>
248→									Clear All
249→								</button>
250→							)}
251→							<button
252→								onClick={refresh}
253→								className="p-2 text-slate-400 hover:text-slate-200 hover:bg-slate-700 rounded-lg transition-colors"
254→								title="Refresh"
255→							>
256→								<svg
257→									className={`w-5 h-5 ${isLoading ? "animate-spin" : ""}`}
258→									fill="none"
259→									stroke="currentColor"
260→									viewBox="0 0 24 24"
261→								>
262→									<path
263→										strokeLinecap="round"
264→										strokeLinejoin="round"
265→										strokeWidth={2}
266→										d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"
267→									/>
268→								</svg>
269→							</button>
270→						</div>
271→					</div>
272→
273→					{/* Content */}
274→					<div className="min-h-[400px]">
275→						{/* In Progress Section */}
276→						{activeJobs.length > 0 && (
277→							<div className="border-b border-slate-700/50">
278→								{/* Section header */}
279→								<div className="flex items-center gap-3 px-6 py-3 bg-slate-800/50">
280→									<svg
281→										className="w-4 h-4 text-indigo-400"
282→										fill="none"
283→										stroke="currentColor"
284→										viewBox="0 0 24 24"
285→									>
286→										<path
287→											strokeLinecap="round"
288→											strokeLinejoin="round"
289→											strokeWidth={2}
290→											d="M13 10V3L4 14h7v7l9-11h-7z"
291→										/>
292→									</svg>
293→									<span className="text-sm font-semibold text-slate-300 uppercase tracking-wide">
294→										In Progress
295→									</span>
296→									<span className="bg-indigo-600 text-white text-xs font-medium px-2 py-0.5 rounded-full">
297→										{activeJobs.length}
298→									</span>
299→								</div>
300→								{/* Active job list */}
301→								<div className="divide-y divide-slate-700/30">
302→									{activeJobs.map((job) => (
303→										<ActiveJobCard
304→											key={job.jobId}
305→											jobId={job.jobId}
306→											status={job.status}
307→											repositoryUrl={job.repositoryUrl}
308→											createdAt={job.createdAt}
309→											startedAt={job.startedAt}
310→											progress={job.progress}
311→										/>
312→									))}
313→								</div>
314→							</div>
315→						)}
316→
317→						{/* Loading state */}
318→						{isLoading && history.length === 0 && activeJobs.length === 0 ? (
319→							<div className="flex items-center justify-center py-16 text-slate-400">
320→								<svg
321→									className="w-6 h-6 animate-spin mr-3"
322→									fill="none"
323→									viewBox="0 0 24 24"
324→								>
325→									<circle
326→										className="opacity-25"
327→										cx="12"
328→										cy="12"
329→										r="10"
330→										stroke="currentColor"
331→										strokeWidth="4"
332→									/>
333→									<path
334→										className="opacity-75"
335→										fill="currentColor"
336→										d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4z"
337→									/>
338→								</svg>
339→								Loading evaluations...
340→							</div>
341→						) : history.length === 0 && activeJobs.length === 0 ? (
342→							<div className="py-16 text-center text-slate-400">
343→								<svg
344→									className="w-16 h-16 mx-auto mb-4 opacity-50"
345→									fill="none"
346→									stroke="currentColor"
347→									viewBox="0 0 24 24"
348→								>
349→									<path
350→										strokeLinecap="round"
351→										strokeLinejoin="round"
352→										strokeWidth={1.5}
353→										d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10"
354→									/>
355→								</svg>
356→								<p className="text-lg">No evaluations yet</p>
357→								<p className="text-sm mt-2 text-slate-500">
358→									Run an evaluation to see it here
359→								</p>
360→								<button
361→									onClick={() => navigate("/")}
362→									className="btn-primary mt-6 px-6 py-2"
363→								>
364→									Start New Evaluation
365→								</button>
366→							</div>
367→						) : sortedHistory.length === 0 && searchQuery ? (
368→							<div className="py-16 text-center text-slate-400">
369→								<svg
370→									className="w-16 h-16 mx-auto mb-4 opacity-50"
371→									fill="none"
372→									stroke="currentColor"
373→									viewBox="0 0 24 24"
374→								>
375→									<path
376→										strokeLinecap="round"
377→										strokeLinejoin="round"
378→										strokeWidth={1.5}
379→										d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
380→									/>
381→								</svg>
382→								<p className="text-lg">No evaluations match "{searchQuery}"</p>
383→								<p className="text-sm mt-2 text-slate-500">
384→									Try a different search term
385→								</p>
386→								<button
387→									onClick={() => setSearchQuery("")}
388→									className="mt-4 px-4 py-2 text-sm text-slate-300 hover:text-slate-100 hover:bg-slate-700 rounded-lg transition-colors"
389→								>
390→									Clear search
391→								</button>
392→							</div>
393→						) : (
394→							<>
395→								{/* History section header - only show when there are active jobs */}
396→								{activeJobs.length > 0 && sortedHistory.length > 0 && (
397→									<div className="flex items-center gap-3 px-6 py-3 bg-slate-800/30 border-b border-slate-700/50">
398→										<svg
399→											className="w-4 h-4 text-slate-400"
400→											fill="none"
401→											stroke="currentColor"
402→											viewBox="0 0 24 24"
403→										>
404→											<path
405→												strokeLinecap="round"
406→												strokeLinejoin="round"
407→												strokeWidth={2}
408→												d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"
409→											/>
410→										</svg>
411→										<span className="text-sm font-semibold text-slate-400 uppercase tracking-wide">
412→											History
413→										</span>
414→									</div>
415→								)}
416→								<ul className="divide-y divide-slate-700/50">
417→									{sortedHistory.map((item) => (
418→										<li key={item.id}>
419→											<div
420→												role="button"
421→												tabIndex={0}
422→												onClick={() => handleSelect(item)}
423→												onKeyDown={(e) => {
424→													if (e.key === "Enter" || e.key === " ") {
425→														e.preventDefault();
426→														handleSelect(item);
427→													}
428→												}}
429→												className="w-full px-6 py-4 text-left hover:bg-slate-700/30 transition-colors group cursor-pointer"
430→											>
431→												<div className="flex items-center justify-between gap-4">
432→													{/* Left side: Status + Repo name */}
433→													<div className="flex-1 min-w-0">
434→														<div className="flex items-center gap-3">
435→															{/* Status indicator */}
436→															{item.status === "completed" ? (
437→																<span
438→																	className="w-3 h-3 bg-green-500 rounded-full flex-shrink-0"
439→																	title="Completed"
440→																/>
441→															) : (
442→																<span
443→																	className="w-3 h-3 bg-red-500 rounded-full flex-shrink-0"
444→																	title="Failed"
445→																/>
446→															)}
447→															{/* Repo name */}
448→															<span className="text-lg font-medium text-slate-100 truncate">
449→																{extractRepoName(item.repositoryUrl)}
450→															</span>
451→														</div>
452→
453→														{/* Metadata row */}
454→														<div className="flex items-center gap-4 mt-2 text-sm text-slate-400">
455→															<span>
456→																{formatRelativeDate(item.completedAt)}
457→															</span>
458→															{item.status === "completed" && (
459→																<>
460→																	<span className="text-slate-600">|</span>
461→																	<span>{item.totalFiles} files</span>
462→																	<span className="text-slate-600">|</span>
463→																	<span>
464→																		{formatDuration(item.totalDurationMs)}
465→																	</span>
466→																</>
467→															)}
468→														</div>
469→													</div>
470→
471→													{/* Right side: Issue counts or error */}
472→													<div className="flex items-center gap-4">
473→														{item.status === "completed" ? (
474→															<div className="flex items-center gap-2">
