 21→export const AssessmentPage: React.FC = () => {
 22→	const [loading, setLoading] = useState(true);
 23→	const [error, setError] = useState<string | null>(null);
 24→	const [aggregate, setAggregate] = useState<IEvaluatorFeedback[]>([]);
 25→	const { getAggregateFeedback } = useFeedbackApi();
 26→	const { history } = useEvaluationHistory();
 27→
 28→	const loadAggregate = useCallback(async () => {
 29→		try {
 30→			setLoading(true);
 31→			setError(null);
 32→			const data = await getAggregateFeedback();
 33→			setAggregate(data);
 34→		} catch (err) {
 35→			console.error("Failed to load aggregate feedback:", err);
 36→			setError("Failed to load feedback data");
 37→		} finally {
 38→			setLoading(false);
 39→		}
 40→	}, [getAggregateFeedback]);
 41→
 42→	useEffect(() => {
 43→		loadAggregate();
 44→	}, [loadAggregate]);
 45→
 46→	if (loading) {
 47→		return (
 48→			<div className="min-h-screen bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 text-slate-100">
