 748→	// Load evaluation from URL on mount or when URL changes
 749→	// Handles both active jobs (reconnect SSE) and completed evaluations (load from database)
 750→	// biome-ignore lint/correctness/useExhaustiveDependencies: handleFileLoad is intentionally excluded - we want the latest version but don't want it to trigger re-runs
 751→	useEffect(() => {
 752→		if (!urlEvaluationId) return;
 753→		if (evaluationData && evaluationMode === "completed") return; // Already loaded
 754→		if (evaluationMode === "evaluating") return; // Currently evaluating
 755→
 756→		const loadEvaluationById = async () => {
 757→			setIsLoadingFromUrl(true);
 758→			setApiError(null);
 759→
 760→			try {
 761→				// Step 1: Check if it's an active job
 762→				const jobStatus = await api.getJobStatus(urlEvaluationId);
 763→
 764→				if (jobStatus.status === "completed" && jobStatus.result) {
 765→					// Job completed, result in memory - use it directly
 766→					handleFileLoad(jobStatus.result);
 767→					setCurrentRepositoryUrl(jobStatus.repositoryUrl || null);
 768→					setEvaluationMode("completed");
 769→					// Load feedback, bookmarks, and selections
 770→					try {
 771→						const feedback =
 772→							await feedbackApi.getFeedbackForEvaluation(urlEvaluationId);
 773→						setFeedbackMap(feedback);
 774→						if (!cloudMode) {
 775→							const bookmarks =
 776→								await bookmarkApi.getBookmarksForEvaluation(urlEvaluationId);
 777→							setBookmarkSet(bookmarks);
 778→						}
 779→						const selections =
 780→							await selectionApi.getSelectionsForEvaluation(urlEvaluationId);
 781→						setSelectedIssueKeys(selections);
 782→					} catch (err) {
 783→						console.error("Failed to load metadata:", err);
 784→					}
 785→					setIsLoadingFromUrl(false);
 786→					return;
 787→				}
 788→
 789→				if (jobStatus.status === "failed") {
 790→					setApiError(jobStatus.error?.message || "Evaluation failed");
 791→					setIsLoadingFromUrl(false);
 792→					return;
 793→				}
 794→
 795→				// Active job (running or queued) - reconnect SSE
 796→				if (jobStatus.status === "running" || jobStatus.status === "queued") {
 797→					setCurrentJobId(urlEvaluationId);
 798→					const sseUrlValue = jobStatus.sseUrl?.startsWith("/")
 799→						? `${window.location.origin}${jobStatus.sseUrl}`
 800→						: jobStatus.sseUrl || null;
 801→					setSSEUrl(sseUrlValue);
 802→					setCurrentRepositoryUrl(jobStatus.repositoryUrl || null);
 803→					setEvaluationMode("evaluating");
 804→
 805→					// Convert server logs to frontend format, or use default message
 806→					const historicalLogs = jobStatus.logs?.length
 807→						? jobStatus.logs.map((log) => ({
 808→								timestamp: new Date(log.timestamp),
 809→								type: log.type,
 810→								message: log.message,
 811→							}))
 812→						: [
 813→								{
 814→									timestamp: new Date(),
 815→									type: "info" as const,
 816→									message: "Reconnected to evaluation in progress",
 817→								},
 818→							];
 819→
 820→					// Calculate progress percentage
 821→					let percentage = 0;
 822→					if (jobStatus.progress?.totalEvaluators) {
 823→						percentage = Math.round(
 824→							((jobStatus.progress.completedEvaluators || 0) /
 825→								jobStatus.progress.totalEvaluators) *
 826→								100,
 827→						);
 828→					}
 829→
 830→					// Initialize progress state from job with historical logs
 831→					setProgressState({
 832→						status: jobStatus.status,
 833→						repositoryUrl: jobStatus.repositoryUrl || "Unknown",
 834→						totalFiles: jobStatus.progress?.totalFiles || 0,
 835→						completedFiles: jobStatus.progress?.completedFiles || 0,
 836→						totalEvaluators: jobStatus.progress?.totalEvaluators || 0,
 837→						completedEvaluators: jobStatus.progress?.completedEvaluators || 0,
 838→						currentEvaluator: jobStatus.progress?.currentEvaluator,
 839→						percentage,
 840→						startTime: new Date(jobStatus.startedAt || jobStatus.createdAt),
 841→						logs: historicalLogs,
 842→					});
 843→					setIsLoadingFromUrl(false);
 844→					return;
 845→				}
 846→			} catch {
 847→				// Not an active job - try database
 848→				console.log("[App] Not an active job, trying database");
 849→			}
 850→
 851→			// Step 2: Try loading from database
 852→			try {
 853→				const record = await loadEvaluationRef.current(urlEvaluationId);
 854→				if (record?.result) {
 855→					handleFileLoad(record.result);
 856→					setCurrentRepositoryUrl(record.repositoryUrl);
 857→					setEvaluationMode("completed");
 858→					// Load feedback, bookmarks, and selections
 859→					try {
 860→						const feedback =
 861→							await feedbackApi.getFeedbackForEvaluation(urlEvaluationId);
 862→						setFeedbackMap(feedback);
 863→						if (!cloudMode) {
 864→							const bookmarks =
 865→								await bookmarkApi.getBookmarksForEvaluation(urlEvaluationId);
 866→							setBookmarkSet(bookmarks);
 867→						}
 868→						const selections =
 869→							await selectionApi.getSelectionsForEvaluation(urlEvaluationId);
 870→						setSelectedIssueKeys(selections);
 871→					} catch (err) {
 872→						console.error("Failed to load metadata:", err);
 873→					}
 874→				} else {
 875→					setApiError("Evaluation not found");
 876→				}
 877→			} catch (err) {
 878→				setApiError(
 879→					err instanceof Error ? err.message : "Failed to load evaluation",
 880→				);
 881→			} finally {
 882→				setIsLoadingFromUrl(false);
 883→			}
 884→		};
 885→
 886→		loadEvaluationById();
 887→	}, [
 888→		urlEvaluationId,
 889→		evaluationData,
 890→		evaluationMode,
 891→		api,
 892→		feedbackApi,
 893→		bookmarkApi,
 894→		selectionApi,
 895→		cloudMode,
 896→	]);
