 898→	// Parse all issues from evaluation data
 899→	const allIssues = useMemo(() => {
 900→		if (!evaluationData) return [];
 901→
 902→		const issues: Array<Issue & { evaluatorName?: string }> = [];
 903→
 904→		if (isUnifiedFormat(evaluationData)) {
 905→			// Unified format: results array
 906→			const unifiedData = evaluationData as UnifiedEvaluationOutput;
 907→			for (const result of unifiedData.results) {
 908→				if (result.output && result.output.result) {
 909→					const parsedIssues = parseEvaluatorResult(result.output.result);
 910→					parsedIssues.forEach((issue) => {
 911→						issues.push({ ...issue, evaluatorName: result.evaluator });
 912→					});
 913→				}
 914→			}
 915→
 916→			// Add top-level cross-file issues for unified format
 917→			if (unifiedData.crossFileIssues) {
 918→				issues.push(
 919→					...unifiedData.crossFileIssues.map((issue) => ({
 920→						...issue,
 921→						evaluatorName: "cross-file",
 922→					})),
 923→				);
 924→			}
 925→		} else if (isIndependentFormat(evaluationData)) {
 926→			// Independent format: files object
 927→			const independentData = evaluationData as IndependentEvaluationOutput;
 928→			for (const [_filePath, fileResult] of Object.entries(
 929→				independentData.files,
 930→			)) {
 931→				for (const evaluation of fileResult.evaluations) {
 932→					const evalWithIssues = evaluation as {
 933→						issues?: Issue[];
 934→						evaluator: string;
 935→						output?: { result: string };
 936→					};
 937→					// Handle both formats:
 938→					// 1. Backend API format: issues array directly on evaluation
 939→					// 2. JSON file format: output.result as a string to parse
 940→					if ("issues" in evaluation && Array.isArray(evalWithIssues.issues)) {
 941→						// Backend API format - issues are already parsed
 942→						const evalIssues = evalWithIssues.issues;
 943→						evalIssues.forEach((issue) => {
 944→							issues.push({ ...issue, evaluatorName: evaluation.evaluator });
 945→						});
 946→					} else if (evaluation.output && evaluation.output.result) {
 947→						// JSON file format - need to parse result string
 948→						const parsedIssues = parseEvaluatorResult(evaluation.output.result);
 949→						parsedIssues.forEach((issue) => {
 950→							issues.push({ ...issue, evaluatorName: evaluation.evaluator });
 951→						});
 952→					}
 953→				}
 954→			}
 955→
 956→			// Add cross-file issues
