2094→// Scrolls to top on every route change
2095→function ScrollToTop() {
2096→	const { pathname } = useLocation();
2097→	// biome-ignore lint/correctness/useExhaustiveDependencies: re-run scroll on route change
2098→	useEffect(() => {
2099→		window.scrollTo(0, 0);
2100→	}, [pathname]);
2101→	return null;
2102→}
2103→
2104→// Router wrapper component
2105→function App() {
2106→	return (
2107→		<FeatureFlagProvider>
2108→			<BrowserRouter>
2109→				<ScrollToTop />
2110→				<AppRoutes />
2111→			</BrowserRouter>
2112→		</FeatureFlagProvider>
2113→	);
