				) : isLoadingFromUrl ? (
					<div className="card text-center py-12">
						<div className="animate-spin w-8 h-8 border-2 border-purple-500 border-t-transparent rounded-full mx-auto mb-4"></div>
						<p className="text-slate-400">Loading evaluation...</p>
