				const newSet = new Set(prev);
				for (const issue of issues) {
					const globalIndex = allIssues.indexOf(issue);
					if (globalIndex >= 0) {
						const key = generateIssueKey(issue, globalIndex);
						if (!newSet.has(key)) {
							keysToAdd.push(key);
						}
						newSet.add(key);
					}
				}
				return newSet;
