  1import { useCallback, useEffect, useMemo, useRef, useState } from "react";
  2import type {
  3	ProviderName,
  4	RemediationPromptsResponse,
  5	TargetAgent,
  6} from "../hooks/useEvaluationApi";
  7import { useEvaluationApi } from "../hooks/useEvaluationApi";
  8import {
  9	type ProviderStatus,
 10	useProviderDetection,
 11} from "../hooks/useProviderDetection";
 12import { useSSE } from "../hooks/useSSE";
 13import type { EvaluationOutput, Issue } from "../types/evaluation";
 14import {
 15	getImpactBadgeClass,
 16	getImpactLabel,
 17	getIssueSeverity,
 18	getIssueType,
 19	getSeverityColor,
 20	getSeverityLevel,
 21} from "../types/evaluation";
 22import type {
 23	IRemediationProgressState,
 24	RemediationHistoryItem,
 25	RemediationResult,
 26} from "../types/remediation";
 27import {
 28	countPackmindArtifacts,
 29	formatArtifactCount,
 30} from "../utils/packmind-artifacts";
 31import { AGENT_GENERATED_FILES, agentLogoMap } from "./AgentLogos";
 32import { RemediationHistory } from "./RemediationHistory";
 33import { RemediationProgress } from "./RemediationProgress";
 34import { CopyButton } from "./shared/CopyButton";
 35import { Modal } from "./shared/Modal";
 36import { PackmindLogo } from "./shared/PackmindLogo";
 37import { PackmindProductTourModal } from "./shared/PackmindProductTourModal";
 38
 39type Phase = "idle" | "progress";
 40
 41interface RemediateTabProps {
 42	evaluationId: string | null;
 43	evaluationData: EvaluationOutput | null;
 44	selectedIssueKeys: Set<string>;
 45	issueKeyMap: Map<string, Issue>;
 46	onRemoveIssue: (key: string) => void;
 47	onClearAll: () => void;
 48	cloudMode?: boolean;
 49	repositoryUrl?: string | null;
 50}
 51
 52export function RemediateTab({
 53	evaluationId,
 54	evaluationData,
 55	selectedIssueKeys,
 56	issueKeyMap,
 57	onRemoveIssue,
 58	onClearAll,
 59	cloudMode = false,
 60	repositoryUrl,
 61}: RemediateTabProps) {
 62	const api = useEvaluationApi();
 63	const providerDetection = useProviderDetection();
 64
 65	const [showTourModal, setShowTourModal] = useState(false);
 66
 67	// Phase: idle (config visible) or progress (SSE running)
 68	const [phase, setPhase] = useState<Phase>("idle");
 69	const [targetAgent, setTargetAgent] = useState<TargetAgent>("agents-md");
 70	const [selectedProvider, setSelectedProvider] =
 71		useState<ProviderName>("claude");
 72	const [isExecuting, setIsExecuting] = useState(false);
 73	const [showConfirmModal, setShowConfirmModal] = useState(false);
 74	const [executeError, setExecuteError] = useState<string | null>(null);
 75
 76	// History state
 77	const [remediations, setRemediations] = useState<RemediationHistoryItem[]>(
 78		[],
 79	);
 80
 81	// SSE state
 82	const [sseUrl, setSseUrl] = useState<string | null>(null);
 83	const [, setActiveRemediationId] = useState<string | null>(null);
 84	const activeRemediationIdRef = useRef<string | null>(null);
 85	const [autoExpandRemediationId, setAutoExpandRemediationId] = useState<
 86		string | null
 87	>(null);
 88	const [progressState, setProgressState] = useState<IRemediationProgressState>(
 89		{
 90			status: "queued",
 91			logs: [],
 92		},
 93	);
 94
 95	// Legacy prompt generation state
 96	const [isGenerating, setIsGenerating] = useState(false);
 97	const [generateError, setGenerateError] = useState<string | null>(null);
 98	const [prompts, setPrompts] = useState<RemediationPromptsResponse | null>(
 99		null,
100	);
101
102	// Detect providers on mount
103	useEffect(() => {
104		if (providerDetection.status === "idle") {
105			providerDetection.detectProviders();
106		}
107	}, [providerDetection]);
108
109	// Load remediation history on mount / evaluationId change
110	const refreshHistory = useCallback(async () => {
111		if (!evaluationId) return;
112		const data = await api.getRemediationsForEvaluation(evaluationId);
113		setRemediations(data.remediations);
114		return data;
115	}, [evaluationId, api]);
116
117	useEffect(() => {
118		if (!evaluationId) return;
119
120		let cancelled = false;
121
122		async function loadRemediations() {
123			const data = await api.getRemediationsForEvaluation(evaluationId!);
124			if (cancelled) return;
125
126			setRemediations(data.remediations);
127
128			// If there's an active job, connect SSE
129			if (data.activeJob) {
130				activeRemediationIdRef.current = data.activeJob.id;
131				setActiveRemediationId(data.activeJob.id);
132				setSseUrl(
133					`${window.location.origin}/api/remediation/${data.activeJob.id}/progress`,
134				);
135				setProgressState({
136					status: data.activeJob.status as "queued" | "running",
137					logs: [],
138				});
139				setPhase("progress");
140			}
141		}
142
143		loadRemediations();
144
145		return () => {
146			cancelled = true;
147		};
148	}, [evaluationId, api]);
149
150	// Available providers for the selector
151	const availableProviders = useMemo(() => {
152		const providers: ProviderStatus[] = [];
153		for (const p of providerDetection.providers.values()) {
154			if (p.available && p.name !== "random") {
155				providers.push(p);
156			}
157		}
158		return providers;
159	}, [providerDetection.providers]);
160
161	// Aggregate Packmind artifact totals across all remediations
162	const packmindTotals = useMemo(() => {
163		let standards = 0;
164		let skills = 0;
165		for (const r of remediations) {
166			const allActions = [
167				...(r.summary?.errorFixActions ?? []),
168				...(r.summary?.suggestionEnrichActions ?? []),
169			];
170			const counts = countPackmindArtifacts(allActions);
171			standards += counts.standards;
172			skills += counts.skills;
173		}
174		return { standards, skills };
175	}, [remediations]);
176
177	// Resolve selected issue keys to actual issues, split by type
178	const { errorEntries, suggestionEntries } = useMemo(() => {
179		const errors: { key: string; issue: Issue }[] = [];
180		const suggestions: { key: string; issue: Issue }[] = [];
181
182		for (const key of selectedIssueKeys) {
183			const issue = issueKeyMap.get(key);
184			if (!issue) continue;
185			const type = getIssueType(issue);
186			if (type === "error") {
187				errors.push({ key, issue });
188			} else {
189				suggestions.push({ key, issue });
190			}
191		}
192
193		errors.sort(
194			(a, b) => getIssueSeverity(b.issue) - getIssueSeverity(a.issue),
195		);
196		const impactOrder: Record<string, number> = {
197			High: 0,
198			Medium: 1,
199			Low: 2,
200		};
201		suggestions.sort(
202			(a, b) =>
203				(impactOrder[a.issue.impactLevel ?? "Low"] ?? 2) -
204				(impactOrder[b.issue.impactLevel ?? "Low"] ?? 2),
205		);
206
207		return { errorEntries: errors, suggestionEntries: suggestions };
208	}, [selectedIssueKeys, issueKeyMap]);
209
210	const totalSelected = errorEntries.length + suggestionEntries.length;
211
212	// SSE message handler
213	const handleSSEMessage = useCallback(
214		// biome-ignore lint/suspicious/noExplicitAny: SSE event data varies by type
215		(event: any) => {
216			const timestamp = new Date().toISOString();
217
218			switch (event.type) {
219				case "remediation.started":
220					setProgressState((prev) => ({
221						...prev,
222						status: "running",
223						logs: [...prev.logs, { timestamp, message: "Remediation started" }],
224					}));
225					break;
226
227				case "remediation.progress": {
228					const d = event.data ?? {};
229					setProgressState((prev) => ({
230						...prev,
231						...(d.errorCount !== undefined && {
232							errorCount: d.errorCount as number,
233						}),
234						...(d.suggestionCount !== undefined && {
235							suggestionCount: d.suggestionCount as number,
236						}),
237						...(d.totalBatches !== undefined && {
238							totalBatches: d.totalBatches as number,
239						}),
240						...(d.completedBatches !== undefined && {
241							completedBatches: d.completedBatches as number,
242						}),
243						...(d.phase !== undefined && {
244							currentPhase: d.phase as "errors" | "suggestions",
245						}),
246						...(d.runningTotalCostUsd !== undefined && {
247							runningTotalCostUsd: d.runningTotalCostUsd as number,
248						}),
249						...(d.runningTotalDurationMs !== undefined && {
250							runningTotalDurationMs: d.runningTotalDurationMs as number,
251						}),
252						...(d.runningTotalInputTokens !== undefined && {
253							runningTotalInputTokens: d.runningTotalInputTokens as number,
254						}),
255						...(d.runningTotalOutputTokens !== undefined && {
256							runningTotalOutputTokens: d.runningTotalOutputTokens as number,
257						}),
258					}));
259					break;
260				}
261
262				case "remediation.step.started": {
263					const bi = event.data?.batchInfo as
264						| { batchNumber: number; totalBatches: number }
265						| undefined;
266					const batchLabel = bi
267						? ` (batch ${bi.batchNumber}/${bi.totalBatches})`
268						: "";
269					const issueSummaries = event.data?.issuesSummary as
270						| string[]
271						| undefined;
272					setProgressState((prev) => ({
273						...prev,
274						currentStep: event.data?.step,
275						batchInfo: bi,
276						...(issueSummaries && {
277							currentBatchIssues: issueSummaries,
278						}),
279						logs: [
280							...prev.logs,
281							{
282								timestamp,
283								message: `Step started: ${event.data?.step}${batchLabel}`,
284							},
285						],
286					}));
287					break;
288				}
289
290				case "remediation.step.completed": {
291					const bi2 = event.data?.batchInfo as
292						| { batchNumber: number; totalBatches: number }
293						| undefined;
294					const batchLabel2 = bi2
295						? ` (batch ${bi2.batchNumber}/${bi2.totalBatches})`
296						: "";
297					const step = event.data?.step as string | undefined;
298					const batchDurationMs = event.data?.batchDurationMs as
299						| number
300						| undefined;
301					const batchCostUsd = event.data?.batchCostUsd as number | undefined;
302					const batchInputTokens = event.data?.batchInputTokens as
303						| number
304						| undefined;
305					const batchOutputTokens = event.data?.batchOutputTokens as
306						| number
307						| undefined;
308
309					const hasBatchStats =
310						batchDurationMs !== undefined && batchCostUsd !== undefined;
311					let logMessage = `Step completed: ${step}${batchLabel2}`;
312					if (hasBatchStats) {
313						const phaseName =
314							step === "executing_error_fix"
315								? "Error fix"
316								: "Suggestion enrich";
317						const label = bi2
318							? `${phaseName} batch ${bi2.batchNumber}/${bi2.totalBatches}`
319							: phaseName;
320						logMessage = `${label} completed: ${Math.round(batchDurationMs / 1000)}s, $${batchCostUsd.toFixed(4)}`;
321					}
322
323					setProgressState((prev) => ({
324						...prev,
325						batchInfo: undefined,
326						currentBatchIssues: undefined,
327						...(hasBatchStats && {
328							lastBatchStats: {
329								durationMs: batchDurationMs!,
330								costUsd: batchCostUsd!,
331								inputTokens: batchInputTokens ?? 0,
332								outputTokens: batchOutputTokens ?? 0,
333							},
334						}),
335						logs: [...prev.logs, { timestamp, message: logMessage }],
336					}));
337					break;
338				}
339
340				case "remediation.completed": {
341					setProgressState((prev) => ({
342						...prev,
343						status: "completed",
344						result: event.data?.result as RemediationResult | undefined,
345						logs: [
346							...prev.logs,
347							{ timestamp, message: "Remediation completed" },
348						],
349					}));
350					// Reset to idle, refresh history, clear selection
351					// Capture completed remediation ID for auto-expand
352					if (activeRemediationIdRef.current) {
353						setAutoExpandRemediationId(activeRemediationIdRef.current);
354					}
355					activeRemediationIdRef.current = null;
356					setActiveRemediationId(null);
357					setSseUrl(null);
358					setPhase("idle");
359					onClearAll();
360					refreshHistory();
361					break;
362				}
363
364				case "remediation.failed":
365					setProgressState((prev) => ({
366						...prev,
367						status: "failed",
368						error: event.data?.error,
369						logs: [
370							...prev.logs,
371							{
372								timestamp,
373								message: `Failed: ${event.data?.error?.message || "Unknown error"}`,
374							},
375						],
376					}));
377					setExecuteError(event.data?.error?.message || "Remediation failed");
378					setSseUrl(null);
379					activeRemediationIdRef.current = null;
380					setActiveRemediationId(null);
381					setPhase("idle");
382					refreshHistory();
383					break;
384			}
385		},
386		[onClearAll, refreshHistory],
387	);
388
389	useSSE({
390		url: sseUrl,
391		onMessage: handleSSEMessage,
392	});
393
394	// Execute remediation
395	const handleExecute = useCallback(async () => {
396		if (!evaluationId || totalSelected === 0) return;
397
398		setIsExecuting(true);
399		setExecuteError(null);
400		setPrompts(null);
401
402		try {
403			const issues = [
404				...errorEntries.map((e) => e.issue),
405				...suggestionEntries.map((e) => e.issue),
406			];
407
408			const response = await api.executeRemediation(
409				evaluationId,
410				issues,
411				targetAgent,
412				selectedProvider,
413			);
414
415			activeRemediationIdRef.current = response.remediationId;
416			setActiveRemediationId(response.remediationId);
417			setSseUrl(response.sseUrl);
418			setProgressState({ status: "queued", logs: [] });
419			setPhase("progress");
420		} catch (err) {
421			const message =
422				err instanceof Error ? err.message : "Failed to start remediation";
423			setExecuteError(message);
424		} finally {
425			setIsExecuting(false);
426		}
427	}, [
428		evaluationId,
429		totalSelected,
430		errorEntries,
431		suggestionEntries,
432		targetAgent,
433		selectedProvider,
434		api,
435	]);
436
437	// Generate prompts (legacy copy-paste mode)
438	const handleGeneratePrompts = useCallback(async () => {
439		if (!evaluationId || totalSelected === 0) return;
440
441		setIsGenerating(true);
442		setGenerateError(null);
443
444		try {
445			const issues = [
446				...errorEntries.map((e) => e.issue),
447				...suggestionEntries.map((e) => e.issue),
448			];
449			const result = await api.generateRemediationPrompts(
450				evaluationId,
451				issues,
452				targetAgent,
453			);
454			setPrompts(result);
455		} catch (err) {
456			setGenerateError(
457				err instanceof Error ? err.message : "Failed to generate prompts",
458			);
459		} finally {
460			setIsGenerating(false);
461		}
462	}, [
463		evaluationId,
464		totalSelected,
465		errorEntries,
466		suggestionEntries,
467		targetAgent,
468		api,
469	]);
470
471	// Delete a remediation from history
472	const handleDeleteRemediation = useCallback(
473		async (remediationId: string) => {
474			await api.deleteRemediation(remediationId);
475			setRemediations((prev) => prev.filter((r) => r.id !== remediationId));
476		},
477		[api],
478	);
479
480	if (!evaluationData) {
481		return (
482			<div className="card text-center py-12">
483				<p className="text-body-muted">
484					No evaluation data. Run an evaluation first to generate remediation
485					prompts.
486				</p>
487			</div>
488		);
489	}
490
491	//  PROGRESS PHASE 
492	if (phase === "progress") {
493		return (
494			<div className="space-y-6">
495				<div className="card">
496					<h2 className="text-heading text-slate-100">Executing Remediation</h2>
497					<p className="text-body-muted mt-1">
498						Running {selectedProvider} agent to fix{" "}
499						{totalSelected > 0 ? totalSelected : "selected"} issue
500						{totalSelected !== 1 ? "s" : ""}...
501					</p>
502				</div>
503				<RemediationProgress
504					currentStep={progressState.currentStep}
505					batchInfo={progressState.batchInfo}
506					logs={progressState.logs}
507					errorCount={progressState.errorCount}
508					suggestionCount={progressState.suggestionCount}
509					totalBatches={progressState.totalBatches}
510					completedBatches={progressState.completedBatches}
511					currentPhase={progressState.currentPhase}
512					runningTotalCostUsd={progressState.runningTotalCostUsd}
513					runningTotalDurationMs={progressState.runningTotalDurationMs}
514					runningTotalInputTokens={progressState.runningTotalInputTokens}
515					runningTotalOutputTokens={progressState.runningTotalOutputTokens}
516					currentBatchIssues={progressState.currentBatchIssues}
517				/>
518			</div>
519		);
520	}
521
522	//  IDLE PHASE (config + history) 
523	return (
524		<div className="space-y-6">
525			<div className="card">
526				<h2 className="text-heading text-slate-100">Remediation</h2>
527				<p className="text-body-muted mt-1">
528					Execute remediation to fix issues automatically, or generate
529					copy-paste prompts
530				</p>
531			</div>
532
533			{/* Past Remediations History */}
534			<RemediationHistory
535				remediations={remediations}
536				onDelete={handleDeleteRemediation}
537				onRefresh={refreshHistory}
538				cloudMode={cloudMode}
539				autoExpandId={autoExpandRemediationId}
540				onAutoExpandHandled={() => setAutoExpandRemediationId(null)}
541				parentScore={evaluationData?.metadata?.contextScore?.score}
542				parentGrade={evaluationData?.metadata?.contextScore?.grade}
543				defaultOpen={selectedIssueKeys.size === 0}
544				hasRepoUrl={
545					!!repositoryUrl &&
546					repositoryUrl !== "unknown" &&
547					(repositoryUrl.startsWith("http") || repositoryUrl.startsWith("git@"))
548				}
549			/>
550
551			{/* Packmind persistent promotion section */}
552			<div className="card border-2 packmind-section">
553				<div className="flex items-start gap-4">
554					<PackmindLogo className="h-8 flex-shrink-0 mt-0.5" />
555					<div className="flex-1 min-w-0">
556						<h3 className="text-subheading text-slate-100 mb-1">
557							What's next with your playbook?
558						</h3>
559						{packmindTotals.standards + packmindTotals.skills > 0 && (
560							<p className="text-base text-slate-300 mb-1">
561								Your remediations have created{" "}
562								<span className="font-bold text-blue-400 bg-blue-500/15 px-1.5 py-0.5 rounded">
563									{formatArtifactCount(
564										packmindTotals.standards,
565										packmindTotals.skills,
566									)}
567								</span>{" "}
568								so far.
569							</p>
570						)}
571						<p className="text-body text-slate-400">
572							Your team's AI agent playbook is taking shape. Packmind helps you
573							centralize coding standards and skills, distribute them across
574							repos, and add governance workflows.
575						</p>
576						<div className="flex gap-3 mt-3">
577							<a
578								href="https://packmind.com"
579								target="_blank"
580								rel="noopener noreferrer"
581								className="btn-secondary"
582							>
583								Explore Packmind
584							</a>
585							<button
586								onClick={() => setShowTourModal(true)}
587								className="btn-secondary"
588							>
589								Get product tour
590							</button>
591						</div>
592					</div>
593				</div>
594				<PackmindProductTourModal
595					isOpen={showTourModal}
596					onClose={() => setShowTourModal(false)}
597				/>
598			</div>
599
600			{/* Empty state when no issues selected */}
601			{totalSelected === 0 ? (
602				<div className="card text-center py-12">
603					<svg
604						className="w-12 h-12 text-slate-600 mx-auto mb-4"
605						fill="none"
606						stroke="currentColor"
607						viewBox="0 0 24 24"
608					>
609						<path
610							strokeLinecap="round"
611							strokeLinejoin="round"
612							strokeWidth={1.5}
613							d="M12 4v16m8-8H4"
614						/>
615					</svg>
616					<p className="text-body-muted">
617						No issues selected. Use the{" "}
618						<span className="inline-flex items-center justify-center w-5 h-5 rounded bg-slate-700 text-slate-300 text-xs align-middle mx-0.5">
619							+
620						</span>{" "}
621						button on issue cards in the Errors and Suggestions tabs.
622					</p>
623				</div>
624			) : (
625				<div className="card space-y-5">
626					{/* Target Agent */}
627					<div>
628						<label className="text-label text-slate-300 block mb-2">
629							Target for markdown file rendering
630						</label>
631						<div className="flex gap-2">
632							{(
633								[
634									["agents-md", "AGENTS.md"],
635									["claude-code", "Claude Code"],
636									["github-copilot", "GitHub Copilot"],
637									["cursor", "Cursor"],
638								] as const
639							).map(([value, label]) => (
640								<button
641									key={value}
642									onClick={() => setTargetAgent(value)}
643									className={`flex items-center gap-1.5 px-3 py-1.5 rounded text-sm transition-colors ${
644										targetAgent === value
645											? "bg-blue-600 text-white"
646											: "bg-slate-700 text-slate-300 hover:bg-slate-600"
647									}`}
648								>
649									{agentLogoMap[value] && (
650										<span className="w-4 h-4 flex items-center">
651											{agentLogoMap[value]}
652										</span>
653									)}
654									{label}
655								</button>
656							))}
657						</div>
658						{AGENT_GENERATED_FILES[targetAgent] && (
659							<p className="text-xs text-slate-500 mt-1">
660								Generates: {AGENT_GENERATED_FILES[targetAgent].join(", ")}
661							</p>
662						)}
663					</div>
664
665					{/* Provider Selector */}
666					<div>
667						<label className="text-label text-slate-300 block mb-2">
668							Pick the AI agent to execute the remediation
669						</label>
670						{providerDetection.status === "detecting" ? (
671							<div className="text-sm text-slate-400">
672								Detecting providers...
673							</div>
674						) : availableProviders.length > 0 ? (
675							<div className="flex flex-wrap gap-2">
676								{availableProviders.map((p) => (
677									<button
678										key={p.name}
679										onClick={() => setSelectedProvider(p.name)}
680										className={`flex items-center gap-1.5 px-3 py-1.5 rounded text-sm transition-colors ${
681											selectedProvider === p.name
682												? "bg-blue-600 text-white"
683												: "bg-slate-700 text-slate-300 hover:bg-slate-600"
684										}`}
685									>
686										{agentLogoMap[p.name] && (
687											<span className="w-4 h-4 flex items-center">
688												{agentLogoMap[p.name]}
689											</span>
690										)}
691										{p.displayName}
692									</button>
693								))}
694							</div>
695						) : (
696							<div className="text-sm text-slate-400">
697								No providers detected. Defaulting to Claude.
698							</div>
699						)}
700					</div>
701
702					{/* Selected Issues */}
703					<div className="space-y-4">
704						{errorEntries.length > 0 && (
705							<IssueSection
706								title="Errors"
707								count={errorEntries.length}
708								entries={errorEntries}
709								onRemove={onRemoveIssue}
710							/>
711						)}
712						{suggestionEntries.length > 0 && (
713							<IssueSection
714								title="Suggestions"
715								count={suggestionEntries.length}
716								entries={suggestionEntries}
717								onRemove={onRemoveIssue}
718							/>
719						)}
720						<div className="flex items-center justify-between">
721							<span className="text-caption">
722								{totalSelected} issue
723								{totalSelected !== 1 ? "s" : ""} selected
724							</span>
725							<button
726								onClick={onClearAll}
727								className="text-sm text-slate-400 hover:text-slate-200 transition-colors"
728							>
729								Clear all
730							</button>
731						</div>
732					</div>
733
734					{/* Action Buttons */}
735					<div className="flex flex-wrap gap-3">
736						<button
737							onClick={() => setShowConfirmModal(true)}
738							disabled={isExecuting || isGenerating}
739							className="btn-primary"
740						>
741							{isExecuting ? (
742								<span className="flex items-center gap-2">
743									<svg
744										className="animate-spin h-4 w-4"
745										fill="none"
746										viewBox="0 0 24 24"
747									>
748										<circle
749											className="opacity-25"
750											cx="12"
751											cy="12"
752											r="10"
753											stroke="currentColor"
754											strokeWidth="4"
755										/>
756										<path
757											className="opacity-75"
758											fill="currentColor"
759											d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
760										/>
761									</svg>
762									Starting...
763								</span>
764							) : (
765								`Execute Remediation (${totalSelected})`
766							)}
767						</button>
768						<button
769							onClick={handleGeneratePrompts}
770							disabled={isExecuting || isGenerating}
771							className="btn-secondary"
772						>
773							{isGenerating ? "Generating..." : "Generate Prompts"}
774						</button>
775					</div>
776
777					{executeError && (
778						<p className="text-sm text-red-400 mt-2">{executeError}</p>
779					)}
780					{generateError && (
781						<p className="text-sm text-red-400 mt-2">{generateError}</p>
782					)}
783				</div>
784			)}
785
786			{/* Confirmation Modal */}
787			<Modal
788				isOpen={showConfirmModal}
789				onClose={() => setShowConfirmModal(false)}
790				title="Confirm Remediation"
791				maxWidth="max-w-md"
792			>
793				<div className="space-y-4">
794					<p className="text-sm text-slate-300">
795						This will run the AI provider to remediate the selected issues.
796						Please confirm the details below.
797					</p>
798					<div className="bg-slate-800/50 rounded-lg border border-slate-700/50 p-3 space-y-2 text-sm">
799						{errorEntries.length > 0 && (
800							<div className="flex justify-between text-slate-300">
801								<span>Errors</span>
802								<span className="font-semibold">{errorEntries.length}</span>
803							</div>
804						)}
805						{suggestionEntries.length > 0 && (
806							<div className="flex justify-between text-slate-300">
807								<span>Suggestions</span>
808								<span className="font-semibold">
809									{suggestionEntries.length}
810								</span>
811							</div>
812						)}
813						<div className="flex justify-between text-slate-300">
814							<span>Target for markdown file rendering</span>
815							<span className="font-semibold">
816								{targetAgent === "agents-md"
817									? "AGENTS.md"
818									: targetAgent === "claude-code"
819										? "Claude Code"
820										: "GitHub Copilot"}
821							</span>
822						</div>
823						<div className="flex justify-between text-slate-300">
824							<span>Provider</span>
825							<span className="font-semibold">{selectedProvider}</span>
826						</div>
827					</div>
828					<div className="flex justify-end gap-3 pt-2">
829						<button
830							onClick={() => setShowConfirmModal(false)}
831							className="btn-secondary"
832						>
833							Cancel
834						</button>
835						<button
836							onClick={() => {
837								setShowConfirmModal(false);
838								handleExecute();
839							}}
840							className="btn-primary"
841						>
842							Confirm
843						</button>
844					</div>
845				</div>
846			</Modal>
847
848			{/* Generated Prompts (legacy mode) */}
849			{prompts && (
850				<div className="space-y-6">
851					{prompts.errorFixPrompt && (
852						<PromptDisplay
853							title={`Fix Errors Prompt (${prompts.errorCount} issues)`}
854							content={prompts.errorFixPrompt}
855						/>
856					)}
857					{prompts.suggestionEnrichPrompt && (
858						<PromptDisplay
859							title={`Enrich Suggestions Prompt (${prompts.suggestionCount} issues)`}
860							content={prompts.suggestionEnrichPrompt}
861						/>
862					)}
863				</div>
864			)}
865		</div>
866	);
867}
868
869function IssueSection({
870	title,
871	count,
872	entries,
873	onRemove,
874}: {
875	title: string;
876	count: number;
877	entries: { key: string; issue: Issue }[];
878	onRemove: (key: string) => void;
879}) {
880	return (
881		<div className="bg-slate-800/50 rounded-lg border border-slate-700/50 p-3">
882			<div className="flex items-center justify-between mb-2">
883				<span className="text-sm font-semibold text-slate-200">
884					{title} ({count})
885				</span>
886			</div>
887			<div className="space-y-1 max-h-48 overflow-y-auto custom-scrollbar">
888				{entries.map(({ key, issue }) => {
889					const numericSeverity = getIssueSeverity(issue);
890					const severityLevel = getSeverityLevel(numericSeverity);
891					const issueTitle =
892						issue.title || issue.problem || issue.description || issue.category;
893
894					return (
895						<div
896							key={key}
897							className="flex items-center gap-2 py-1 px-1 rounded hover:bg-slate-700/30 group"
898						>
899							<div
900								className={`severity-dot severity-dot-${severityLevel} flex-shrink-0`}
901							/>
902							<div className="flex-shrink-0 w-[72px]">
903								{issue.issueType === "error" ? (
904									<span className={getSeverityColor(issue.severity)}>
905										{severityLevel.charAt(0).toUpperCase() +
906											severityLevel.slice(1)}
907									</span>
908								) : (
909									<span className={getImpactBadgeClass(issue.impactLevel)}>
910										{getImpactLabel(issue.impactLevel)}
911									</span>
912								)}
913							</div>
914							<span className="flex-1 min-w-0 text-sm text-slate-300 truncate">
915								{issueTitle}
916							</span>
917							{issue.evaluatorName && (
918								<span className="flex-shrink-0 text-xs text-slate-500 hidden sm:block">
919									{issue.evaluatorName}
920								</span>
921							)}
922							<button
923								onClick={() => onRemove(key)}
924								className="flex-shrink-0 text-slate-500 hover:text-red-400 transition-colors opacity-0 group-hover:opacity-100"
925								title="Remove from remediation"
926							>
927								<svg
928									className="h-4 w-4"
929									fill="none"
930									viewBox="0 0 24 24"
931									stroke="currentColor"
932								>
933									<path
934										strokeLinecap="round"
935										strokeLinejoin="round"
936										strokeWidth={2}
937										d="M6 18L18 6M6 6l12 12"
938									/>
939								</svg>
940							</button>
941						</div>
942					);
943				})}
944			</div>
945		</div>
946	);
947}
948
949function PromptDisplay({ title, content }: { title: string; content: string }) {
950	return (
951		<div className="card">
952			<div className="flex items-center justify-between mb-3">
953				<h3 className="text-subheading text-slate-200">{title}</h3>
954				<CopyButton text={content} variant="text" />
955			</div>
956			<div className="bg-slate-900/50 rounded-lg p-4 border border-slate-700/50 font-mono text-xs text-slate-300 max-h-96 overflow-y-auto custom-scrollbar whitespace-pre-wrap">
957				{content}
958			</div>
959		</div>
960	);
961}
