			{
				id: "suggestions",
				label: "Suggestions",
				icon: (
					<svg
						className="w-4 h-4"
						fill="none"
						stroke="currentColor"
						viewBox="0 0 24 24"
					>
						<path
							strokeLinecap="round"
							strokeLinejoin="round"
							strokeWidth={2}
							d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z"
						/>
					</svg>
				),
				count: filteredSuggestionsCount,
				countClassName: "tab-count-suggestions",
			},
			{
				id: "remediate",
				label: "Remediate",
				icon: (
					<svg
						className="w-4 h-4"
						fill="none"
						stroke="currentColor"
						viewBox="0 0 24 24"
					>
						<path
							strokeLinecap="round"
							strokeLinejoin="round"
							strokeWidth={2}
							d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z"
						/>
					</svg>
				),
				count: selectedIssueKeys.size > 0 ? selectedIssueKeys.size : undefined,
				countClassName: "tab-count-remediate",
			},
		];

		// Only show Debugging and Cost Analysis tabs in non-cloud mode
		if (!cloudMode) {
			baseTabs.push(
				{
					id: "debugging",
					label: "Debugging",
					icon: (
						<svg
							className="w-4 h-4"
							fill="none"
							stroke="currentColor"
							viewBox="0 0 24 24"
						>
							<path
								strokeLinecap="round"
								strokeLinejoin="round"
								strokeWidth={2}
								d="M10 20l4-16m4 4l4 4-4 4M6 16l-4-4 4-4"
							/>
						</svg>
					),
				},
				{
					id: "cost",
					label: "Cost Analysis",
					icon: (
						<svg
							className="w-4 h-4"
							fill="none"
							stroke="currentColor"
							viewBox="0 0 24 24"
						>
							<path
								strokeLinecap="round"
								strokeLinejoin="round"
								strokeWidth={2}
								d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
							/>
						</svg>
					),
				},
			);
		}

		return baseTabs;
	}, [
		filteredErrorsCount,
		filteredSuggestionsCount,
		cloudMode,
		selectedIssueKeys.size,
	]);

	return (
		<div className="min-h-screen bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 text-slate-100 animate-fade-in">
			{/* Header */}
			<AppHeader
				currentPage="home"
				onLogoClick={handleClear}
				historyCount={historyApi.history.length}
			/>

			{/* Selection Summary Bar */}
			<SelectionSummaryBar
				selectedCount={selectedIssueKeys.size}
				onClearSelection={handleClearSelection}
				onReviewSelected={handleReviewSelected}
			/>

			{/* Main Content */}
			<main
				className={`max-w-[1400px] mx-auto px-6 py-6 ${selectedIssueKeys.size > 0 ? "pt-14" : ""}`}
			>
				{/* Content Area */}
				{evaluationData ? (
					<div className="space-y-4">
						{/* Evaluator Failure Panel - Show if some evaluators failed */}
						{evaluationData.metadata.failedEvaluators &&
							evaluationData.metadata.failedEvaluators.length > 0 && (
								<EvaluatorFailurePanel
									failures={evaluationData.metadata.failedEvaluators}
								/>
