										<FilterPanel
											filters={filters}
											onFilterChange={setFilters}
											availableCategories={availableCategories}
											issueCount={filteredIssues.length}
											totalIssues={errorIssues.length}
											filterOptionCounts={filterOptionCounts}
											bookmarkedCount={bookmarkedCount}
