								<div className="lg:col-span-4">
									<div className="flex items-center justify-end mb-3">
										<button
											onClick={() => handleAddAllToRemediation(errorIssues)}
											className="btn-secondary text-sm"
										>
											Add all to remediation ({errorIssues.length})
										</button>
