					<div className="grid grid-cols-1 md:grid-cols-2 gap-3">
						{/* Curated Errors */}
						{curation.errors && (
							<div className="card-hover bg-slate-800/80 border border-slate-700/60 rounded-lg p-4">
								<div className="flex items-center gap-3">
