   256â†’									</div>
   257â†’								</div>
   258â†’								<button
   259â†’									onClick={() => setShowSkillsModal(true)}
   260â†’									className="flex items-center gap-1 text-xs text-indigo-400 hover:text-indigo-300 transition-colors ml-3 flex-shrink-0"
   261â†’								>
   262â†’									<span>Display all</span>
   263â†’									<ExternalLinkIcon />
   264â†’								</button>
   265â†’							</div>
   266â†’						)}
   267â†’
   268â†’						{/* Linked Docs row */}
   269â†’						{linkedDocsCount > 0 && (
   270â†’							<div className="flex items-center justify-between py-2.5 px-3 rounded-lg hover:bg-slate-800/30 transition-colors">
   271â†’								<div className="flex items-center gap-3 flex-1 min-w-0">
