   274â†’										fill="none"
   275â†’										viewBox="0 0 24 24"
   276â†’										stroke="currentColor"
   277â†’									>
   278â†’										<path
   279â†’											strokeLinecap="round"
   280â†’											strokeLinejoin="round"
   281â†’											strokeWidth={2}
   282â†’											d="M13.828 10.172a4 4 0 00-5.656 0l-4 4a4 4 0 105.656 5.656l1.102-1.101m-.758-4.899a4 4 0 005.656 0l4-4a4 4 0 00-5.656-5.656l-1.1 1.1"
   283â†’										/>
   284â†’									</svg>
   285â†’									<span className="text-sm text-slate-300">Linked Docs</span>
   286â†’									<span className="text-xs bg-slate-700/60 text-slate-400 px-1.5 py-0.5 rounded">
