import React, { useState } from "react";
import type { Metadata } from "../types/evaluation";
import { ContextFilesBrowserModal } from "./shared/ContextFilesBrowserModal";
import { LinkedDocsBrowserModal } from "./shared/LinkedDocsBrowserModal";
import { SkillsBrowserModal } from "./shared/SkillsBrowserModal";

interface ContextTabProps {
	metadata: Metadata;
}

const ExternalLinkIcon: React.FC = () => (
	<svg
		className="w-3 h-3"
		fill="none"
		stroke="currentColor"
		viewBox="0 0 24 24"
	>
		<path
			strokeLinecap="round"
			strokeLinejoin="round"
			strokeWidth={2}
			d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14"
		/>
	</svg>
);

export const ContextTab: React.FC<ContextTabProps> = ({ metadata }) => {
	const [showSkillsModal, setShowSkillsModal] = useState(false);
	const [showLinkedDocsModal, setShowLinkedDocsModal] = useState(false);
	const [contextFilesModalType, setContextFilesModalType] = useState<
		"agents" | "claude" | "copilot" | "rules" | "claude-code" | null
	>(null);

	// Context files derivation
	const agentsFilePaths = metadata.projectContext?.agentsFilePaths ?? [];
	const contextFilesFromApi = metadata.projectContext?.contextFiles ?? [];

	const contextFiles =
		contextFilesFromApi.length > 0
			? contextFilesFromApi
			: agentsFilePaths.map((path) => {
					const lowerPath = path.toLowerCase();
					let type: "agents" | "claude" | "copilot" = "agents";
					if (lowerPath.includes("claude")) {
						type = "claude";
					} else if (lowerPath.includes("copilot")) {
						type = "copilot";
					}
					return {
						path,
						type,
						content: "",
						summary: "Re-run evaluation to load content and summary.",
					};
				});

	const agentsMdPaths =
		contextFilesFromApi.length > 0
			? contextFilesFromApi
					.filter((f) => f.type === "agents")
					.map((f) => f.path)
			: agentsFilePaths.filter((p) => p.toLowerCase().includes("agents"));
	const claudeMdPaths =
		contextFilesFromApi.length > 0
			? contextFilesFromApi
					.filter((f) => f.type === "claude")
					.map((f) => f.path)
			: agentsFilePaths.filter((p) => p.toLowerCase().includes("claude"));
	const rulesPaths =
		contextFilesFromApi.length > 0
			? contextFilesFromApi.filter((f) => f.type === "rules").map((f) => f.path)
			: agentsFilePaths.filter((p) => p.includes(".claude/rules/"));
	const copilotInstructionsPaths =
		contextFilesFromApi.length > 0
			? contextFilesFromApi
					.filter((f) => f.type === "copilot")
					.map((f) => f.path)
			: agentsFilePaths.filter((p) =>
					p.toLowerCase().includes("copilot-instructions"),
				);

	const agentsMdCount = agentsMdPaths.length;
	const claudeMdCount = claudeMdPaths.length;
	const rulesCount = rulesPaths.length;
	const copilotInstructionsCount = copilotInstructionsPaths.length;
	const claudeCodeCount = claudeMdCount + rulesCount;
	const skills = metadata.projectContext?.skills ?? [];
	const skillsCount = skills.length;
	const linkedDocs = metadata.projectContext?.linkedDocs ?? [];
	const linkedDocsCount = linkedDocs.length;

	// Sorted for display
	const sortedAgentsMdPaths = [...agentsMdPaths].sort((a, b) =>
		a.localeCompare(b),
	);
	const sortedClaudeCodePaths = [...claudeMdPaths, ...rulesPaths].sort((a, b) =>
		a.localeCompare(b),
	);
	const sortedCopilotInstructionsPaths = [...copilotInstructionsPaths].sort(
		(a, b) => a.localeCompare(b),
	);
	const sortedSkills = [...skills].sort((a, b) => a.name.localeCompare(b.name));
	const sortedLinkedDocs = [...linkedDocs].sort((a, b) =>
		a.path.localeCompare(b.path),
	);

	const docFilesCount =
		agentsMdCount + claudeCodeCount + copilotInstructionsCount;
	const extensionsCount = skillsCount + linkedDocsCount;
	const hasDocFiles = docFilesCount > 0;
	const hasExtensions = extensionsCount > 0;

	if (!hasDocFiles && !hasExtensions) {
		return (
			<div className="glass-card p-12 text-center">
				<svg
					className="w-12 h-12 text-slate-600 mx-auto mb-3"
					fill="none"
					stroke="currentColor"
					viewBox="0 0 24 24"
				>
					<path
						strokeLinecap="round"
						strokeLinejoin="round"
						strokeWidth={1.5}
						d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"
					/>
				</svg>
				<p className="text-sm text-slate-400">
					No context files found in this repository.
				</p>
			</div>
		);
	}

	return (
		<div className="space-y-4 animate-fade-in">
			{/* Group 1: Documentation Files */}
			{hasDocFiles && (
				<div className="glass-card p-6">
					<div className="flex items-center gap-2 mb-4">
						<span className="w-6 h-6 bg-slate-700 rounded-lg flex items-center justify-center">
							<svg
								className="w-4 h-4 text-slate-300"
								fill="none"
								stroke="currentColor"
								viewBox="0 0 24 24"
							>
								<path
									strokeLinecap="round"
									strokeLinejoin="round"
									strokeWidth={2}
									d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"
								/>
							</svg>
						</span>
						<h3 className="text-base font-bold text-slate-100">
							Documentation Files
						</h3>
						<span className="text-xs text-slate-500 ml-1">
							{docFilesCount} {docFilesCount === 1 ? "file" : "files"}
						</span>
					</div>

					<div className="space-y-1">
						{/* AGENTS.md row */}
						<ContextRow
							label="AGENTS.md"
							count={agentsMdCount}
							paths={sortedAgentsMdPaths}
							onBrowse={() => setContextFilesModalType("agents")}
						/>

						{/* CLAUDE.md + Rules row */}
						<ContextRow
							label="CLAUDE.md + Rules"
							count={claudeCodeCount}
							paths={sortedClaudeCodePaths}
							onBrowse={() => setContextFilesModalType("claude-code")}
						/>

						{/* Copilot Instructions row */}
						<ContextRow
							label="Copilot Instructions"
							count={copilotInstructionsCount}
							paths={sortedCopilotInstructionsPaths}
							onBrowse={() => setContextFilesModalType("copilot")}
						/>
					</div>
				</div>
			)}

			{/* Group 2: Extensions */}
			{hasExtensions && (
				<div className="glass-card p-6">
					<div className="flex items-center gap-2 mb-4">
						<span className="w-6 h-6 bg-slate-700 rounded-lg flex items-center justify-center">
							<svg
								className="w-4 h-4 text-slate-300"
								fill="none"
								stroke="currentColor"
								viewBox="0 0 24 24"
							>
								<path
									strokeLinecap="round"
									strokeLinejoin="round"
									strokeWidth={2}
									d="M5 3v4M3 5h4M6 17v4m-2-2h4m5-16l2.286 6.857L21 12l-5.714 2.143L13 21l-2.286-6.857L5 12l5.714-2.143L13 3z"
								/>
							</svg>
						</span>
						<h3 className="text-base font-bold text-slate-100">Extensions</h3>
						<span className="text-xs text-slate-500 ml-1">
							{extensionsCount} {extensionsCount === 1 ? "item" : "items"}
						</span>
					</div>

					<div className="space-y-1">
						{/* Skills row */}
						{skillsCount > 0 && (
							<div className="flex items-center justify-between py-2.5 px-3 rounded-lg hover:bg-slate-800/30 transition-colors">
								<div className="flex items-center gap-3 flex-1 min-w-0">
									<svg
										className="h-4 w-4 text-slate-500 flex-shrink-0"
										fill="none"
										viewBox="0 0 24 24"
										stroke="currentColor"
									>
										<path
											strokeLinecap="round"
											strokeLinejoin="round"
											strokeWidth={2}
											d="M5 3v4M3 5h4M6 17v4m-2-2h4m5-16l2.286 6.857L21 12l-5.714 2.143L13 21l-2.286-6.857L5 12l5.714-2.143L13 3z"
										/>
									</svg>
									<span className="text-sm text-slate-300">Skills</span>
									<span className="text-xs bg-slate-700/60 text-slate-400 px-1.5 py-0.5 rounded">
										{skillsCount}
									</span>
									<div className="flex-1 min-w-0 ml-2">
										<div className="flex flex-wrap gap-1.5">
											{sortedSkills.slice(0, 5).map((skill, i) => (
												<span
													key={i}
													className="text-xs text-slate-500 bg-slate-800/50 px-1.5 py-0.5 rounded"
												>
													{skill.name}
												</span>
											))}
											{skillsCount > 5 && (
												<span className="text-xs text-slate-600">
													+{skillsCount - 5} more
												</span>
											)}
										</div>
									</div>
								</div>
								<button
									onClick={() => setShowSkillsModal(true)}
									className="flex items-center gap-1 text-xs text-indigo-400 hover:text-indigo-300 transition-colors ml-3 flex-shrink-0"
								>
									<span>Display all</span>
									<ExternalLinkIcon />
								</button>
							</div>
						)}

						{/* Linked Docs row */}
						{linkedDocsCount > 0 && (
							<div className="flex items-center justify-between py-2.5 px-3 rounded-lg hover:bg-slate-800/30 transition-colors">
								<div className="flex items-center gap-3 flex-1 min-w-0">
									<svg
										className="h-4 w-4 text-slate-500 flex-shrink-0"
										fill="none"
										viewBox="0 0 24 24"
										stroke="currentColor"
									>
										<path
											strokeLinecap="round"
											strokeLinejoin="round"
											strokeWidth={2}
											d="M13.828 10.172a4 4 0 00-5.656 0l-4 4a4 4 0 105.656 5.656l1.102-1.101m-.758-4.899a4 4 0 005.656 0l4-4a4 4 0 00-5.656-5.656l-1.1 1.1"
										/>
									</svg>
									<span className="text-sm text-slate-300">Linked Docs</span>
									<span className="text-xs bg-slate-700/60 text-slate-400 px-1.5 py-0.5 rounded">
										{linkedDocsCount}
									</span>
									<div className="flex-1 min-w-0 ml-2">
										<div className="flex flex-wrap gap-1.5">
											{sortedLinkedDocs.slice(0, 3).map((doc, i) => (
												<span
													key={i}
													className="text-xs text-slate-500 bg-slate-800/50 px-1.5 py-0.5 rounded truncate max-w-48"
													title={doc.path}
												>
													{doc.path}
												</span>
											))}
											{linkedDocsCount > 3 && (
												<span className="text-xs text-slate-600">
													+{linkedDocsCount - 3} more
												</span>
											)}
										</div>
									</div>
								</div>
								<button
									onClick={() => setShowLinkedDocsModal(true)}
									className="flex items-center gap-1 text-xs text-indigo-400 hover:text-indigo-300 transition-colors ml-3 flex-shrink-0"
								>
									<span>Display all</span>
									<ExternalLinkIcon />
								</button>
							</div>
						)}
					</div>
				</div>
			)}

			{/* Modals */}
			<SkillsBrowserModal
				isOpen={showSkillsModal}
				onClose={() => setShowSkillsModal(false)}
				skills={skills}
			/>
			<ContextFilesBrowserModal
				isOpen={contextFilesModalType !== null}
				onClose={() => setContextFilesModalType(null)}
				contextFiles={contextFiles}
				filterType={contextFilesModalType ?? undefined}
			/>
			<LinkedDocsBrowserModal
				isOpen={showLinkedDocsModal}
				onClose={() => setShowLinkedDocsModal(false)}
				linkedDocs={linkedDocs}
			/>
		</div>
	);
};

/** A row for a documentation file type */
const ContextRow: React.FC<{
	label: string;
	count: number;
	paths: string[];
	onBrowse: () => void;
}> = ({ label, count, paths, onBrowse }) => {
	if (count === 0) return null;

	return (
		<div className="flex items-center justify-between py-2.5 px-3 rounded-lg hover:bg-slate-800/30 transition-colors">
			<div className="flex items-center gap-3 flex-1 min-w-0">
				<svg
					className="h-4 w-4 text-slate-500 flex-shrink-0"
					fill="none"
					viewBox="0 0 24 24"
					stroke="currentColor"
				>
					<path
						strokeLinecap="round"
						strokeLinejoin="round"
						strokeWidth={2}
						d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"
					/>
				</svg>
				<span className="text-sm text-slate-300">{label}</span>
				<span className="text-xs bg-slate-700/60 text-slate-400 px-1.5 py-0.5 rounded">
					{count}
				</span>
				<div className="flex-1 min-w-0 ml-2">
					<div className="flex flex-wrap gap-1.5">
						{paths.slice(0, 3).map((path, i) => (
							<span
								key={i}
								className="text-xs text-slate-500 bg-slate-800/50 px-1.5 py-0.5 rounded truncate max-w-48"
								title={path}
							>
								{path}
							</span>
						))}
						{paths.length > 3 && (
							<span className="text-xs text-slate-600">
								+{paths.length - 3} more
							</span>
						)}
					</div>
				</div>
			</div>
			<button
				onClick={onBrowse}
				className="flex items-center gap-1 text-xs text-indigo-400 hover:text-indigo-300 transition-colors ml-3 flex-shrink-0"
			>
				<span>Browse</span>
				<ExternalLinkIcon />
			</button>
		</div>
	);
};

