import React, { useState } from "react";
import type { Metadata } from "../types/evaluation";
import { ContextFilesBrowserModal } from "./shared/ContextFilesBrowserModal";
import { LinkedDocsBrowserModal } from "./shared/LinkedDocsBrowserModal";
import { SkillsBrowserModal } from "./shared/SkillsBrowserModal";

interface ContextTabProps {
	metadata: Metadata;
}

const ExternalLinkIcon: React.FC = () => (
	<svg
		className="w-3 h-3"
		fill="none"
		stroke="currentColor"
		viewBox="0 0 24 24"
	>
		<path
			strokeLinecap="round"
			strokeLinejoin="round"
			strokeWidth={2}
			d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14"
		/>
	</svg>
);

/** Icon: document */
const DocIcon: React.FC<{ className?: string }> = ({
	className = "h-4 w-4",
}) => (
	<svg
		className={className}
		fill="none"
		viewBox="0 0 24 24"
		stroke="currentColor"
	>
		<path
			strokeLinecap="round"
			strokeLinejoin="round"
			strokeWidth={2}
			d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"
		/>
	</svg>
);

/** Icon: sparkles (skills) */
const SparklesIcon: React.FC<{ className?: string }> = ({
	className = "h-4 w-4",
}) => (
	<svg
		className={className}
		fill="none"
		viewBox="0 0 24 24"
		stroke="currentColor"
	>
		<path
			strokeLinecap="round"
			strokeLinejoin="round"
			strokeWidth={2}
			d="M5 3v4M3 5h4M6 17v4m-2-2h4m5-16l2.286 6.857L21 12l-5.714 2.143L13 21l-2.286-6.857L5 12l5.714-2.143L13 3z"
		/>
	</svg>
);

/** Icon: link */
const LinkIcon: React.FC<{ className?: string }> = ({
	className = "h-4 w-4",
}) => (
	<svg
		className={className}
		fill="none"
		viewBox="0 0 24 24"
		stroke="currentColor"
	>
		<path
			strokeLinecap="round"
			strokeLinejoin="round"
			strokeWidth={2}
